}

.keyword, .shortcode, .phone-number {
    color: black;
}


#faq div#right a.question {
    color: black;
    text-decoration: none;
}

#faq p#frequency-name {
    color: black;
}

#faq div#frequency {
    background: white;
    width: 260px;
    overflow: hidden;
    border: 2px black solid;
    border-radius: 10px;
    -moz-border-radius: 10px;
    z-index: 1;
    padding: 40px 10px 10px;
}

#faq div#right a.category {
    font-family: "century gothic";
    font-size: 14pt;
    font-style: italic;
    color: black;
}

.soundController {
    text-align: center;
    border-top: solid 1px transparent;
    border-left: solid 1px transparent;
    border-bottom: solid 1px transparent;
    background: rgba(0, 53, 158, 0.1)!important;
}

.sliderContainer {
    border: 1px solid gainsboro;
    border-radius: 6px 0 0 6px;
    border-right: rgba(0, 53, 95, 0.5)!important;
    background-color: SteelBlue;
}
  
.css-button.green {
    background: black;
    font-family: "century gothic", serif;
}

