    color: black;
}

.cupboard .tab-body ul li, #zIndexTools>#current, #zIndexTools #backward, #zIndexTools #forward {
    background: rgba(255,255,255,0.7)!important;
    text-align: center;
    border-radius: 10px;
}

#zIndexTools>#description {
    color: Gainsboro;
    font-size: 14px;
    font-family: georgia, sans-serif;
    text-shadow: 1px 1px #696969;
    text-align: center;
    margin: 0 5px;
}
