css:
@-moz-document url-prefix("http://www.kreuzenonline.de/_kreuzen/") {
.accordion_open.home label {
    height: 22px;
    line-height: 24px;
    font-size: 18px;
}
.accordion_open label {
    padding: 5px 20px;
    position: relative;
    display: block;
    height: 30px;
    color: #ffffff;
    text-shadow: 1px 1px 1px rgba(255, 255, 255, 0.8);
    line-height: 33px;
    font-size: 19px;
    box-shadow: 0px 0px 0px 1px rgba(155, 155, 155, 0.3), 0px 2px 2px rgba(0, 0, 0, 0.1);
    word-wrap: break-word;
    text-overflow: ellipsis;
    overflow: hidden;
}
aside a,
.citation_start,
.citation_end,
.accordion_dynamic article.mobile p.mobile,
#small_profile a,
#mini_profile a,
#post_legend a,
#comment_legend a,
#post_new_shell .button:hover,
#comment_new_shell .button:hover,
#bug_new_shell .button:hover,
#chat_shell .button:hover,
span#banner,
#button_show_more {
    color: #ff8400;
}
#nav,
nav_top,
li.logo_top,
li.user,
.top-level-menu > li:hover,
#footer_table,
.accordion_dynamic label.login_label:hover,
.accordion_dynamic label.mobile:hover,
.accordion_dynamic label.mobile,
.accordion_dynamic_defi input:checked + label,
.accordion_dynamic_defi input:checked + label:hover,
nav_top .top-level-menu > li.search:hover,
.container1 > div,
.container2 > div,
.container3 > div,
#post_new_shell .button,
#comment_new_shell .button,
#bug_new_shell .button,
#chat_shell .button,
.spinner,
#chat_toggle,
#chat_select,
header defilogo,
#overlay_box label,
.accordion input:checked + label,
.accordion input:checked + label:hover,
.accordion_open label,
#bug_type input[type="radio"]:checked + label {
    background-color: #ff8400;
}
.button:hover {
    background-color: white;
    color: black;
}
textarea,
input[type="text"],
input[type="button"],
input[type="password"],
input[type="submit"] {
    -webkit-appearance: none;
    border-radius: 0;
}
.button {
    background-color: #676767;
    color: white;
    border: 1px solid rgba(0, 0, 0, 0.75);
    height: 33px;
    cursor: pointer;
    padding: 0px 5px;
    margin: 5px 5px 5px 0px;
}
a.pdf {
    content: "";
    display: block;
    width: 64px;
    height: 64px;
    cursor: pointer;
    background: transparent url("http://i.imgur.com/BCU79Lu.png") no-repeat scroll center center;
}
}
