    width: 0px !important;
    height: 0px !important;
    display: block;
}

/* User Signature */
div[class="post-text-disp"] {
   text-align: center;
}

/* Post */
div[class="post-text-disp"] div {
   text-align: left;
}

/* Remove Signature Elements */
div.post-text-disp span, div.post-text-disp a, div.post-text-disp hr, div.post-text-disp img{
    display: none;
}

/* Hide Signature Text */
div.post-text-disp {
    visibility: hidden;
    line-height: 0;
}

/* Restore Post Text */
div[class="post-text-disp"] div {
    visibility: visible;
    line-height: 1.5;
}

/* Minimal chat, lads! */

/* Message Borders */
.chat-msg-display {
    border-bottom: 0px !important;
    min-height: 0px !important;
}

/* User Avatar */
.chat-msg-image {
    width: 0px !important;
    height: 0px !important;
    margin: 0px !important;
}

/* Message Time */
.chat-msg-time {
    display: inline;
    margin-right: -60px;
}

/* Chat Message */
.chat-message-text {
    display: inline-block;
    top: 6px;
}

/* Image & Emoji Alignment Fix */
.emoji-img-style, .image-embed-style, .e1a-heart, span.chat-mention-name
{
    vertical-align: bottom;
    max-height: 18px !important;
    margin: 0px !important;
}

.image-embed-style
{
    max-height: 100px !important;
}

.e1a-heart
{
    height: 20px !important;
