css:
@-moz-document url-prefix("https://www.xetown.com/") {
body,
input,
textarea,
select,
button,
table {
	font-family: sans-serif;
	letter-spacing: -0.1em
}



div.xe_updated_ver {
	display: none
}


.container.fixed_header {
	padding-top: 80px;
	padding-bottom: 40px
}
.body > .side .banner {
	margin-top: 0;
	width: 198px;
}

.body .side .banner > div {margin-bottom:0 !important}
.body .side .banner div.xetownWidgetStyle {margin-bottom:10px !important}

.header_wrap {
	background-color: transparent
}
.fixed_header .header_wrap.shrink {
	background-color: rgba(0, 0, 0, 0.7)
}
.visual.sub .sub_title h1 {
	font-size: 15px;
	letter-spacing: -0.1em;
	color: #666;
	line-height: 1
}
.gnb > ul > li > a {
	color: rgba(255, 255, 255, 0.6);
	font-weight: bold;
	font-size: 13px
}
.fixed_header .header_wrap.shrink .gnb > ul > li > a {
	padding: 10px;
	line-height: inherit
}
.gnb > ul > li > a img {
	border: none !important;
	width: 6%;
	margin-left: 2px !important;
	border-radius: 50%;
	position: absolute;
}
.header > .side > ul > li > a {
	display: block;
	width: inherit;
	height: inherit;
	margin-left: 12px;
	font-size: 16px;
	line-height: inherit;
	color: #888;
	text-align: center;
}
.gnb > ul > li > a .guide {
	display: none
}
.gnb > ul > li > a {
	padding: 10px;
	height: inherit
}
.header > .side {
	margin: 10px 0
}
.header > h1 {
	padding: 0;
	line-height: 1
}
.header > h1 img {
	display: none
}
.header > h1 a:before {
	content: "XETOWN";
	color: #eee;
	font-size: 17px;
	line-height: 40px;
}
.gnb > ul > li > a:hover .guide,
.gnb > ul > li > a:active .guide,
.gnb > ul > li > a:focus .guide,
.gnb > ul > li.on > a .guide,
.gnb > ul > li > a:hover,
.gnb > ul > li > a:active,
.gnb > ul > li > a:focus,
.gnb > ul > li.on > a {
	background-color: rgba(255, 255, 255, 0.2);
	color: #fff
}
.gnb > ul .depth2 > li:focus,
.gnb > ul .depth2 > li:active,
.gnb > ul .depth2 > li:hover {
	background-color: #02b875
}
.fixed_header .header_wrap.shrink .header > .side.member,
.fixed_header .header_wrap.shrink .header > .side {
	margin: 0
}
.gnb > ul .depth2 {
	background-color: transparent;
	padding: 10px 0 0;
	box-shadow : none

}
.gnb > ul .depth2 > li {
	background: rgba(0,0,0,0.8)
}
.gnb > ul .depth2 > li > a {
	color: #ccc
}
.gnb > ul .depth2:after {
	display: none
}
div.xetownWidgetStyle .widgetTitle {
	background: transparent;
	border-bottom: none
}
div.xetownWidgetStyle .widgetTitle h2 {
	color: #fff
}
.xetownContainer ul.contentNormal li:nth-child(6),
.xetownContainer ul.contentNormal li:nth-child(7) {
	display: none
}
.xe-widget-wrapper {
	height: auto !important
}
.visual.sub .sub_title h1:after {
	display: none
}
.visual.sub .sub_title,
.body {
	width: 1140px !important
}
.body .sub_desc {
	display: none;
	box-shadow: none;
	margin-top: 0;
}
.body .content {
	margin: 0;
	width: 932px !important
}
.visual.sub {
	background: transparent;
	padding: 0
}
.visual.sub .sub_title h1 {
	display: none
}
.container {
	background: rgb(15, 114, 168);
	background: -moz-linear-gradient(left, rgba(15, 114, 168, 1) 0%, rgba(30, 136, 229, 1) 50%, rgba(62, 136, 232, 1) 100%);
	background: -webkit-linear-gradient(left, rgba(15, 114, 168, 1) 0%, rgba(30, 136, 229, 1) 50%, rgba(62, 136, 232, 1) 100%);
	background: linear-gradient(to right, rgba(15, 114, 168, 1) 0%, rgba(30, 136, 229, 1) 50%, rgba(62, 136, 232, 1) 100%);
}
footer.footer {
	display: none
}
.shrink .header {
	padding-top: 0
}
header.header {
	padding-top: 20px
}
.bannerContent {
	display: none
}
.header > .side > ul > li .login_after img {
	width: 20px;
	height: 20px
}
.header > .side > ul > li .login_after {
	line-height: inherit;
	width: 20px;
	height: 20px;
	margin-top: 0
}
.header > .side > .member_level {
	display: none
}
.header > .side.member {
	margin: 10px 0
}
.fixed_header .header_wrap.shrink .header > .side.member,
.fixed_header .header_wrap.shrink .header > .side {
	margin: 10px 0
}
div.xetownWidgetStyle .widgetTitle.blue a.widgetMoreLink {
	display: none
}
.bd {
	margin: 0
}
.body .lnb > ul > li.on {
	background: rgba(255, 255, 255, 0.4)
}
.bd_tb_lst th {
	border: none;
	box-shadow: none;
	background: #f1f1f1
}
.xetownContainer ul.contentNormal li a {
	color: rgba(255, 255, 255, 0.7)
}
.body.sub .lnb {
	background: rgba(255, 255, 255, 0.15);
	width:198px;
	margin-bottom:7px
}

.bd_tb_lst .notice td, .bd_zine li.notice, .bd_tb_lst .select, .bd_tb_lst tbody tr:hover {
    background: rgba(167, 204, 196, 0.3);
}

body #content .xe-widget-wrapper  { 
	width:304px !important;
	margin:0 10px 10px 0 !important
}

body #content .xe-widget-wrapper:nth-of-type(3n)  { 
	margin:0 0 10px !important
}

div.xetownWidgetStyle {
	border-color: #e5e5e5;
	background-color: rgba(255, 255, 255, 0.15);
	border: none;
	margin-top: 0
}


.xetownContainer ul.contentNormal li a .title { letter-spacing: -0.05em }

.xetownContainer a:hover .title, .xetownContainer a:active .title, .xetownContainer a:focus .title {
	text-decoration:none;
	color:#fff;
	font-weight:bold
	
}

.header > .side > ul > li .login_after ~ .ly_login .ic_edge {
	right:3px
}

.header > .side > ul .ly.ly_login.member {
margin-top : 0	
}

.content .att {background:#fff}
}
