css:
@namespace url(http://www.w3.org/1999/xhtml);
/*通用修改*/
@-moz-document domain("saraba1st.com")
{
  /*style*/
  body, input, button, select, textarea{
    font: 40px/1.5 Tahoma,'Microsoft Yahei','Simsun';
  }
  #toptb{
    height:0;
    overflow:hidden;
  }
  #nc-float{
    width: 80%;
    left: 20%;
  }
  #nc-float .nc-f-head,
  #nc-float .nc-f-head i.nc_iconfont{
    font-size: 40px;
    line-height: 56px;
  }
  img{
    max-width: 100%;
  }
  .mn{
    overflow: visible;
  }
  .xst,
  .xs3{
    font-size: 40px !important;
    font-weight: bold;
  }
  .px, .pt{
    line-height: 42px;
    height: auto;
  }
  .pr, .pc{
    transform: scale(2.2);
    width: 20px;
    margin: 0 10px;
  }
  .m_c .o{
    height: 52px;
  }
  .tl .bm_c tr{
    padding: 20px 0;
  }
  .tl td em,
  .tl td em, .tl td em a{
    color: #F26C4F;
    font-size: 28px;
  }
  #hd .wp, #wp{
    min-width: auto;
    width: 97%;
    padding: 0;
  }
  .hdc{
    min-height: auto;
  }
  #um p{
    text-align: left;
  }
  #um p > .vwmy,
  #um p > span,
  #um p > a{
    margin-right: 20px;
  }
  .vwmy{
    background: 0;
  }
  #um{
    padding: 0;
  }
  .fl_g{
    display: block;
    width: 100%;
  }
  .fl_g dl{
    padding: 0;
    margin: 0;
    display: flex;
  }
  .fl_g dd,.fl_g dt{
    display: inline-block;
  }
  .fl_g dt{
    flex-grow: 1;
  }
  .fl_row td{
    border: 0;
  }
  .flg .fl_row td{
    display: inline-block;
  }
  .fl_tb td{
    padding: 5px 0;
    flex-grow: 1;
  }
  .bm_h,.tl .by{
    line-height: 36px;
  }
  .ttp a, .ttp strong{
    font-size: 36px;
    line-height: 42px;
    height: 48px;
    border-radius: 4px;
    border: 0;
    box-shadow: 2px 2px 5px #333;
  }
  .tl .th th,.tl th, .tl td,.bm_h{
    min-height: 52px;
    line-height: 52px;
  }
  #nv{
    background: none;
    border: 0;
    height: auto;
  }
  #nv ul{
    align-items: right;
  }
  #nv li{
    font-size: 42px;
    line-height: 42px;
    border-radius: 4px;
    height: 46px;
    background: #D1D9C1;
    box-shadow: 2px 2px 5px #333;
    padding: 6px 8px;
    margin: 5px !important;
  }
  #nv li a{
    padding: 0 6px;
    height: auto !important;
  }
  #qmenu{
    background: #D1D9C1;
    box-shadow: 2px 2px 5px #333;
    border-radius: 4px;
    font-size: 48px;
    padding: 9px 25px;
    margin: 5px;
    width: auto;
    height: 46px;
    line-height: 42px;
  }
  #qmenu_menu{
    padding: 5px;
    border: 0;
    background: #D1D9C1;
    box-shadow: 2px 2px 5px #333;
    border-radius: 4px;
    width: 230px;
    top: 175px !important;
  }
  #qmenu_menu ul.nav a{
    margin: 0;
    padding: 50px 25px 0;
  }
  #scbar{
    border: 0;
    height: 68px;
    line-height: 68px;
  }
  #scbar_form{
    display: flex;
    padding: 10px;
    align-items: right !important;
  }
  #scbar_form > table:first-of-type{
    position: absolute;
    right: 15px;

  }
  .scbar_txt_td,.scbar_type_td{
    background: none;
  }
  .scbar_type_td{
    position: relative;
    z-index:1;
  }
  #scbar_txt{
    width: 100%;
    height: 66px;
    border: 0;
    border-radius: 3px 0 0 3px;
    padding: 0 10px;
    margin-right: -1px;
    box-shadow: inset 2px 2px 5px #333;
  }
  #scbar_type{
    width: 125px;
    height: 66px;
    line-height: 62px;
    background: #D1D9C1;
    border-radius: 0 3px 3px 0;
  }
  #scbar_type:after{
    content: " ▼";
    font-size: 24px;
    vertical-align: top;
  }
  .scbar_btn_td{
    background: none;
  }
  #scbar_btn{
    background: #25b;
    height: 66px;
    line-height: 44px;
    width: 78px;
    border-radius: 3px;
    margin: 0 5px;
    padding: 0 0 2px;
  }
  #scbar_btn:before{
    content: "▶";
    font-size: 52px;
    text-shadow: 1px 1px 3px #333;
  }
  .mtn{
    margin-top: 5px !important;
  }
  .pn{
    height: 50px;
    background: #15b;
    color: #eee !important;
    font-weight: bold;
    border: 0;
    box-shadow: 1px 1px 3px #333;
    border-radius: 3px;
  }
  .pnpost .pn{
    font-size: 50px;
    height: 88px;
    padding: 0 15px;
    transition: all .06s ease;
  }
  .pnpost .pn:active{
    background-color: #EEE;
    color: #111 !important;
    animation: click .2s linear;
  }
  @keyframes click
  {
    0%  {transform:translateY(0)}
    20% {transform:translateY(-12px)}
    40% {transform:translateY(12px)}
    60% {transform:translateY(-5px)}
    80% {transform:translateY(5px)}
    100% {transform:translateY(0)}
  }
  a[href="forum.php?mod=misc&action=nav"] img,
  #newspecial img,
  #newspecialtmp img,
  #post_reply img,
  #post_replytmp img{
    display: none;
  }
  a[href="forum.php?mod=misc&action=nav"],
  #newspecial,
  #newspecialtmp,
  #post_reply,
  #post_replytmp{
    background:#15b;
    margin:0 8px 0 0 !important;
    padding: 5px 12px;
    box-shadow:#333 1px 1px 3px;
    border-radius:3px;
    text-align:center;
    float: right !important;
  }
  a[href="forum.php?mod=misc&action=nav"]:after,
  #newspecial:after,
  #newspecialtmp:after{
    content:"发帖";
    padding:0;
    line-height:48px;
    color:#eee;
    font-weight: bold;
  }
  #post_reply:after,
  #post_replytmp:after{
    content:"回帖";
    padding:0;
    line-height:48px;
    color:#eee;
    font-weight: bold;
  }
  .tedt .bar,
  .fpd{
    line-height: 54px;
    height: 54px;
  }
  .fpd a,
  .swfupload{
    transform: scale(2.5);
    width: 20px;
    height: 20px;
    margin: 12px 20px;
    padding: 1px;
  }
  span#fd_page_top{
    display: inline;
    height: auto;
  }
  .pg, .pgb{
    float: none;
    line-height: 70px;
    min-height: 70px;
    margin: 5px 0;
  }
  .pgt{
    display: inline;
    height: 62px;
  }
  #visitedforums{
    display: inline;
  }
  .pg a, .pg strong, .pgb a, .pg label{
    float: none;
    height: 58px;
    line-height: 58px;
    padding: 2px 8px !important;
    background-image: none !important;
    border: 0;
    box-shadow: #333 1px 1px 3px;
  }
  .pg a.prev:before{
    content: " ◀";
    font-size: 32px;
  }
  #pt{
    height: 52px;
    line-height: 52px;
    margin: 10px 0;
  }
  #pt .z a, #pt .z em, #pt .z span{
    height: 52px;
  }
  #pt .z em{
    background: none;
    width: auto;
    height: 52px;
    font-size: 0;
    line-height: 52px;
    padding: 0 8px;
  }
  #pt .z em:after{
    content: "▶";
    color: #15b;
    font-size: 36px;
  }
  .nvhm{
    width: auto;
    font-size: 0;
    line-height: 52px;
    background: none;
  }
  .nvhm:after{
    content: "首页";
    font-size: 40px;
  }
  .pg label .px{
    height: 40px;
    width: 70px;
    line-height: 28px;
    text-align: center;
    border: 0;
    border-radius: 3px;
    box-shadow: inset 1px 1px 5px #333;
  }
  .tl .num{
    width: 200px;
    line-height: 52px;
    text-align: right;
  }
  .tl .num a{
    background: #b21;
    border-radius: 20px;
    color: #eee;
    padding: 2px 15px;
    font-weight: bold;
  }
  .tps{
    width: auto;
    font-size: 0;
  }
  img[alt="digest"]{
    width: 52px;
  }
  .pbl p{
    height: auto;
    line-height: 40px;
  }
  .flb{
    height: 38px;
    line-height: 36px;
  }
  .flb em{
    font-size: 28px;
  }
  .flbc{
    background-size: 40px;
    width: 40px;
    height: 40px;
  }
  .flbc:hover{
    background-position: 0 -40px;
  }
  .tb{
    line-height: 46px;
  }
  .tb .o, .tb .o a{
    height: auto;
    line-height: 40px;
    background: #15b;
    border:0;
    box-shadow:#333 1px 1px 3px;
    border-radius:3px;
  }
  .tb .o{
    margin: 2px 10px;
  }
  #fwin_reply{
    width: 90%;
    top: 5% !important;
    height: auto !important;
  }
  .m_c .tedt{
    width: auto;
  }
  .m_c .quote{
    width: auto;
    background-image: none;
    border-radius: 8px 3px 8px 3px;
    box-shadow: 1px 1px 4px #333;
    padding: 10px 20px;
  }
  .m_l, .m_r{
    width: 20px;
  }
  .t_c, .b_c{
    height: 20px;
  }
  #atarget, .unchk, .chked{
    padding-left: 40px;
    line-height: 52px;
    background-size: 32px;
    background-position: 0 12px;
  }
  #atarget.atarget_1, .chked{
    background-size: 32px;
    background-position: 0 -104px;
  }
  .fastlg_l .pn.vm{
    width: auto !important;
  }
  #ls_fastloginfield_ctrl{
    line-height: 40px;
    height: 40px;
    width: auto !important;
    font-size: 32px;
    background: none;
  }
  .login_slct a{
    background: none;
  }
  #ls_fastloginfield_ctrl:after,
  .login_slct a:after{
    content: " ▼";
    font-size: 20px;
  }
  .sltm{
    width: auto !important;
  }
  .nfl .f_c,
  .fwin .rfm,
  .nfl .f_c .rfm{
    width: auto;
  }
  .rfm th{
    width: 200px;
  }
  .rfm th, .rfm td{
    line-height: 42px;
  }
  .alert_right,
  .alert_error,
  .alert_info{
    font-size: 32px;
  }
  .showhide{
    text-indent: 0;
    background: none;
    font-size: 0;
    width: auto;
    height: auto;
    line-height: 46px;
    margin-right: 10px;
    overflow: visible;
  }
  .showhide:before{
    content: "×";
    background: #b21;
    color: #fff;
    font-size: 21px;
    font-weight: bold;
    padding: 0px 7px 3px;
    border-radius: 20px;
    box-shadow: 1px 1px 2px #666;
  }
  .bm_h .o{
    width: auto;
  }
  .bm_h .o img{
    width: 36px;
  }
  .pbnv{
    width: auto;
  }
  #scrolltop{
    left: 85% !important;
    bottom: 180px;
    margin: 0;
    width: 60px;
    opacity: .5;
    background: none;
    border: 0;
  }
  #scrolltop a{
    color: #fff;
    font-size: 48px;
    height: 100px;
    line-height: 100px;
    width: 100px;
    padding: 0;
    background: #789;
    border: 5px solid #def;
    border-radius: 60px;
    margin: 15px 0;
  }
  a.replyfast:before{
    content: "回";
  }
  a.scrolltopa:before{
    content: "顶";
  }
  a.returnlist:before{
    content: "列";
  }
  a.returnboard:before{
    content: "版";
  }
  img[smilieid],
  img[id^=smilie]{
    width: 72px;
    height: auto;
  }
  div#fastpostsml_menu,
  div#postsml_menu{
    left: 0 !important;
    height: auto !important;
  }
  div#fastpostsmiliesdiv_tb.tb.tb_s.cl,
  div#postsmiliesdiv_tb.tb.tb_s.cl{
    width: auto !important;
  }
  .tps a{
    display: none;
  }
  .tps a:last-of-type{
    display: inline;
    color: #fff !important;
    font-size: 32px;
    font-weight: bold;
    background-color: #15b;
    border-radius: 5px 30px 30px 5px;
    padding: 5px 15px 5px 10px;
  }
  /*advanced editing mode*/
  .edt .bbar,
  .edt .bar{
    height: 114px;
    font-size: 38px;
  }
  .edt .bbar em{
    height: 60px;
    width: 100%;
    display: block;
  }
  .edt .b2r a.dp{
    height: 60px;
    width:auto;
    background: #eee;
    text-align: center;
  }
  #post_extra_tb label{
    line-height: 48px;
    height: 48px;
  }
  #post_extra_tb label span{
    line-height: 48px;
    height: 48px;
    background-size: 12px;
    background-position: 10px 22px;
    padding: 0 8px 0 30px;
  }
  .exfm table th, .exfm table td{
  }
  #extra_additional_c label{
    width: auto;
  }
  /*font size*/
  .fszm{
    height: auto !important;
  }
  font[size="1"]{font-size: 30px}
  font[size="2"]{font-size: 32px}
  font[size="3"]{font-size: 36px}
  font[size="4"]{font-size: 40px}
  font[size="5"]{font-size: 44px}
  font[size="6"]{font-size: 50px}
  font[size="7"]{font-size: 60px}
  h1, h2, h3, h4, h5, h6{font-size: 40px}
  .title{font-size: 44px;line-height: 50px;height: 50px}
  /*deleted elements*/
  #ft, #scbar_hot,#fjump_menu,div.xi1,#um .avt,#um .showmenu,#um .pipe, .chart,.bm.lk,.fl_icn,.fl_icn_g,.fl_g dd:nth-child(2),#scbar_btn strong, .scbar_icon_td, #visitedforums_menu,.a_h,#hd h2, .card, .bui, #fastsmiliesdiv, img[alt="heatlevel"], img[alt="agree"], img[alt="attach_img"], #newspecial_menu, .pipe, .ad, #e_svdsecond, #scrolltop a b, .tl .num em{
    display: none !important;
  }
  /*去除顶部快捷入口，像本行一样加入前后标签即可注释/无效化，删除即启用*/
  #mn_Ndec7{display:none}/*页游联运*/
  #mn_F77 {display:none}/*八卦体育*/
  #mn_N9701{display:none}/*手游*/
  *#mn_N1105{display:none}/*首页测试*/
  /*#mn_N300b{display:none}*//*动漫*/
  /*#mn_Nf7c8{display:none}*//*游戏*/
  /*#mn_NF75{display:none}*//*外野*/
  #mn_N5d64{display:none}/*欧美动漫*/
}


/*主页修改*/
@-moz-document regexp("^[^\.]*\.saraba1st\.com/2b/forum\.php(#.*|[?]gid=.*)?")
{
  /*style*/
  .fl_by,
  .fl_by div{
    width: auto;
    padding: 0;
    margin: 0;
    flex-grow: 0 !important;
  }
  div#category_0 tr,
  div#category_1 tr,
  div#category_7 tr{
    display: flex;
    width: 100%;
  }
  /*deleted elements*/
  #online,.xg2,.fl_i,.fl_by div .xi2,.fl_by div a,.fl_tb td p:last-child{
    display: none !important;
  }
}

/*?*/

/*帖子列表修改*/
@-moz-document regexp("^[^\.]*\.saraba1st\.com/2b/forum(-|.*mod=forumdisplay).*")
{
  /*style*/
  .bml{
    border: 0;
    padding: 0 !important;
  }
  .bml .bm_h{
    height: auto;
    padding: 0;
  }
  .tl .bm_c tr{
    display: flex;
    flex-wrap: wrap;
  }
  .tl .bm_c th{
    display: inline-block;
    width: 100% !important;
    border: 0;
    padding: 0;
  }
  .tl .bm_c th:first-of-type{
    padding: 0px;
  }
  .tl .bm_c td{
    display: inline-block;
    border: 0;
    padding: 0 10px;
  }
  td.new.fn + .by{
    flex-grow: 1 !important;
  }
  td.new.fn{
    display: inline-block;
    width: 100% !important;
    border: 0;
    padding: 0;
  }
  .tl .by:nth-of-type(2){
    flex-grow: 1;
  }
  .tl .by:last-of-type{
    width: 340px;
    text-align: right;
  }
  .tl #forumnewshow{
    font-size: 30px;
  }
  .tl #forumnewshow th,
  .tl #forumnewshow td{
    width: 100%;
  }
  .tl #forumnewshow a{
    height: auto;
    line-height: 52px;
  }
  .tl th em,
  .newthread .new em{
    font-size: 0;
  }
  .tl th em a,
  .newthread .new em a{
    font-size: 32px;
    color: #eee !important;
    background: #022C80;
    padding: 5px 10px;
    border-radius: 0 10px 10px 0;
  }
  .cttp{
    padding-right: 60px;
    height: 58px;
  }
  .cttp .unfold, .cttp .fold{
    width: auto;
    height: auto;
  }
  .forumrefresh{
    background-size: 36px;
    padding-right: 36px;
  }
  /*deleted elements*/
  #f_pst, #autopbn, .ttp .num, #separatorline, .bm .pbn, .bm_h .i, .bml .o, .bml .xs2 a:first-child,.tl .th .by,.tl .th .num,.tl .icn{
    display: none !important;
  }
}

/*我的帖子修改*/
@-moz-document regexp("^[^\.]*\.saraba1st\.com/2b/(.*(mod=guide|mod=space)|space-username-|space-uid-|\\?\\d+).*")
{
  /*style*/
  #thread_types{
    display: flex;
  }
  #thread_types li{
    flex-grow: 1;
    padding: 0;
    display: flex;
  }
  #thread_types a{
    flex-grow: 1;
    text-align: center;
  }
  .tl .bm_c tr{
    display: flex;
    flex-wrap: wrap;
  }
  .tl th, .tl td.fn{
    width: 100%;
    padding: 5px 10px;
  }
  .tl .by{
    width: 280px;
    text-align: right;
    padding: 0 20px;
  }
  .tl th, .tl td{
    border-bottom: 0;
  }
  .tl .num{
    flex-grow: 1;
  }
  .bw0_all{
    background: #F2F1EF;
  }
  .bw0_all td{
    width: 100%;
  }
  .pbm{
    padding: 0 0 4px 12px !important;
  }
  .tl .th th{
    font-size: 0;
    display: flex;
  }
  .tl .th th a{
    font-size: 36px !important;
    margin: 0 auto;
  }
  /*deleted elements*/
  .tl > .th td,   .tl .icn,   .tl .bm_c tr td.by:nth-of-type(2),   .tl .bm_c tr td.by:nth-of-type(3),   .bw0_all + tbody,   #filter_special_menu,   .bm.bml.pbn,   img[alt="heatlevel"]{
    display: none !important;
  }
}


/*帖子内容修改*/
@-moz-document regexp("^[^\.]*\.saraba1st\.com/2b/.*(thread-|mod=viewthread).*") 
{
  /*style*/
  .t_f, .t_f td{
    font-size: 42px;
  }
  .pl .quote{
    background-image: none;
    padding: 10px 20px;
    margin: 5px 0;
    border-radius: 8px 3px 8px 3px;
    box-shadow: 1px 1px 4px #333;
  }
  .pl .quote blockquote{
    background-image: none;
    padding: 0;
  }
  .hasfsl{
    margin: 0;
  }
  .xl2 li{
    width: auto;
  }
  .ts{
    font-size: 42px;
  }
  #postlist{
    border:none;
    border-radius:3px;
    background:none;
  }
  #postlist>div{
    margin: 22px 0;
    border-radius:3px;
    box-shadow:#333 2px 2px 5px;
  }
  #postlist>div tr{
    display: flex;
    flex-direction: column;
  }
  #postlist > table:first-of-type{
    border-radius:3px;
    box-shadow:#333 2px 2px 5px;
  }
  .pls{
    background: none;
    height: 60px;
    overflow: visible;
    width: 60%;
  }
  .pls .avatar{
    float: left;
    height: 0px;
  }
  .pls .favatar{
    position: absolute !important;
    top: initial !important;
    height: 60px;
  }
  .plhin tbody tr:first-of-type .plc{
    display: block;
  }
  .pls .avtm{
    border: 0;
    border-radius: 4px;
    box-shadow: 2px 2px 5px #333;
    position: relative;
    top:-82px;
  }
  .favatar .pi{
    padding: 0;
    font-size: 48px;
    min-height: 42px;
    height:auto;
    margin: 15px 0 0 160px;
  }
  .plhin,
  .pi,
  .po,
  .pls,
  #postlist .pbn{
    border: 0;
    opacity: 1 !important;
  }
  .pi{
    min-height: 40px;
    height: auto;
    margin: 0;
    padding: 20px 0 15px 140px;
