css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vector.im") {
  .mx_EventTile .mx_MessageTimestamp {
    visibility : visible !important;
  }
  
  .mx_EventTile {
    margin-top: 2px;
    
  }
  
  .mx_EventTile_avatar {
    margin-right: -50px;
    float: right;
    padding-right: 0px;
  }
  
  .mx_EventTile_readAvatars {
    display: none;
  }
  
  .mx_EventTile .mx_SenderProfile {
    float: left;
    margin-left: -90px;
    padding-right: 10px;
  }
  
  .mx_EventTile .mx_SenderProfile:after {
    content: ":";
  }
  
  .mx_EventTile {
    margin-left: 90px;
  }
  
  .mx_EventTile_selected {
    margin-left: 77px;
  }
  
  .mx_TextualEvent {
    font-size:10px;
  }
}
