css:
@-moz-document url-prefix("http://www.tsdm.net/") {
/*使用本CSS配置请先切换论坛风格至五周年（简洁）使用*/
/*我的邮箱：kssn@nanjo.cc*/

/*改变样式*/

* {font-family: "宋体"!important;border: 0!important;}
html {background: #F0F0F0 url("http://image.gfan.com/static/image/gfan/gfan_bg.png")!important;zoom: 0.9!important;}/*替换背景、字体，全局0.9缩放*/
.wp {box-shadow: 0 0 0px 0px rgba(0, 0, 0, 0)!important;margin-top: 0px!important;}/*顶部距离从LOGO开始*/
#wp,.wp{width: 1280px!important;}/*统一框架宽度1280*0.9*/
#wp,#ct,#wp.wp{background-color: rgba(255,255,255,0)!important;}.bm {background: none!important;}/*去掉框架背景颜色*/
.fl_row td,.bm,.fl{border: 0px !important;}/*去掉框架边框*/
#nv {background-color: #f99 !important;}/*导航栏底色*/
#nv a{font-size:18px!important;}
#nv li {background: rgba(255, 255, 255, 0)!important;/*导航栏去掉按钮本身底色*/}
#ct #wp{background-color: rgba(255, 255, 255, 0) !important;border-radius: 0px!important;box-shadow: 0 0 0px 0px rgba(0, 0, 0, 0)!important;border-color: rgba(255, 255, 255, 0)!important;}/*去掉框架背景颜色和边框*/
.tl table {border:0px!important;}

/*清理辣鸡*/

.tedt .area textarea{background:none!important;}/*去掉发/回帖框里的黑白熊*/
#tsdmadv_img,#ft,#tsdmWbFrame ,#previewmc，.a_c {display: none !important;}/*清理广告*/
#toptb,/*隐藏顶部横条*/
#Kahrpba,/*隐藏首页banner*/
#pt .y,/*隐藏路径右边的捐助帖子链结*/
.bm_h,/*隐藏版块标题*/
#category_1,/*隐藏整个天使后花园所有版块*/
#category_3,/*隐藏整个天使综合区所有版块*/
#mn_Na01b,#mn_N3ed7,#mn_N1c1e,#mn_Nf5f5,#mn_N0251,#mn_N5bdd,#mn_N4a63,/*隐藏导航栏没有必要的选项*/
#ts_sidebar_base,/*隐藏右边浮动的微博*/
.wp h2/*logo*/
{display: none !important;}
}

@-moz-document url-prefix("http://www.tsdm.net/forum.php?mod=forumdisplay&fid=") {
#forum_rules_247,/*隐藏音乐区顶部索引*/
#subforum_247,/*隐藏音乐区子版块*/
#pgt,.fl,.pbn,/*隐藏音乐区版主列表和顶部页码*/
#stickthread_660815,#stickthread_628244,#stickthread_608037,#stickthread_521206,#stickthread_252788,#stickthread_2155,#stickthread_104006,#stickthread_712824,#stickthread_689532,#stickthread_674191,#stickthread_651700,#stickthread_649676,#stickthread_647741,#stickthread_591722,#stickthread_509457,#stickthread_467724,#stickthread_297648,#stickthread_59423,/*隐藏音乐区置顶帖*/
.bm_c .cl ul/*隐藏所有版块顶部的公告和版规*/
{display: none !important;}
}

@-moz-document url-prefix("http://www.tsdm.net/forum.php?mod=viewthread&tid=") {
/*隐藏帖子内的个人信息*/
#postlist div{color:#666!important;}
.tsdm_statbar,.tsdm_replybar,/*UID,注册时间等信息*/
.qdsmile,/*签到信息*/
.md_ctrl,/*徽章信息*/
div.tsdm_norm_title,/*隐藏狗牌*/
.pls p,/*签到*/
.post_nickname
{display: none !important;}
.t_f {background: rgba(255, 255, 255, 0)!important;}/*去掉回帖文字背景*/
table {background-image:none!important;}/*去掉回帖图片背景*/
td {background-image:none!important;}/*去掉回帖头像背景*/
.pls .avatar img{height: 150px!important;width: 150px!important;border-radius:50%!important; overflow:hidden!important;padding:0 0 0 0!important;}/*优化头像样式*/
.pls {background:  Transparent!important;}/*优化左侧背景*/
}
