css:
@-moz-document domain(whatculture.com) {

.background-page {
    color: #c5c5c5;
    background-color: #111;
}
.form__legend,
.h1,
.h2,
.h3,
.info-box__heading,
h1,
h2,
h3 {
    color: #ddd;
    margin-top: 0;
    font-family: 'Roboto Condensed', sans-serif;
}
.app-side {
    position: relative;
    width: 100%;
    max-width: 100%;
    padding-top: .9rem;
    background-color: #060606;
}
.side-box {
    display: block;
    overflow: hidden;
    background-color: #111;
    font-weight: 700;
}
.post-tile__title {
    font-size: 1.3rem;
    line-height: 1.4562rem;
    margin-top: 0;
    margin-bottom: 0;
    font-family: 'Roboto Condensed', sans-serif;
    font-weight: 700;
    color: #bbb;
    text-transform: none;
    -webkit-transition: color .2s ease-in-out;
    transition: color .2s ease-in-out;
}
.post-tile__title {
    font-size: 1.3rem;
    line-height: 1.4562rem;
    margin-top: 0;
    margin-bottom: 0;
    font-family: 'Roboto Condensed', sans-serif;
    font-weight: 700;
    color: #bbb;
    text-transform: none;
    -webkit-transition: color .2s ease-in-out;
    transition: color .2s ease-in-out;
}
.app-heading--line-through .app-heading__inner {
    position: relative;
    display: block;
    float: left;
    margin-left: .6rem;
    padding-right: .6rem;
    background-color: #111;
    z-index: 2;
}
.post-tile--focal .post-tile__title {
    font-size: 1.4rem;
    line-height: 1.2;
    margin-bottom: .45rem;
    color: #c5c5c5;
}
.post-tile {
    min-width: 100%;
    margin-bottom: .9rem;
    color: #c5c5c5;
    -webkit-transition: border-color .2s ease-in-out;
    transition: border-color .2s ease-in-out;
}
.app-header {
    position: relative;
    z-index: 9999;
    background-color: #111;
    color: #c5c5c5;
}
.app-nav,
.app-nav__interactive {
    height: 3rem;
    background-color: #111;
    position: relative;
}
.app-nav__interactive {
    display: block;
    padding: .675rem .7rem;
    font-family: 'Roboto Condensed', sans-serif;
    font-weight: 700;
    box-shadow: -1px 0 #E8E8E8, 1px 0 #E8E8E8;
    -webkit-transition-property: border-color, color;
    transition-property: border-color, color;
    -webkit-transition-duration: .2s;
    transition-duration: .2s;
    -webkit-transition-timing-function: ease-in-out;
    transition-timing-function: ease-in-out;
    color: #c5c5c5;
    text-transform: uppercase;
}
body {
    color: #c5c5c5;
    background-color: #111;
    font-family: Roboto, sans-serif, arial;
    font-weight: 400;
    font-size: 1em;
    line-height: 1.618rem;
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
}
.app-mega-nav__item:nth-of-type(even) {
    background-color: #111;
}
.app-mega-nav__item:nth-of-type(odd) {
    background-color: #111;
}
.app-mega-nav__post {
    position: relative;
    width: 100%;
    color: #c5c5c5;
    -webkit-transition: color .2s ease-in-out;
    transition: color .2s ease-in-out;
}
.app-search__button,
.app-search__search {
    position: absolute;
    height: 100%;
    background-color: #2f313e;
}
input,
textarea,
keygen,
select,
button {
    text-rendering: auto;
    color: #c5c5c5;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: start;
    margin: 0em 0em 0em 0em;
    font: 13.3333px Arial;
}
.app-search__button {
    display: block;
    float: left;
    min-width: 3rem;
    padding-right: .9rem;
    padding-left: .9rem;
    text-align: center;
    color: #ddd;
}
.app-nav__side::before {
    width: 16px;
    left: -9916px;
    background-repeat: repeat-y;
}
.app-mega-nav__item--post::after {
    width: 16px;
    left: -9916px;
    background-repeat: repeat-y;
}
.author-section {
    display: block;
    padding: .9rem;
    background-color: #111;
    border: 1px solid #E8E8E8;
}
body {
    background: #111;
    color: #c5c5c5;
    direction: ltr;
    line-height: 1.34;
    margin: 0;
    padding: 0;
    unicode-bidi: embed;
}
a {
    color: hsl(219, 69%, 53%);
    cursor: pointer;
    text-decoration: none;
}

.app-heading--line-through::after {
    width: 100%;
    margin-left: .6rem;
    height: 2px;
    z-index: 1;
    background-color: #636363;
}

.trending-box__slug {
    display: block;
    padding: .9rem .9rem .9rem 2.7rem;
    color: #bbb;
    -webkit-transition: background-color .2s ease-in-out;
    transition: background-color .2s ease-in-out;
}

.trending-box__slug:focus, .trending-box__slug:hover {
    background-color: #333;
}

.post-aside__title {
    margin-top: 0;
    font-family: 'Roboto Condensed',sans-serif;
    font-size: 1.4rem;
    clear: right;
    margin-bottom: .45rem;
    font-weight: 700;
    color: #a2a2a2;
    -webkit-transition: color .2s ease-in-out;
    transition: color .2s ease-in-out;
}

@media (min-width: 50em){

.app-side__section {
    border-top: 1px solid rgba(0,0,0,.12);
    border-bottom: 1px solid rgba(0,0,0,.12);
    background-color: #060606;
}
    
.post-aside__title {
    margin-top: 0;
    font-family: 'Roboto Condensed',sans-serif;
    font-size: 16px;
    clear: right;
    margin-bottom: .45rem;
    font-weight: 700;
    color: #a2a2a2;
    -webkit-transition: color .2s ease-in-out;
    transition: color .2s ease-in-out;
}

}

.app-page__module.post__section {
    background-color: #666;
}

.bq, .generic blockquote {
    margin-right: 0;
    margin-left: 0;
    margin-bottom: .9rem;
    padding: .9rem;
    border: 1px solid rgba(0,0,0,.12);
    border-left-width: 3px;
    border-radius: 2px;
    background-color: rgb(55, 55, 55);
    color: #aaa;
}

.post-tile, .post-tile__inner {
    color: #ddd;
}

}
