css:
@-moz-document url-prefix("http://www1.flightrising.com/forums/") {
table#postlist div.poststatus.poststatus-sticky {
    background-image: url(http://i.imgur.com/5YssAqL.png);
    _background-size: 50%;
    background-repeat: no-repeat;
    background-position: 50%;
}

table#postlist div.poststatus.poststatus-locked.poststatus-sticky {
    background-image: url(http://i.imgur.com/8Er1LVA.png);
    _background-image: url(http://i.imgur.com/XMPa0FX.png);
}

table#postlist div.poststatus.poststatus-locked {
    background-image: url(http://i.imgur.com/tSgCcy8.png);
    _background-image: url(http://i.imgur.com/7EYmWXJ.png);
    background-repeat: no-repeat;
    background-position: 50%;
}

table#postlist td.activity div.activity-admin-post {
    position: relative;
    top: 9px;
	left: -15px;
    width: 10px;
}

img[class="forum-first-admin-post"]
{
    _height: 50%;
    content: url(http://i.imgur.com/93Fb0FL.png);
    _left: -130px;
    _position: relative;
    _top: 17px;
}

span[style="left:472px"]{
    display: none;
}

img[class="forum-column-divider-center"]{
    display:none;
}

span[style="left:408px"]{
        left: 435px !important;
}

table#postlist td.activity div.activity-replies {
    left: 32px !important;
}

img[src="/static/layout/fr_post_next_white.png"]{
        width: 40px;
}

.post-footer.post-footer-edited div.edittime {
    font-size: 9px;
}
}
