css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("the-lounge.org") {

#wrap-bg {
   width: 90% !important;
   background-color: #ffffff !important ;
   }

#wrap {
   padding-top: 5px !important;
   background-color: #EFEFEF !important ;
   }   

.forabg { background-repeat: repeat !important; }
.forumbg { background-repeat: repeat !important; }
.bg1   { background-repeat: repeat !important; }
.bg2   { background-repeat: repeat !important; }
.bg3   { background-repeat: repeat !important; }

html,body  {
   background-color: #FFFFFF !important;
   background-image: url(http://s10.directupload.net/images/081216/g7uex2nm.gif) !important;
   background-repeat: repeat !important;
   background-attachment: scroll !important;
   }

li.row:hover {
   background-repeat: repeat-y !important;
   }

ul.linklist li {
   font-weight: bold !important;
   }

#page-header {
   padding: 0 10px !important;
   padding-top: 0  !important;
   }

#flash {
   width: auto !important;
   height: 95px !important;
   padding-bottom: 10px !important;
   background-color: #FFFFFF !important;
   background-image: url(http://s10.directupload.net/images/081216/5kr3kmkd.png) !important;
   background-repeat: no-repeat !important;
   background-attachment: scroll !important;
   }

#flash p, #flash h1 ,#flash img  {
   display: none !important;
   }

#page-body iframe {
   display: none !important;
   }

.spoiler-content {
   color: #000000 !important;
   }
.spoiler-content div {
   background-color: #ABABAB  !important;
   border-color: #000000  !important;
   border-width: 2px  !important;
   border-style: dashed  !important;
   }

}
