/* HTML elements with Roboto Fonts */
html, body, input, select, textarea, button, td, a, p {
  font-family: Roboto,arial,sans-serif !important;
}
div.logo-subtext, .h, .g, .std, .c h2, h1, #cdr_min, #cdr_max, .cpbb, .kpbb, .kprb, .kpgb, .kpgrb, .ksb {
  font-family: Roboto,arial,sans-serif !important;
}

/* customer classes or ids */

/* Link color */
.a, cite, cite a:link, cite a:visited, .cite, .cite:link, #_bGc>i, .bc a:link {
    color: #27981C;
    font-family: Roboto,arial,sans-serif !important;
}
a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
    color: #3655e9;
}
.mblink:visited, a:visited {
    color: #e93655;
}

/* Search Box on top */
.gsfi, .gsfs {
	font: 17px Roboto,arial,sans-serif !important;
    font-weight: 300 !important;
}
.gsfs b {
	font-weight: 500 !important;
}

/* Search Results */
#res h3, #res div.s {
	font-family: Roboto,sans-serif !important;
}
#res div.s, #res cite._Rm {
    font-size: 15px;
}

/* Search Results - Additional information box on the right */
.kp-blk {
    font-family: Roboto,sans-serif !important;
    -webkit-box-shadow: 0px 0px 10px 3px rgba(0,0,0,0.2);
    box-shadow: 0px 0px 10px 3px rgba(0,0,0,0.2);
}
.kp-blk .kno-ecr-pt, .kp-blk ._gdf {
    font-family: Roboto,sans-serif !important;
}

/* Searching for specific Address, suggestion box on top gives Arial fonts */
.vk_ans, .vk_h, .vk_sh, .vk_txt, ._yz, ._PGm {
    font-family: inherit;
}
