css:
@-moz-document domain("pandora.com") {
.contentContainer {
    margin-left: auto;
    margin-right: auto;
    width: 665px;
    background: rgba(228, 213, 197, 0.53);
    color: #ff4900;
    -webkit-border-radius: 20px;
    -webkit-border-bottom-right-radius: 6px;
    -webkit-border-bottom-left-radius: 6px;
    -moz-border-radius: 20px;
    -moz-border-radius-bottomright: 6px;
    -moz-border-radius-bottomleft: 6px;
    border-radius: 20px 20px 6px 6px;
}



#trackInfoButtons {
    display: none;
    overflow: hidden;
    zoom: 1;
    background-color: rgba(232, 231, 241, 0);
    text-align: right;
    border-bottom: 1px solid rgba(255, 255, 255, 0);
}


#trackInfo {
    background-color: rgba(3, 115, 228, 0);
    overflow: hidden;
    zoom: 1;
    padding: 10px 9px 10px 9px;
    border-bottom: 1px solid rgba(0, 120, 230, 0);
    border-left: 1px solid rgba(255, 51, 51, 0);
    min-height: 51px;
}

.detail a {
    font-size: 12px;
    font-weight: normal;
    color: #000;
    text-decoration: none;
}

.detail {
    color: #000000;
    border-left: none;
}


.detail .heading {
    padding-bottom: 3px;
    font-weight: bold;
    font-size: 14px;
    color: #ff3d00;
}

body #footer {
    font-family: "Avenir Next","Helvetica Neue",Helvetica,Arial,"Lucida Grande","sans-serif";
    padding: 40px 0;
    text-align: center;
    background-color: rgba(255, 174, 0, 0);
    width: 100%;
    top: 200px;
    bottom: 0;
    height: 150px;
    margin: 200px 0 0;
}



#trackInfo .info, #trackInfo .artistMessageInfo {
    color: rgb(255, 96, 0);
    padding-top: 3px;
    text-shadow: 0 1px 0 #ff9300;
    font-size: 13px;
    text-align: right;
    width: 100%;
    margin: auto;
}



#trackInfo .info a {
    color: #ff3100;
    text-decoration: none;
    margin-right: 6px;
}



body, #mainContainer {
    background-image: url(http://static4.gamespot.com/uploads/original/536/5360430/2656299-screen10-png.png);
    background-position: center 90px;
    background-repeat: no-repeat;
    background-color: rgba(0, 0, 0, 0);
}

#playerBar .searchBox {
    padding: 0 0 0 26px;
    width: 208px;
    height: 29px;
    background: url(http://novask.in/4637510419611648.png)no-repeat;
    background-size: 29px 29px;
    color: #0669ff;
    background-color: #312f2f;
    border-radius: 3px;
    margin-left: 7px;
}



.navFooter,
.legal
{
display:none !important;
}

#splash {
background: #091028 url(http://i.imgur.com/GitQJ.jpg) center top no-repeat;
}

#mainContainer {
background-image: url(http://oi65.tinypic.com/sq07ww.jpg)!important;
}

#playerBar {
background-image: url(http://i.imgur.com/Rvas1.png)!important;
}
#playerBar .progress .progressBar {
    width: 345px;
    height: 4px;
    overflow: hidden;
    background-color: #e28b0f;
    text-align: left;
    vertical-align: middle;
    border-radius: 2px;
}



::selection {
background: #3e3e3e;
color: #3e3e3e;
text-shadow: none;
}

#brandingBar {
background: url(http://i.imgur.com/vW7XT.jpg) repeat-x scroll !important;
}

.contentnav {
height: 28px;
background-color: #373737;
}

.contentContainer .top {
background-color: #399bc8;
border-top: 1px solid #399bc8;
}

#brandingBar {
color: #A8C4E2;
}



#skinTab {
    display: none;
    background-color: rgba(255, 255, 255, 0);
    border-radius: 0 0 5px 5px;
    -moz-border-radius: 0 0 5px 5px;
    -webkit-border-radius: 0 0 5px 5px;
    width: 90px;
    height: 21px;
    color: rgba(255, 255, 255, 0);
    text-align: center;
    padding-top: 4px;
    margin-top: 5px;
    margin-right: 7px;
    text-shadow: none;
    opacity: .85;
    float: right;
}


#skinTab:hover {
    color: rgba(214, 222, 234, 0);
    cursor: default;
}



#brandingBar {
    color: rgb(255, 120, 0);
}



.stationChangeSelector {
    display: inline-block;
    width: 190px;
    color: #ff6000;
    cursor: pointer;
    background: transparent;
    position: relative;
    z-index: 5100;
}



#playerBar .rightcolumn .info a {
    color: rgb(255, 143, 0);
    font-size: 12px;
    text-decoration: none;
}



#playerBar .rightcolumn {
    color: #00daff;
    font-size: 12px;
    white-space: nowrap;
    vertical-align: top;
    position: static;
    padding: 3px 3px 0 0;
}




.stationListItem .stationName {
    font-family: arial;
    font-weight: normal;
    font-size: 12px;
    color: #4100ff;
    text-shadow: 0 1px 0 #0066ff;
    padding: 0;
    margin: -5px 0 -5px 0;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
}

#brandingBar {
    color: #ff6000;
}





.stationListItem .option {
    color: #e600ff;
    float: left;
    font-size: 10px;
    text-align: center;
    height: 15px;
    padding-top: 2px;
}



#playerBar .searchInput {
    position: relative;
    top: 5px;
    line-height: 1;
    width: 190px;
    border: 0;
    padding: 0 0 0 4px;
    margin: 0;
    font-size: 13px;
    color: #ff7800;
    background: transparent;
}
}
