css:
@-moz-document url-prefix("https://scratch.mit.edu") {
body
{
text-shadow:none;
background-color:whitesmoke;
}
#topnav ul.account-nav>li:last-child {
border-right: 1px solid #ff8c00;
}
#navigation, .dropdown-menu, #topnav, .innerwrap,.dropdown,#topnav .innerwrap
{
background-color:DarkOrange ;
border-bottom: 1px solid #101010;

}
#topnav .innerwrap
{border-bottom: 1px solid #101010;

box-shadow:5px 1px black;}

.box .box-header
{
background-color:DarkOrange;
border-top: 1px solid #101010;
box-shadow:-5px -1px black;
}
 p {
color: black;
}
.box-head
{ box-shadow:-5px -1px black;}
#topnav ul.account-nav .sign-in .popover , #topnav .innerwrap, #topnav ul.site-nav li, #topnav ul.account-nav>li, #topnav ul.site-nav li.last,.box .box-head
{
background-color:DarkOrange;
    border-color: DarkOrange;
    border-left: 1px solid DarkOrange;
   
}

.showcase .info 
{
text-shadow:none;
}
.text-block
{
color:black;
}
a
{
color: #3e58f5;
}
.button, button
{
background-image: -webkit-linear-gradient(top,#13bbd4,#03424e);
}

h1, h2, h3, h4, h5,#navigation .link>a,a:active, a:link, a:visited,#topnav ul.site-nav li a,input, textarea, select
{
color: #1c3056;
}

.tooltip span.hovertext
{
background-color:orange;
color:#000;
}
.header-text .profile-details {
    color: #fff;
}
#topnav ul.account-nav.logged-in>li {
    border-left: 1px solid #ff8c00;
}
#topnav ul.account-nav .logged-in-user .dropdown-menu, #topnav li.logout.divider input, #topnav ul.account-nav ul.user-nav li.logout.divider, .dropdown span.dropdown-toggle span.caret
{
background-color:DarkOrange;
}
#topnav ul.account-nav ul.user-nav li.logout.divider ,#topnav ul.account-nav>li:last-child{
    border-top: 1px solid orange;
}
.blockpost div.box
{
     background-color: white;
    border-top: 1px solid white;
}





.intro h1
{
text-shadow: 0.5px 0.5px black, 1px 1px black;
}

.button.white
{
color:white;
}
.showcase .actions {
    background-image: -webkit-linear-gradient(top,#13bbd4,#03424e);
    display: block;
    padding: 2px 12px 2px 22px;
    list-style-type: none;
    margin: 0;
    clear: both;
    border-top: 1px solid #ccc;
}
.small>a
{
color:white;
}
.button.grey
{
background-image: -webkit-linear-gradient(top,#ffd49f,#ff8c00);
color:black;
}
.box-head .wip
{
background-image: -webkit-linear-gradient(top,#13bbd4,#03424e);
    color:white;
}
.tooltip.grey .hovertext {
    background-color: orange;
}
/*Yeni*/

#topnav .container li:hover {
    background-color:#FF7900 !important;
}
#topnav ul.account-nav .logged-in-user.dropdown.open { background-color:#FF7900 !important; }
#topnav ul.account-nav>li.sign-in.open { background-color: #FF7900 !important; }

#topnav ul.account-nav>li>span,#topnav ul.account-nav ul.user-nav li a,#topnav li.logout.divider input {
    color: #1c3056;
}

/* ScrollBar */

.scrollbar .thumb
{
		-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	background-color: #F5F5F5;
}



.scrollbar .thumb .end, .scrollbar .thumb 
{
   
	background-color: DarkOrange;	
	background-image: -webkit-linear-gradient(45deg,
	                                          rgba(255, 255, 255, .8) 25%,
											  transparent 25%,
											  transparent 50%,
											  rgba(255, 255, 255, .8) 50%,
											  rgba(255, 255, 255, .8) 75%,
											  transparent 75%,
											  transparent)
}
body::-webkit-scrollbar-track
{
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	background-color: #F5F5F5;
}

body::-webkit-scrollbar
{
	width: 10px;
	background-color: #F5F5F5;
}

body::-webkit-scrollbar-thumb
{
	background-color: DarkOrange;
	border: 2px solid #555555;
}

}
