@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pikabu.ru") {
 
  /*==============================Body==============================*/
  body {
    background-color: #e9ffe9 !important;
  }
  a {
    color: #006400;}
  a:hover {
    color: #FF4500;
  }
  
  /*==============================Hidden AD Blocks==============================*/
  div.header-cont > a,
  div.login_f.menu-block-branding > a,
  body > a[target="_blank"],
  div[data-story-id="_"],
  div#yandex_ad_R-56459-35,
  table.footer_links2.footer-table,
  span#right_menu > table,
  div#yandex_ad,
  div.story[style="margin-top: 10px;"],
  div.story[style="margin-top: 10px"],
  .footer_links2,
  div.stories_prev, div.story__preview, span.story__source-domain,
  section.ad-slider /*NEW*/ {
    display: none !important;
  }
  div.story[data-story-id="4589325"],
  div.story[data-story-id="4591607"],
  div.story[data-story-id="4593917"],
  div.story[data-story-id="4593928"],
