  div.story[data-story-id="4639270"],
  div.story[data-story-id="4639696"],
  div.story[data-story-id="4642196"],
  div.story[data-story-id="4642283"],
  div.story[data-story-id="4643643"],
  div.story[data-story-id="4663498"],
  div.story[data-story-id="4663891"],
  div.story[data-story-id="4667772"],
  div.story[data-story-id="4668717"],
  div.story[data-story-id="4668803"],
  div.story[data-story-id="4678513"],
  div.story[data-story-id="4680601"],
  div.story[data-story-id="4683476"],
  div.story[data-story-id="4684249"],
  div.story[data-story-id="4685861"],
  div.story[data-story-id="4694696"],
  div.story[data-story-id="4695718"],
  div.story[data-story-id="4700343"],
  div.story[data-story-id="4704300"],
  div.story[data-story-id="4721443"],
  div.story[data-story-id="4742444"],
  div.story[data-story-id="4748142"],
