  div.story[data-story-id="4815725"],
  div.story[data-story-id="4817390"],
  div.story[data-story-id="4838254"],
  div.story[data-story-id="4846910"],
  div.story[data-story-id="4867673"],
  div.story[data-story-id="4869962"],
  div.story[data-story-id="4870464"],
  div.story[data-story-id="4871547"],
  div.story[data-story-id="4875420"],
  div.story[data-story-id="4876313"],
  div.story[data-story-id="4877643"],
  div.story[data-story-id="4877582"],
  div.story[data-story-id="4878762"], /*NEW!*/  
  div.story[data-story-id="4880338"],
  div.story[data-story-id="4880483"],
  div.story[data-story-id="4892710"],
  div.story[data-story-id="4893127"],  
  div.story[data-story-id="4894635"],
  div.story[data-story-id="4902089"], /*NEW!*/
  div.story[data-story-id="4904983"], /*NEW!*/  
  div.story[data-story-id="4906335"], /*NEW!*/  
  div.story[data-story-id="4912391"] /*NEW!*/ {
    display: none !important;
  }
  
  /*==============================Header==============================*/
  div.header-cont {
    background: #e9ffe9;
  }
  table#header_t {
    background: inherit;
  }
  table#header_t {
    height: 80px;
  }
  div[style="width: 100%; background: url('http://cs.pikabu.ru/images/header_bg13.gif') #f0f0f0 repeat-x bottom;"] {
    background: #e9ffe9 !important;
  }
  td.header-logo {
    width: 15%;
  }
  td.header-nav-bottom {
    width: 400;
  }
  table#header_t .menu {
    word-wrap: break-word;
  }
  div#wrap {
    margin-top: 15px;
  }
  
  /*==============================Menu==============================*/
  td.header-nav-bottom > ul.menu > li.menu-item-default,
  li.noactive {
    width: 110px !important;
  }
  table#header_t .menu li a {
    width: 105px;
  }
  table#header_t .menu .active a,
  table#header_t .menu .active a:hover {
    color: #FFF;
    background: #c63;
    background-color: #c63;
    border-radius: 2px;
    font-weight: 700;
    border: 0;
    width: 105px;
  }
  table#header_t .active,
  table#header_t .active a,
  table#header_t .menu .active2 a,
  table#header_t .menu .active2 a:hover {
    text-shadow: unset;
  }
  
  table#header_t .menu li a {
    background: #61c15b;
    padding: 1px;
    margin: 2px;
    border-radius: 2px;
    color: #FFF;
    font-weight: 700;
  }
  table#header_t .menu li a:hover {
    background-color: #093;
  }
  table#header_t .menu .active2 a {
    color: #FFF;
    background: #c63;
    background-color: #c63;
    border-radius: 2px;
    font-weight: 700;
    border: 0;
    width: 105px;
    padding: 1px;
    height: 20px;
  }
  table#header_t .menu .active2 a:hover {
    color: #FFF;
    background: #c30;
    background-color: #c30;
    border-radius: 2px;
    font-weight: 700;
    border: 0;
    width: 105px;
    padding: 1px;
    height: 20px;
  }
  table#header_t ul li {
    margin-left: 2px;
  }
  
  /*==============================Search==============================*/
  input[type="text"]:active, input[type="password"]:active, textarea:active, input[type="password"]:focus, input[type="text"]:focus, textarea:focus {
    color: green;
  }
  input[type="text"], input[type="password"], textarea {
    color: #000;
  }
  .story-search {
    background-color: #9c9;
    border-radius: 2px;
  }
  .story-search div.search-text-field > input,
  .story-search div.search-text-field,
  .story-search div.search-text-field.ss-selected > input,
  .story-search div.search-text-field.ss-selected,
  .tags-bar > div.tb-tag > input,
  .tags-bar > div.tb-tag,
  .tags-bar > div.tb-tag.tb-selected,
  .tags-bar > div.tb-tag.tb-selected > input {
    box-shadow: 0 0 0 0 #fff;
    color: green !important;
    border-radius: 3px;
    border: 0 solid #fff;
  }
  .b-button_type_green:hover {
    background-color: #093;
  }
  .story-search table.search-tags,
  .story-search div.story-adv-search {
    color: #000;
  }
  .tags-bar > a.tb-add-btn,
  .tags-bar > a.tb-more-btn,
  .story-search div.story-adv-search > a,
  .story-search .story-search-controls > a.story-search-reset,
  .search-result-msg a {
    color: green;
  }
  .tags-bar > a.tb-add-btn:hover,
  .tags-bar > a.tb-more-btn:hover,
  .story-search div.story-adv-search > a:hover,
  .story-search .story-search-controls > a.story-search-reset:hover,
  .search-result-msg a:hover {
    color: #FF4500;
  }
  .story-search .story-search-controls > a.story-search-reset:hover {
    text-decoration: none;
  }
  
  /*==============================Basic links==============================*/
  div.menu-cont div.header-nav-top > ul > li > a {
    color: #006400;
    text-decoration: none;
  }
  div.menu-cont div.header-nav-top > ul > li > a:hover {
    color: #c60;
    text-decoration: none;
  }
  ul.header_links > li > a {
    color: #006400 !important;
    text-decoration: none;
  }
  ul.header_links > li > a:hover {
    color: #c60 !important;
    text-decoration: none;
  }
  
  /*==============================Add post==============================*/
  .addlink_button {
    background: #79c36c !important;
  }
  .addlink_button:hover {
    background: #67bb58 !important;
  }
  
  /*==============================Sign in==============================*/
  .b-tabs_type_sign .b-tabs__list {
    background: #9c9;
    border-left: 1px solid #9c9;
    border-right: 1px solid #9c9;
    border-top: 1px solid #9c9;
  }
  .b-tabs_type_sign .b-tabs__list li {
    color: #006400;
  }
  .b-tabs_type_sign .b-tabs__list li.b-tabs_li_active_yes {
    color: #FF4500;
  }
  .b-tabs_type_sign .b-tabs__container {
    border-left: 1px solid #9c9;
    border-right: 1px solid #9c9;
    border-bottom: 1px solid #9c9;
  }
  .b-input:focus {
    border: 1px solid green;
    box-shadow: 0 0 0 #fff;
  }
  .b-sign__in .b-button_type_default {
    background: #61c15b;
    border-color: #61c15b;
    color: #FFF;
  }
  .b-button_type_default {
    text-shadow: 0 0 0 #fff !important;
  }
  .b-button_type_default:hover {
    background-color: #093;
  }
  .b-sign__oauth-label {
    color: green;
    margin-bottom: 10px;
    text-align: center;
  }
  .b-sign__oauth-providers {
    text-align: center;
  }
  .b-sign__oauth-hr {
    border-bottom: 2px solid #9c9;
    margin-top: 10px;
  }
  .b-sign {
    color: green;
  }
  .b-sign__validate_state_error {
    color: #FF4500 !important;
  }
  .b-button_type_create {
    background: #61c15b;
    border-color: #61c15b;
    color: #FFF;
    text-shadow: 0 0 0 #fff !important;
  }
  .b-button_type_create:hover {
    background-color: #093;
  }
  
  /*==============================Best comment==============================*/
  div.info.info_c.menu-comm-head {
    color: #e9ffe9;
  }
  div.menu-block-branding td.menu-block-title,
  div.menu-block-branding td.menu-best-comm {
    font-weight: 700;
    background: inherit;
    border: 1px solid #90EE90;
  }
  td.menu-block-title {
    border-bottom: 0 !important;
  }
  td.menu-best-comm {
    border-top: 0 !important;
  }
  .rating_bl {
    color: #006400;
    font-weight: 700;
  }
  span.menu-block-title-right > a {
    color: #FFF;
    text-decoration: none;
    text-transform: lowercase;
    background: #61c15b;
    border-radius: 2px;
    font-weight: 400 !important;
    padding: 2px;
  }
  span.menu-block-title-right > a:hover {
    color: #FFF;
    text-decoration: none;
    text-transform: lowercase;
    background: green;
    border-radius: 2px;
    font-weight: 400 !important;
    padding: 2px;
  }
  td.menu-best-comm > div.menu-story-link {
    margin-bottom: 5px;
  }
  td.menu-best-comm > div.menu-story-link > a {
    color: #ff7200;
    text-decoration: none;
  }
  td.menu-best-comm > div.menu-story-link > a:hover {
    color: #FF4500;
    text-decoration: none;
  }
  td.menu-best-comm > div.menu-comm-head > h6 {
    color: #006400;
    border: 1px solid #006400;
    border-radius: 2px;
    padding: 2px;
  }
  .info_c a {
    color: #006400;
  }
  .detailDate,
  .detailDate:hover {
    color: #006400 !important;
    font-weight: 700;
  }
  
  /*==============================How to use==============================*/
  div.menu-block, table.menu-block {
    border: 1px solid #90EE90;
  }
  td.menu-block-title {
    background: inherit;
  }
  td.menu-block-small-txt {
    padding-bottom: 0;
  }
  
  /*==============================Active communities==============================*/
  div.login_f.menu-top-tags {
    border: 1px solid #90EE90;
  }
  .b-community-line__name {
    color: #006400;
  }
  .b-community-line:hover .b-community-line__name {
    color:#FF4500;
    text-decoration: none;
  }
  .b-community-top-caption i {
    color: green;
  }
  
  /*==============================Popular tags==============================*/
  span.tag.no_ch {
    background: #61c15b;
    border-color: #61c15b;
    color: #FFF !important;
    text-shadow: 0 0 0 #FFF;
    padding: 2px 50px !important;
  }
  span.tag.no_ch:hover {
    background: green;
    border-color: green;
    color: #dedede !important;
    text-shadow: 0 0 0 #FFF;
    padding: 2px 50px !important;
  }
  .tag {
    transition: .2s ease-in;
  }
  .tag_count {
    color: green;
    margin-left: 10;
    border: 1px solid green;
    border-radius: 5px;
    padding: 3px;
  }
  .tag_all a {
    color: #006400;
    text-decoration: none;
  }
  .tag_all a:hover {
    color: #FF4500;
  }

  /*==============================Feed Sort==============================*/
  .b-feed-panel {
    color: #000;
    background-color: #9c9;
    border-radius: 2px;
  }
  .b-feed-panel a {
    color: #006400;
  }
  .b-feed-panel a:hover {
    color: #c60;
  }
  #blk-upcoming {
    border-bottom: 1px solid green;
  }
  #blk-upcoming .a-selected {
    padding: 7px 35px 0;
    background-color: #90EE90;
    border-radius: 2px;
    border-bottom: 3px solid green;
  }
  #blk-upcoming span {
    margin: 0 0 0 0;
  }
  #blk-upcoming > span {
    padding: 3px 35px 0;
  }
  #blk-upcoming a,
  #blk-upcoming .a-selected a {
    color: #006400;
  }
  .b-communities .community-search {
    background-color: #9c9;
    border-radius: 2px;
  }
  div.community-filter > span {
    color: #000 !important;
  }
  div.community-filter > a {
    color: #006400 !important;
  }
  div.community-filter > a:hover {
    color: #FF4500 !important;
  }
  
  /*==============================Pop ups==============================*/
  div.popup-box.ppb-round {
    background-color: #ffdab1 !important;
  }
  .b-feed-panel-box > section {
    border-bottom: 0;
    color: #FF4500;
  }
  div.ppb-arrow  {
    visibility: hidden !important;
  }
  .uical > .uical-head {
    background-color: #ffdab1 !important;
  }
  .uical > .uical-head > .uical-top-btns > a.uical-selected,
  .uical > .uical-head > .uical-top-btns > a {
    color: #FF4500;
    background: #ffdab1 !important;
  }
  .uical > .uical-head > .uical-top-btns > a.uical-selected:hover,
  .uical > .uical-head > .uical-top-btns > a:hover {
    color: #f96;
    background: #ffdab1 !important;
  }
  .uical > .uical-head > div.uical-date > input {
    border: 1px solid #FF4500;
  }
  .uical > .uical-head > div.uical-date > input:focus {
    box-shadow: 0 0 0 0 #fff;
    border: 1px solid #FF4500;
  }
  .uical > .uical-head > span.uical-separator {
    color: #FF4500;
  }
  .uical > .uical-head > canvas[data-border="y"] {
    display: none;
  }
  
  /*==============================Communities==============================*/
  .b-community-short > div a[data-role="link"] img,
  .b-community-line img,
  .b-community-header[data-has-bg="1"] div.community-info .community-bg img {
    border-radius: 50%;
    border: 1px solid #90EE90;
  }
  .b-community-info .community-info > a[data-role="avatar"] img {
    border-radius: 50%;
    border: 1px solid grey;
  }
  .b-community-header div.community-info > div h1,
  .b-community-info .community-info > a h2{
    color: #ff7200;
  }
  .b-community-header {
    padding-bottom: 20px;
    border: 1px solid darkgrey;
    border-radius: 3px;
    background: #dedede;
  }
  div.community-header-bg {
    border-bottom: 1px solid darkgrey;
  }
  .b-community-statuses a {
    color: #006400;
    text-decoration: none;
  }
  .b-community-info .community-info > a h2:hover,
  .b-community-statuses a:hover{
    color: #FF4500;
    text-decoration: none;
  }
  .b-community-header div.community-info > div > span,
  .b-community-info .community-info > span {
    color: #FFF;
    border: 1px solid #999;
    border-radius: 2px;
    padding: 3px;
    background: #b2b2b2;
    text-shadow: 1px 2px 2px #000;
  }
  .b-community-header div.community-info > div > span b {
    color: #FFF;
  }
  .b-community-line img {
    width: 35px;
    height: 35px;
  }
  .b-community-line:hover .b-community-line__name {
    color: green;
  }
  a.community-tag {
    color: #FFF !important;
    background: #61c15b !important;
    border: 1px solid #61c15b !important;
  }
  .b-communities a.community-tag:hover {
    color: #dedede !important;
    background: green !important;
    border: 1px solid green !important;
    transition: .2s ease-in;
  }
  .b-community-short > div a[data-role="link"] h2 {
    color: #006400;
  }
  .b-community-short > div a[data-role="link"] h2:hover {
    color: green;
  }
  .b-community-short > div > .community-buttons a[data-role="subs+"],
  .b-community-header div.community-info > div > .community-buttons a[data-role="subs+"],
  .b-community-info > .community-buttons a[data-role="subs+"]{
    color: green;
    border: 1px solid #90EE90;
    background: #FFF;
  }
  .b-community-short > div > .community-buttons a[data-role="subs+"]:hover,
  .b-community-header div.community-info > div > .community-buttons a[data-role="subs+"]:hover,
  .b-community-info > .community-buttons a[data-role="subs+"]:hover {
    color: #006400;
    border: 1px solid green;
    background: #90EE90;
  }
  .b-community-short > div > .community-buttons a[data-role="ignore+"],
  .b-community-short > div > .community-buttons a[data-role="ignore-"],
  .b-community-header div.community-info > div > .community-buttons a[data-role="ignore+"],
  .b-community-header div.community-info > div > .community-buttons a[data-role="ignore-"],
  .b-community-info > .community-buttons a[data-role="ignore+"],
  .b-community-info > .community-buttons a[data-role="ignore-"] {
    color: #c66;
  }
  .b-community-short > div > .community-buttons a[data-role="ignore+"]:hover,
  .b-community-short > div > .community-buttons a[data-role="ignore-"]:hover,
  .b-community-header div.community-info > div > .community-buttons a[data-role="ignore+"]:hover,
  .b-community-header div.community-info > div > .community-buttons a[data-role="ignore-"]:hover,
  .b-community-info > .community-buttons a[data-role="ignore+"]:hover,
  .b-community-info > .community-buttons a[data-role="ignore-"]:hover,
  .b-community-info .community-rules section,
  .b-community-info .community-owner section {
    color: #FF4500;
  }
  .b-community-info .community-rules {
    background: #e9ffe9;
  }
  .b-community-info .community-owner a.community-user {
    color: green;
  }
  .b-community-info .community-owner span {
    color: brown;
    margin-left: 20px;
  }
  
  /*==============================List of tags==============================*/
  .tag {
    background-color: #61c15b;
    color: #FFF !important;
    text-shadow: 0 0 0 #FFF;
    border-width: 1px;
    border-style: solid;
    border-color: #61c15b;
  }
  .tag:hover {
    background: green !important;
    color: #dedede !important;
    text-shadow: 0 0 0 #FFF;
    border-width: 1px;
    border-style: solid;
    border-color: green;
  }
  .b-tag-search {
    background-color: #9c9;
    border-radius: 2px;
  }
  .b-tag-search div.ts-text.ts-selected {
	  border: 0 solid #fff;
  }
  .b-tag-search div.ts-text.ts-selected > input,
  .b-tag-search div.ts-text,
  .b-tag-search div.ts-text > input {
    box-shadow: 0 0 0 0 #fff;
    color: green !important;
    border-radius: 3px;
  }
  div.b-tag-search-result > div a {
    background: #61c15b;
    border-color: #61c15b;
    color: #FFF !important;
  }
  div.b-tag-search-result > div a:hover {
    background: green;
    border-color: green;
    color: #dedede !important;
  }
  
  /*==============================Content==============================*/
  blockquote {
    color: green;
    background: #dedede !important;
    border: 1px solid green !important;
  }
  .b-story-blocks__wrapper,
  .b-story__content_type_text,
  .b-story__content_type_media {
    background: #e9ffe9;
    border: 1px solid #90EE90;
    border-radius: 2px;
  }
  .story__title-link {
    color: #ff7200;
  }
  .story__header-title > a:hover {
    color: #FF4500;
  }
  .story__title-link_visited_yes {
    color: #ffb18a;
  }
  .story__header-additional {
    color: #006400;
  }
  .story__header-additional a,
  .b-story__content a {
    color: green;
    text-decoration: none;
  }
  .story__header-additional a:hover,
  .b-story__content a:hover {
    color: #c60;
    text-decoration: none;
  }
  .story__date {
    color: #006400;
    font-weight: 700;
  }
  .b-story__show-all {
    background: #90EE90;
    border-radius: 2px;
  }
  .b-story__show-all:hover {
    background: #6c6;
    border-radius: 2px;
  }
  .b-story-blocks_with-border .b-story-blocks__wrapper::before,
  .story__scroll-wrapper_enabled::before {
    background: none !important;
  }
  .story__rating-count,
  .story__authors {
    color: #006400;
  }
  .story__tag {
    background: #61c15b;
    border-color: #61c15b;
    color: #FFF !important;
  }
  .story__tag:hover {
