css:
@-moz-document url-prefix("http://www.behindthename.com/"), url-prefix("http://surnames.behindthename.com/") {
/* === GLOBAL STUFF === */

/* margin background */
body {
    background: #111;
    color: #ddd;
}
/* top menu bar */
div.menubar-wrapper {
    background: linear-gradient(to bottom, #5a5655, #333);
}
div.menubar a {
    color: #eeeeea !important;
}
/* main body */
div.body {
    background: #222;
    border: 1px solid black;
    box-shadow: none;
}
/* all links not otherwise specified */
a {
    color: #a98 !important;
}
/* language usage links */
a.usg {
 	color: #888 !important;
}
/* text edit fields */
input {
    background-color: #666;
}
/* misc non-link texts */
span.info {
    color: #999;
}
/* small pic on the bottom right */
div.sheknows img {
    -webkit-filter: invert(100%) sepia(100%);
    filter: invert(100%) sepia(100%);
    border: none !important;
}





/* === GENDER STUFF === */

/* text */
span.masc {
    color: #79c;
}
span.fem {
    color: #c78;
}
/* the symbols on the front page */
div.genderlinks img[alt="Masculine"] {
    -webkit-filter: grayscale(80%) brightness(250%);
    filter: grayscale(80%) brightness(250%);
}
div.genderlinks img[alt="Feminine"] {
    -webkit-filter: grayscale(80%) brightness(400%);
    filter: grayscale(80%) brightness(400%);
}







}

@-moz-document url("http://www.behindthename.com/"), url("http://surnames.behindthename.com/") {
/* === FRONT PAGE === */

/* the large logo */
div#logo img {
    -webkit-filter: invert(100%) sepia(100%) saturate(150%);
    filter: invert(100%) sepia(100%) saturate(150%);
}
/* social media buttons */
div.social_home img {
    -webkit-filter: grayscale(100%) contrast(140%) brightness(70%);
	filter: grayscale(100%) contrast(140%) brightness(70%);
}
/* link buttons */
a:link.quick, a:visited.quick {
    background-color: #444 !important;
    border: 2px solid #1b1b1b !important;
    color: #ccc !important;
}
}

@-moz-document url("http://www.behindthename.com/names"), url("http://surnames.behindthename.com/names"), url-prefix("http://www.behindthename.com/names/"), url-prefix("http://surnames.behindthename.com/names/") {
/* === NAME LIST === */

/* subheaders */
span.namesub {
    color: #955;
}
}

@-moz-document url-prefix("http://www.behindthename.com/names/list"), url-prefix("http://surnames.behindthename.com/names/list") {
/* === NAME LIST: Search box === */

table.emtable {
    background: #222;
    border: none;
}

table.emtable td {
    background: #555;
    border: none;
}
}

@-moz-document url-prefix("http://www.behindthename.com/name/"), url-prefix("http://surnames.behindthename.com/name/") {
/* === NAME PAGE === */

/* main top header (the gradiant) */
div.body > div:first-child > div:first-child {
    background: #444 !important;
}
/* thinner line on top of the top header */
div.body > div:first-child > div:first-child > div:first-child > div:first-child {
    background: #222 !important;
    margin: 0px !important;
}
/* background of the social buttons */
div.body > div:first-child > div:first-child > div:first-child > div:first-child > div:first-child {
    background: none !important;
}
/* social buttons */
div.social_block {
	-webkit-filter: sepia(100%) saturate(50%) brightness(80%); 
    filter: sepia(100%) saturate(50%) brightness(80%); 
}
/* active tab title link */
div.body > div:first-child > div > a.nametab_active {
    color: #eee !important;
}
}

@-moz-document regexp("http://(surnames|www).behindthename.com/name/[^/]+") {
/* === NAME PAGE: Name === */

/* add to list/contribute buttons top right */
a.namebutton {
    background-color: #444 !important;
    border: 2px solid #1b1b1b !important;
    color: #ccc !important;
    padding: 2px 5px !important;
}
a.namebutton img {
    display: none;
}

/* popularity rank graphs */
img[src^="/top/thumbs/"] {
    -webkit-filter: invert(100%) grayscale(100%) contrast(90%) brightness(90%);
    filter: invert(100%) grayscale(100%) contrast(90%) brightness(90%);
}
}

@-moz-document regexp("http://(surnames|www).behindthename.com/name/.+?/top") {
/* === NAME PAGE: Popularity === */

/* graph image */
div#graph_div img {
    -webkit-filter: invert(100%) hue-rotate(110deg) grayscale(70%) brightness(180%);
    filter: invert(100%) hue-rotate(110deg) grayscale(70%) brightness(180%);
}
/* table background */
div#numbers_div td {
    background: none !important;
}
/* table bar color */
div#numbers_div td > div {
    background: #955 !important;
}
/* "names shown" green underline */
span.tiny[style*="#00ff00"] {
    border-bottom: 2px solid #955 !important;
}
}

@-moz-document regexp("http://(surnames|www).behindthename.com/name/.+?/rating") {
/* === NAME PAGE: Ratings === */

/* percent text color */
div.body > blockquote > table td > span {
    color: #aaa !important;
}
}

@-moz-document regexp("http://(surnames|www).behindthename.com/name/.+?/comments") {
/* === NAME PAGE: Ratings === */

/* percent text color */
div.comment, center > p > span {
    color: #111 !important;
}
span.tiny {
    color: #ccc !important;
}
}
