css:
/* i really want this to be global */
@-moz-document domain("academubalan.uniquindio.edu.co") {
input {
    border: solid 2px #3b7bbc;
    border-radius: 2px;
    padding: 2px 4px;
    outline: none;
    transition: all 0.5s;
    width: 70%;
}
input:hover {
    border-radius: 10px;
}
input:focus {
    box-shadow: 2px 2px 5px lightblue inset;
    border-radius: 10px;
    background: lightblue;
}
.titulos {
    border-radius: 4px;
    padding: 5px 10px;
    border: solid 2px white;
    background: linear-gradient(#3b7bbc, #025ab3);
    color: white !important;
}
.titulos:hover{
    background: linear-gradient( #025ab3,#3b7bbc);
}

a {
    color: #3b7bbc !important;
    cursor: pointer;
    font-weight:normal;
}
a:hover {
    font-weight: bold !important;
}
table td {
    color: #3b7bbc;
}
form {
    border: solid 2px #3b7bbc !important;
}
#container {
    width: 94%;
}
#header img {
    width: 55%;
    margin: auto;
    box-shadow: 2px 2px 5px black;
    border: solid 5px white;
}
#whitebox_tr ,#whitebox_tr,#whitebox_tl,#whitebox_t{
    opacity:0;
}
.footer {
    background: linear-gradient(#3b7bbc, #025ab3);
    padding: 10px 20px;
    width: 95%;
    margin: auto;
    font-size: 13px;
    border: solid 5px white;
    color: white;
}
.footer a {
    color: white !important;
}
ul li {
    border: solid 2px #3b7bbc;
    padding: 10px 20px;
    list-style: none;
    background: lightblue;
    margin-top: 3px;
}
ul li font {
    color: #025ab3 !important;
}
#dropinboxv2 {
    box-shadow: 2px 2px 5px black;
}
#usuario {
  /*  background: #fff url(../imagenes/users.ico) no-repeat 98% center;*/
    background-size: 10% 100%;
    background-position: left;
    padding-left: 30px;
}
#password {
    /*background: #fff url(../imagenes/users.ico) no-repeat 98% center;*/
    background-size: 10% 100%;
    background-position: left;
    padding-left: 30px;
}
.Estilo2,.Estilo3{
	color:blue !important;
}
#whitebox,#wrapper_r,#container,#wrapper,#header{
	background:rgba(255,255,255,0) !important;
    border:solid 0px white !important;
}

#content{
	width:96%;
    background:white;
    margin:auto;
    border-radius:5px;
    border:solid 1px gray;
}

.Estilo1{
	padding:10px 15px;
	border:solid 2px red;
    border-radius:5px;
    background:pink;
    font-weight:bold;
    margin-top:10px;
}
}
