css:
@-moz-document domain("serebii.net") {


/*Body and links*/

.body, .post {
    color: #FFFFFF;
    font-family: calibri;
    font-size: 15;
}
a:link {
    color: #C3C3C3;
    text-decoration: none;
}
a:visited {
    color: #556B2F;
    text-decoration: none;
}
a:hover {
    color: #747473;
    text-decoration: none;
}

.td {
    font-family: calibri;
    font-size: 15;
}
/* Menu */
#menu,
.tbody {
    background: #383838 !important;
}

#menu a:link {
    color: #FFFFFF;
    font-family: calibri;
    font-size: 13;
}
#menu a:visited {
    color: #556B2F;
    font-family: calibri;
    font-size: 13;
}
#menu a:hover {
    color: #91C536;
    font-family: calibri;
    font-size: 13;
}

/* Tables */

#td {
    color: #FFFFFF;
    font-family: calibri;
    font-size: 15;
}
}
