css:
@-moz-document domain("www.douyu.com") {
#header > div > div > div.o-login.fl > div > div > div.box2.skill-level,
#header > div > div > div.o-login.fl > div > div > div.box1.chat-member,
#header > div > div > div.o-login.fl > div > div > div.box5.task.clearfix,
#left.left-menu,
#anchor-info > div.relate-text.fl > div.headline.clearfix > a,
#js-live-room-recommend,
#js-chat-notice,
#js-fans-rank,
#anchor-info > div.btn-group.fr > div.sq-wrap,
#js-live-room-normal-equal-right > div.column.rec,
#js-live-room-normal-equal-left,
#js-stats-and-actions > div.task-Getyw.clearfix.fl > div.task-btn.fl.bor,
#js-yw-rmd-rooms,
#js-stats-and-actions > div.task-Getyw.clearfix.fl > div.get-yw.fl > div.yw-collect-box.ywrmd > p,
#js-chat-cont,
.room-ad-top,
.chat-top-ad,
.pay-task,
.focus-lead,
.room-wbshare,
.super-rmd-bg.tggcontrol,
.column.rec,
.catagory-order-container,
.sharkgirlbtn,
.order-national-trigger,
.order-national-container,
.weixin-tip,
.action-list,
#js-chat-right-ad {
    display: none !important;
}
#container {
    width: 100% !important;
}
#mainbody {
    padding: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    width: auto !important;
}
#js-live-room-normal-left {
    width: 100% !important;
    float: none !important;
    display: block !important;
}
#js-room-video {
    height: 580px !important;
}
#js-live-room-normal-right {
    float: none !important;
    width: auto !important;
    display: block !important;
    position: static !important;
}
#js-send-msg {
    display: flex;
    margin-top: 3px;
}
.chat {
    position: static;
}
#js-chat-speak {
    border: 0;
    position: absolute;
    top: 602px;
    left: 130px;
    background-color: #23272b;
    padding: 5px;
    display: flex;
    height: auto;
    border-radius: 5px;
}
.chat-feat .cs-textarea {
    flex: 2;
    height: 23px;
    background-color: rgba(255, 255, 255, 0.9);
    padding: 0 3px;
    border-color: #eee !important;
}
.chat-feat .cs-textarea:focus {
    background-color: #fff;
}
.chat-feat .b-btn {
    flex: 1;
    height: 25px;
    line-height: 25px;
    background-color: #000;
}
#js-color-barrage {
    margin-left: 15px;
}
.room-mes,
#js-stats-and-actions,
#js-live-room-normal-equal-right {
    border: 0;
}
.anchor-pic {
    display: none;
}
.relate-text {
    float: none !important;
    display: flex;
}
.relate-text .headline {
    flex: 2;
    padding-left: 10px;
}
.relate-text .acinfo-fs-con,
.relate-text .tag-fs-con {
    flex: 1;
    line-height: 28px;
    padding-top: 8px;
}
.relate-text .acinfo-fs-con {
    flex: 1.5;
}
.room-mes .btn-group .focus-box {
    top: 30px !important;
}
#anchor-info {
    height: auto !important;
}
.give-gift .g-list li {
    width: 25px !important;
    height: 25px !important;
    padding: 2px !important;
}
.give-gift .g-list li .g-img {
    width: 25px !important;
    height: 25px !important;
    padding: 2px !important;
}
.give-gift .g-list li:hover {
    width: 25px !important;
    height: 25px !important;
    padding: 2px !important;
}
.give-gift .g-list li:hover .g-img {
    width: 25px !important;
    height: 25px !important;
}
.g-list-cont {
    margin-top: 12px;
}
#container[style*=staticlive] {
    background: none !important;
}
#container[style*=staticlive] .live-room {
    padding-top: 60px !important;
}
#container[style*=staticlive] #js-chat-speak {
    top: 662px;
}
/*奖牌榜 助威榜 按钮缩小*/
.order-olympics-trigger li {
    width: 58px !important;
    height: 21px !important;
    background-size: 650px 200px;
}
.order-olympics-trigger li.order-olympics-trigger-item1 {
    background-position: 0px 0px !important;
}
.order-olympics-trigger li.order-olympics-trigger-item2 {
    background-position: -67px 0px !important;
}
.order-olympics-show {
    top: 16px !important;
    left: -140px !important;
    width: 138px !important;
    height: 21px !important;
}
.order-olympics-trigger li:hover {
    -webkit-filter: drop-shadow(1px 1px 3px rgba(0,0,0,0.5));
    background-position-y: -1px !important;
}
.order-olympics-trigger li:active {
    -webkit-filter: drop-shadow(2px 2px 1px rgba(0,0,0,0.35));
    background-position-y: 1px !important;
}
}
