css:
@-moz-document domain(habitica.com) {

.toolbar-notifs > a span.notification-counter {
  
    color: #537272;
  
    background: #f4f7f7;
  
    padding: 2px;
  
  }
  

  
      .toolbar-nav .toolbar-button-dropdown.highlight .toolbar-nav .toolbar-button-dropdown.highlight textarea, .toolbar-nav .toolbar-button-dropdown.highlight, .toolbar-nav .toolbar-button-dropdown.highlight > a, .toolbar-nav .toolbar-button-dropdown.highlight > a:nth-of-type(2) {
        
      background-color: #f4f7f7 !important;
        
      border-color: #cccccc !important;    
        

    }
  
      .toolbar-mobile a:hover, .toolbar-nav .toolbar-button a:hover, .toolbar-nav .toolbar-button-dropdown a:hover, .toolbar-options > li > a:hover, .toolbar-nav .toolbar-button-dropdown.highlight a:hover {

      background: none !important;

      background-color: #eff3f4 !important;

      color: #537272 !important;


    }

}
