css:
@-moz-document domain("lexicongta.com") {
/* Dashboard and Navbar */

.content-wrapper, .right-side {
    background-color: #333;
}

.panel-default>.panel-heading {
    color: #fff;
    background-color: #464545;
    border-color: #444;
}

.panel-body {
    background: #333;
}

.message {
    color: #ccc;
}

.btn btn-main dropdown-toggle {
    background-color: #333;
}

.modal-header {
    border-bottom-color: #242424;
    background: #333;
}

.modal-title {
    color: #ccc;
}

.modal-body {
    background: #222;
}

.modal-footer {
    border-top-color: #242424;
    background: #333;
}

.btn-default.hover, .btn-default:active, .btn-default:hover {
    background-color: #444;
    color: #999;
    border-color: #444;
}

.btn-default {
    background-color: #333;
    color: #999;
    border-color: #444;
}

.navbar-nav>.user-menu>.dropdown-menu>.user-body {
    border-bottom: 1px solid #333;
    border-top: 1px solid #333;
    background: #444;
}

.navbar-nav>.user-menu>.dropdown-menu>.user-body a {
    color: #999!important;
}

.dropdown-menu {
    border-color: #333;
    background: #333;
}

.navbar-nav>.user-menu>.dropdown-menu>.user-footer {
    background-color: #444;
}

.panel-footer {
    background-color: #333;
    border-top: 1px solid #444;
}

.input-group .form-control:first-child, .input-group-addon:first-child, .input-group-btn:first-child>.btn, .input-group-btn:first-child>.btn-group>.btn, .input-group-btn:first-child>.dropdown-toggle, .input-group-btn:last-child>.btn-group:not(:last-child)>.btn, .input-group-btn:last-child>.btn:not(:last-child):not(.dropdown-toggle) {
    background: rgba(0, 0, 0, 0.34);
    border-color: #444;
}

.panel-default {
    border-color: #444;
}

.panel {
    background-color: #333;
}

.btn btn-main dropdown-toggle {
    background: #444;
}

.btn {
    background: #292828;
}

.dropdown pull-right {
    background: #333;
}

.alert a {
    color: #444;
}

.notice-highlight {
    color: #333 !important;
}

.navbar-nav>.user-menu>.dropdown-menu>.user-footer .btn-default:hover {
    background: #121212;
}
}

@-moz-document domain("lexicongta.com") {
/* Let's get this party started */
::-webkit-scrollbar {
    width: 10px;
}
 

/* Track */
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
    background: #333;
}
 
/* Handle */
::-webkit-scrollbar-thumb {
    background: #3DB67D; 
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}
::-webkit-scrollbar-thumb:window-inactive {
	background: #3aaa75;
}
}

@-moz-document domain("lexicongta.com") {
/* User Profile */

.box.box-default {
    border-top-color: #3DB67D;
    background: #333;
}

.list-group-unbordered>.list-group-item {
    background: #333;
    border-color: #555;
}

b, strong {
    color: #666;
}

.box-body {
    background: #333;
}

.pull-right {
    color: #726d6d;
}

.box-header.with-border {
    border-bottom: 1px solid #444;
    background: #333;
}

.box-header .box-title, .box-header>.fa, .box-header>.glyphicon, .box-header>.ion {
    color: #666;
}

hr {
    border-top: 1px solid #444;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    border-bottom-color: #333;
}

.nav-tabs-custom>.nav-tabs>li {
    border-top: 3px solid rgba(0,0,0,.125);
}

.nav-tabs-custom>.nav-tabs {
    border-bottom-color: transparent;
    background: rgba(0, 0, 0, 0.74)                                            
}

.nav-tabs-custom>.nav-tabs>li.active:hover>a, .nav-tabs-custom>.nav-tabs>li.active>a {
    background-color: #333;
    color: #999;
    margin-bottom: 1;
}

.nav-tabs-custom>.nav-tabs>li>a, .nav-tabs-custom>.nav-tabs>li>a:hover {
    color: #777;
}

p {
    color: #666;
}

.nav-tabs-custom>.tab-content {
    background: rgba(0, 0, 0, 0.82);
}

.input-group .form-control:last-child, .input-group-addon:last-child, .input-group-btn:first-child>.btn-group:not(:first-child)>.btn, .input-group-btn:first-child>.btn:not(:first-child), .input-group-btn:last-child>.btn, .input-group-btn:last-child>.btn-group>.btn, .input-group-btn:last-child>.dropdown-toggle {
    background: #222;
    border-color: #444;
}

.form-horizontal .checkbox, .form-horizontal .radio {
    color: #777;
}

.label-default {
    background-color: #444;
}

.nav-tabs-custom {
    background: #666;
}

.nav-tabs-custom.tab-default>.nav-tabs>li.active {
    border-top-color: #3db67d;
}

.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #161414;
    border-color: #222;
}

.form-control {
    background: #222;
    border-color: #444;
    color: #666;
}

.nav-tabs-custom>.nav-tabs>li.active>a {
    border-left-color: #333;
    border-right-color: #333;
}
}

@-moz-document domain("lexicongta.com") {
/* Menu Download */

* {
    color: #777;
}

.panel-success>.panel-heading {
    color: #00a65a;
    background-color: #444;
    border-color: #333;
}

.panel {
    border-color: #444;
}

.box {
    border-top: 3px solid #3db67d;
}

.btn-app {
    border: 1px solid #444;
}
}

@-moz-document domain("lexicongta.com") {
/* Referrals Page */

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    border-top: 1px solid #444;
    background: #333;
}

.table>thead>tr>th {
    border-bottom: 2px solid #504f4f;
}
}

@-moz-document domain("lexicongta.com") {
/* Highlight Color */

::selecton {
    background: #3DB67D;
}

::-moz-selection {
  background: #3DB67D; /* Gecko Browsers */
}

::-o-selection {
      background: #3DB67D;
}
}

@-moz-document domain("lexicongta.com") {
/* Suggestions Page */

.panel-info>.panel-heading {
    color: #215269;
    background-color: #69a0bc;
    border-color: #444;
}
}

@-moz-document domain("lexicongta.com") {
/* Lexicon Staff Page */

.product-list-in-box>.item {
    border-bottom: 1px solid #333;
    background: rgba(0, 0, 0, 0.20);
}

.products-list .product-img img {
    margin-left: 5px;
}


/* Box Edits to remove the white corners at the bottom */

.box.box-danger {
    background: #333;
}

.box.box-primary {
    background: #333;
}
}

@-moz-document domain("lexicongta.com") {
/* Lexicon Forum */

.table>tbody+tbody {
    border-top: 2px solid #555;
}

/* Stickied Thread */
.table>thead>tr>th.warning {
    background-color: #444;
}

/* Closed Thread */
.table>thead>tr>th.danger {
    background-color: #a94442;
}

/* Breadcrumb */
.breadcrumb {
    background-color: #444;
}

/* New Thread Button */
.btn-info {
    border-color: #3db67d;
}

.btn-info:hover {
    border-color: #3db67d;
    background: #00a65a;
}


/* Closed Thread */
.table>thead>tr>th.danger {
    background-color: #a94442;
}

/* Postbit */
.info-component {
    border-bottom: 2px solid #444;
}

.info-container {
    border-right: 2px solid #444;
}

/* Stickied and closed thread background fixes */

.table>tbody>tr.danger>td, .table>tbody>tr.danger>th, .table>tbody>tr>td.danger, .table>tbody>tr>th.danger, .table>tfoot>tr.danger>td, .table>tfoot>tr.danger>th, .table>tfoot>tr>td.danger, .table>tfoot>tr>th.danger, .table>thead>tr.danger>td, .table>thead>tr.danger>th, .table>thead>tr>td.danger, .table>thead>tr>th.danger {
    background-color: #a94442;
}

.table>tbody>tr.warning>td, .table>tbody>tr.warning>th, .table>tbody>tr>td.warning, .table>tbody>tr>th.warning, .table>tfoot>tr.warning>td, .table>tfoot>tr.warning>th, .table>tfoot>tr>td.warning, .table>tfoot>tr>th.warning, .table>thead>tr.warning>td, .table>thead>tr.warning>th, .table>thead>tr>td.warning, .table>thead>tr>th.warning {
    background-color: #444;
}
}

@-moz-document domain("lexicongta.com") {
/* Reported Bugs Page */

.panel-danger>.panel-heading {
    color: #7c312f;
    background-color: #ca6361;
    border-color: #444;
}
}

@-moz-document domain("lexicongta.com") {
/* Lexicon Download Button - Hover */

.btn-app:hover {
    border: 1px solid #444;
    background: #333;
    color: #ccc;
}
}

@-moz-document domain("lexicongta.com") {
/* Community Settings */

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: #353535;
}
}

@-moz-document domain("lexicongta.com") {
/*  Lexicon 3.0 Bug Reports */

.panel-warning>.panel-heading {
    color: #080808;
    background-color: #3db67d;
    border-color: #333;
}
}

@-moz-document domain("lexicongta.com") {
/* Polls */

.progress {
    background-color: #3c3b3b;
}

.badge {
    background-color: #232323 !important;
}
}
