@-moz-document url-prefix("https://forum.lowyat.net"), url-prefix("https://forum.lowyat.net") {
/*
Lowyat Forum High Contrast
v20161015
Copy-pasta by https://forum.lowyat.net/user/96132 aka 1024kbps
*/
/*[[font]]*/
}

@-moz-document url-prefix("http://forum.lowyat.net"), url-prefix("https://forum.lowyat.net") {

html,
body,
#userlinks,
#userlinksguest,
#submenu,
#submenu a:link,
#submenu  a:visited,
.newslink td,
.newslink span,
.newslink input,
/*Lowyat.NET Rules and Regulations*/
.tablepad,
/*calendar*/
.calmonths,.calday,
div.lcnb_news_inner
{
	Background:#000!important;
	color:#fff!important
}
.lastaction
{
	color:#aac!important;
}
article.lcnb_news.lcnb_type_rss
{
	border:1px solid #aaf!important;
	box-shadow:1px 1px 0px #aaf!important
}

.textarea
{
	color:#aaa!important;
}

a:link
{
    color: #fff!important;
}
#ads_bottom_leaderboard2
{
	display:none!important;
}
a:visited,
a:active,
table,
tr,
.subtitle a:link,
.button
{
	background: transparent!important;
    color: #bbc!important;
}

/*for changing table color*/
p,
.row1,.row2,
.forumdesc, .forumdesc a:link, .forumdesc a:visited, .forumdesc a:active,
.borderwrap h3, .maintitle, .maintitlecollapse,
table th, .borderwrap table th, .subtitle, .subtitlediv, .postlinksbar,
.catend,
.toplinks span,
.formtable td.formtitle, .formsubtitle,
#gfooter td,
.pagelink, .pagelinklast, .minipagelink, .minipagelinklast,
.darkrow1,
input, textarea, select,
td,
.barc
{
	background:#000!important;
	color: #fff!important;
}
.pagecurrent
{
	background:#aaf!important;
	color: #000!important;
}
#navstrip a:link, #navstrip  a:visited,
.borderwrap table th a:link,
.desc
{
	    color: #fff!important;
}
/*border color*/
.borderwrap
{
	background:#aaf!important;
}

div#navigation_menu.borderwrap.background
{
	background:#000!important;
}
.borderwrap, .borderwrapm,
/*/control Panel border*/
#ucpcontent, #ucpmenu, textarea, .pformleft,
input, .codebuttons, select.dropdown,
.activeusers,
td.formbuttonrow
{
	border:1px solid #aaf
}

.activeusers
{
	background:none!important
}

div#qr_open .borderwrap
{
	background:#000!important;
}
#userlinks, #userlinksguest,
#submenu
{
	border:1px solid #aaf
}
.newslink
{
	Background:#000!important;
	color:#eee!important;
	border:1px solid #aaf;
}
.bar, .barb, .barc
{
	border:1px solid #aaf!important;
}

.quotetop
{
	background:#555!important;
	color:inherit!important;
}
.quotemain
{
	background:#223045!important;
	color:#aaa!important;
}

.borderwrap p.formbuttonrow, .borderwrap p.formbuttonrow1,
.bar, .barb,
#starred_tabs LI A,
SPAN.member-tagged
{
	background:#66c!important
}

#starred_tabs LI A:hover, #starred_tabs LI A.selected
{
	background-color:#caa!important
}

#replies_to_me
{
	background:rgba(64,64,92,0.85)!important;
	color:#aaa!important;
}
.borderwrap h4 {
    background: #000!important;
	color: #aaf!important;
    /*border: 1px solid #FFF;
    border-bottom: 1px solid #5176B5;
    border-top: 1px solid #5176B5;*/
}
.errorwrap h4, #pmnotewrap h4
{
	background:#543535!important;
	color:#aaa!important;
}
#ucpmenu, #ucpcontent,
.gsc-modal-background-image
{
	background:transparent!important;
}
.gsc-results-wrapper-overlay
{
	background:rgba(64,64,92,0.8)!important;
}
.gs-webResult.gs-result a.gs-title:link, .gs-webResult.gs-result a.gs-title:link b, .gs-imageResult a.gs-title:link, .gs-imageResult a.gs-title:link b,
.gs-result .gs-title
{
	Background:#000!important;
	color:#aac!important
}
.gs-webResult div.gs-visibleUrl, .gs-imageResult div.gs-visibleUrl
{
		color:#aac!important
}
.gs-webResult .gs-snippet, .gs-imageResult .gs-snippet, .gs-fileFormatType
{
	color:#fff!important;
}

table th, .borderwrap table th, .subtitle, .subtitlediv, .postlinksbar,
.borderwrap h3, .maintitle, .maintitlecollapse
{
	border-bottom:#fff!important;
	border:none
}

.gsc-results .gsc-cursor-box .gsc-cursor-page
{
	background-color:transparent;
	color:#aaf!important;
}

.quotemain,.quotetop
{
	border-left:4px solid #aba!important;
}
.pagelink, .pagelinklast, .pagecurrent, .minipagelink, .minipagelinklast,
.textarea, .searchinput, .button, .gobutton
{
	border:1px solid #aac!important;
}

.signature
{
	color: #666!important;
}
#topic_replies
{
	background:rgba(64,64,92,0.85)!important;
	color:#eee!important;
}

.spoilertop
{
    background: #223030!important;
    color: #aaa;
}
.spoilermain
{
    background: #302230!important;
	color: #abb!important;
}
.searchinput
{
	color:#fff!important;
}

/*buttons image filter*/
div.borderwrap td.pformleft img,
div.borderwrap td.pformright img,
td.post2.post_td_left img,
td.post1.post_td_left img,
td.post2.post_td_right img,
td.post1.post_td_right img,
/*td.post1 img,
div.signature img,
td.post2 img,
td.row2 img,
div.borderwrap td.row1 img,*/
div#ucpcontent img.avatar,
div#ucpcontent div.signature img,
/*google search*/
.gs-web-image-box img.gs-image,
div#topic_content.borderwrap img.avatar,
div#topic_content.borderwrap div.borderwrap img,
div.borderwrap td.row2 div.signature img,
div.borderwrap img.avatar,
div#forum_topic_list.borderwrap td.row1 img
{
	filter:none;
	-webkit-filter:none
}
div#fo_stat.borderwrap td.row1 img,
td.nopad img,
td.formbuttonrow.post_td_left img,
td.formbuttonrow.post_td_right img,
p img,
/*mobile version*/
/*div#fo_80.borderwrap img,
div#fo_81.borderwrap img,
div#fo_82.borderwrap img,
div#fo_83.borderwrap img,
div#fo_84.borderwrap img,
div#fo_85.borderwrap img,
div#fo_156.borderwrap img,
div#fo_170.borderwrap img,
div#fo_252.borderwrap img,
div#fo_222.borderwrap img,
div#starred_forum_list img,*/
table.newslink img, table.newslink input.button,
div.borderwrap td.row2 img,
/*div.borderwrap td.row2 img,*/
/*private message img*/
div.borderwrapm td.row1 img,
div.borderwrap input.gobutton,
div#ucpcontent img,
div.activeusers img,
td.formbuttonrow img,
table.borderwrap img
{
	/*[[filter]]*/
}
/*private message sectiom*/
td.formbuttonrow,
/*red warning above GamersHideout*/
.ruleswrap
{
	background:#000!important
}

/*lo-fi version*/
.postdate
{
	color:#fff!important
}
.postwrapper, .ipbnav, #ipbcontent
{
	border:1px solid #aaf
}
.posttopbar
{
	background:#55c!important;
}

.ipbnavsmall
{
	border:1px dotted #aaf
}
}

