css:
@-moz-document url-prefix("https://myaucklanduni.ac.nz") {
body, html {
    font-family: 'Segoe UI', 'San Francisco', 'Roboto', sans-serif !important;
}
#main {
    max-width: 100% !important;
    background: 
linear-gradient(63deg, #eee 23%, transparent 23%) 7px 0, 
linear-gradient(63deg, transparent 74%, #eee 78%), 
linear-gradient(63deg, transparent 34%, #eee 38%, #eee 58%, transparent 62%), 
#f3f3f3;
background-size: 16px 48px;
}
#main .list-group-item.active>.badge, #main .nav-pills>.active>a>.badge {
    background-color: #e74c3c !important;
    padding: 4px 7px;
}
#main-body a {
	text-decoration: none;
    color: #00467F;
}
#main-body a:hover {
    text-decoration: underline;
}
#main h1 {
    display: none;
}
#main h1.by-line {
    display: block;
    font-family: 'Segoe UI Light';
    font-weight: 300;
    line-height: 80px;
    font-size: 26px !important;
}
.navbar-header {
    max-width: 1200px;
	margin: 0 auto;
    float: none;
}
.navbar-default {
    border-radius: 0;
}
.navbar-default .navbar-brand {
    height: 80px !important;
}

.navbar-default .navbar-brand img {
	height: 58px;
}
.row #icon-portlets {
    margin-top: 0;
}
.row #icon-portlets .nav>li {
    margin: 20px 15px;
}
.row #icon-portlets .nav>li:hover {
    opacity: 0.8;
}
.row #icon-portlets .nav>li .fa-4x {
    font-size: 3em;
}
.row .portlet-header {
    padding: 7px;
    border-radius: 3px 3px 0 0;
}
.row .portlet-header .fa-3x {
	font-size: 2em;
    margin: 5px;
}
#main .row .portlet-header h2 {
    margin: 0;
    font-weight: normal;
    text-indent: 6px;
    line-height: 36px;
    font-size: 20px;
}
.row .portlet-body {
    border-radius: 0 0 3px 3px;
}
#main .notice {
    padding: 0;
}
#main a .notice {
    padding: 8px;
    font-size: 14px;
    overflow: hidden;
}
.panel.standard-portlet > div {
    padding: 0 8px;
}
p {
    font-size: 14px;
}
#main-body .notice a {
    text-decoration: none;
    color: #00467F;
}
.notice a:hover .notice {
    background: #ddd;
    
}
#main #mastFooter {
    background: linear-gradient(transparent, #fff);
    color: #ccc;
}
#main #mastFooter a {
    color: #333;
}
#main-body {
	margin: 0 auto;
    max-width: 1200px;
}
}
