css:
@-moz-document domain("myanimelist.net") {
  /* Background */
  body:not(.ownlist) {
    background: url("http://i.imgur.com/YQOkUcg.jpg");
    background-size: 100% auto;
    background-attachment: fixed;
  }
  
  /* Background ~end */
  
  .page-common #myanimelist {
    margin-right: 2px;
  }
  
  .page-common #headerSmall {
    background: linear-gradient(to right, transparent, rgba(255, 255, 255, 0.9));
    background-image: url("http://i.imgur.com/hxUxzfN.png"); /* Logo */
    background-repeat: no-repeat;
    background-position: center 8px;
  }
  
  .page-common #headerSmall .banner-header-anime-straming {
    left: 0px;
  }
  
  /* User Menu */
  
  .page-common .header-list-button, .page-common .header-notification-button, .page-common .header-message-button {
    color: #9A3456 !important;
  }
  
  .page-common .header-list-button:hover, .page-common .header-notification-button:hover, .page-common .header-message-button:hover {
    color: #46373C !important;
  }
  
  .page-common .header-notification-button.has-unread::after {
    background-color: #A57F72;
    border: #86665B 1px solid;
    color: #FFF;
    text-shadow: 0px 0px 2px #86665B;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.5);
  }
  
  .page-common .header-notification-item.new {
    background-color: #FFF0FA;
  }
  
  .page-common .header-list .header-list-dropdown::before, .page-common .header-notification-dropdown .arrow_box::before, .page-common .header-profile .header-profile-dropdown.arrow_box::before {
    border-bottom-color: #46373C;
    border-width: 9px;
  }
  
  .page-common .header-list .header-list-dropdown::after, .page-common .header-notification-dropdown .arrow_box::after, .page-common .header-profile .header-profile-dropdown.arrow_box::after {
    border-bottom-color: #F1D5EE;
  }
  
  .page-common .header-list .header-list-dropdown, .page-common .header-profile .header-profile-dropdown {
    border-color: #46373C;
  }
  
  .page-common .header-list .header-list-dropdown ul li a, .page-common .header-profile .header-profile-dropdown ul li a {   
    background: #F1D5EE;
    border-color: #46373C;
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
  }
  
  .page-common .header-list .header-list-dropdown ul li a:hover, .page-common .header-profile .header-profile-dropdown ul li a:hover {
    background: #F9EDF7;
  }
  
  .page-common .header-profile.link-bg {
    background: transparent;    
  }
  
  .page-common .header-profile-link {
    color: #46373C;
  }
  
  .page-common .header-profile.link-bg:hover {
    background: transparent;
  }
  
  .page-common .header-profile.link-bg.on, .page-common .header-profile.link-bg.on:hover {
    background-color: transparent;
  }
  
  .page-common .header-profile-link:hover {
    color: #9A3456;
  }
  
  .page-common #header-menu > .border::before {
    border-left: 1px #46373C solid;
  }
  
  .header-profile-button {
    border-radius: 20px;
  }
  
  /* User menu ~end */
  
  /*Notifications popup*/
  
  .page-common .header-notification-dropdown .arrow_box {
    border-color: #46373C;
  }
  
  .page-common .header-notification-dropdown-inner h3 {
    background: #F1D5EE;
    border-color: #46373C;
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
    font-style: italic;
    font-weight: bold;
  }
  
  .page-common .header-notification-dropdown-inner h3 .mark-all {
    color: #9A3456;
    font-weight: normal;
    font-style: normal;
  }
  
  .page-common .header-notification-dropdown-inner h3 .mark-all.disabled {
    color: #8A7E82;
    font-weight: normal;
    font-style: normal;
  }
  
  .page-common .header-notification-dropdown-inner h3 .settings a {
    color: #9A3456;
    font-weight: normal;
    font-style: normal;
  }
  
  .page-common .header-notification-item a, .page-common .header-notification-item-header .category {
    color: #9A3456;
  }
  
  .page-common .header-notification-item > .inner.is-read {
    color: #8A7E82;
  }
  
  .page-common .header-notification-view-all a {
    background: #F1D5EE;
    border-color: #46373C;
    color: #9A3456;
    text-shadow: 0px 0px 2px #F9EDF7;
  }
  
  /*Notifications popup ~end*/
  
  /* Search/Nav Menu */
  
  .page-common #menu {
    background-color: #46373C;
    border-color: #251C21;
    border-width: 1px 1px 0px 1px;
    border-style: solid;
    box-shadow: 0pxw 0px 3px #F2CFE7;
  }
  
  .page-common #nav li a {
    background: #46373C;
    color: #E9E4E8;
    text-shadow: 0px 0px 2px #251C21;
  }
  
  .page-common #nav li a:hover, .page-common #nav li.hover a {
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
    background: #F1D5EE;
  }
  
  .page-common #nav li {
    background: #F1D5EE;
  }
  
  .page-common #nav ul a:hover {
    background: #F9EDF7;
  }
  
  .page-common #searchBar.searchBar #topSearchButon, .forum .forum-search-side .forum-search-input-submit {
    background: #F9EDF7;
    color: #46373C;
  }
  
  .page-common #searchBar.searchBar #topSearchButon.notActive, .forum .forum-search-side .forum-search-input-submit.notActive {
    background: #D7CCD5;
    color: #8E868D;
  }
  
  .page-common .incrementalSearchResultList {
    border-color: #46373C;
  }
  
  .page-common .incrementalSearchResultList .list.separator {
    background: #F1D5EE;
    padding: 2px 9px;
    border-color: #D0B2CD;
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
    font-style: italic;
    font-weight: bold;
  }
  
  .page-common .incrementalSearchResultList .list {
    border-color: #D0B2CD;
  }
  
  .page-common .incrementalSearchResultList .list .off .info.anime .name, .page-common .incrementalSearchResultList .list .off .info.manga .name, .page-common .incrementalSearchResultList .list .off .info.news .name, .page-common .incrementalSearchResultList .list .off .info.featured .name, .page-common .incrementalSearchResultList .list .off .info.forum .name, .page-common .incrementalSearchResultList .list .on .info .name {
    color: #9A3456;
  }
  
  .page-common .incrementalSearchResultList .list.focus {
    background-color: #F6E3F2;
    color: red;
  }
  
  .page-common .incrementalSearchResultList .list .off .info.anime .media-type, .page-common .incrementalSearchResultList .list .off .info.manga .media-type, .page-common .incrementalSearchResultList .list .off .info.news .media-type, .page-common .incrementalSearchResultList .list .off .info.featured .media-type, .page-common .incrementalSearchResultList .list .off .info.forum .media-type {
    color: #7C6F73;
  }
  
  .page-common .incrementalSearchResultList .list .on .info .media-type, .page-common .incrementalSearchResultList .list .on .info .extra-info {
    color: #6B5C61;
  }
  
  .page-common .incrementalSearchResultList .list.list-bottom {
    background-color: #46373C;
  }
  
  .page-common .incrementalSearchResultList .list.list-bottom a {
    color: #F1D5EE;
  }
  
  .page-common .incrementalSearchResultList .list.list-bottom.focus {
    background: #F1D5EE;    
  }
  
  .page-common .incrementalSearchResultList .list.list-bottom.focus a {
    color: #46373C;
  }
  
  /* Search/Nav Menu ~end */
  
  /* General */
  
  .page-common #contentWrapper {
    background: #FFF url("http://i.imgur.com/AfU5C3h.png") top right no-repeat;
    border: 1px solid #251C21;
  }
  
  .page-common .h1 {
    background: #F1D5EE;
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
    border-bottom-color: #251C21;
  }
  
  .header-right {
    color: #9A3456 !important;
    text-shadow: 0px 0px 2px #F9EDF7;
  }
  
  body.index #content, .page-common #content {
    background: rgba(255, 255, 255, 0.9);
  }
  
  .page-common a, .page-common a:visited {
    color: #796C98;
  }
  
  .page-common a:hover, .page-common a:visited:hover {
    color: #7993AC;
  }
  
  .page-common #content .breadcrumb .di-ib a, .hoverinfo_trigger strong {
    color: #9A3456 !important;
  }
  
  .page-common .hoverinfo {
    background-image: url("http://i.imgur.com/Lkhedmn.png");
  }
  
  .page-common .hoverinfo a {
    color: #D57C9A;
  }
  
  .anime-manga-search .genre-link .genre-list .genre-name-link:hover {
    background: #F9EDF7;
  }
  
  .btn-signup.btn-blue-rect.mauto, .page-advertising .btn-blue-rect, .page-advertising .btn-blue-rect.off, .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.notinmylist, .user-status-block .btn-user-status-add-list, .icon-top-ranking-page .prev, .icon-top-ranking-page .next {
    background-color: #46373C;
    text-shadow: 0px -1px rgba(0, 0, 0, 0.5);
  }
  
  .btn-signup.btn-blue-rect.mauto:hover, .page-advertising .btn-blue-rect:hover, .page-advertising .btn-blue-rect.off:hover, .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.notinmylist:hover, .user-status-block .btn-user-status-add-list:hover, .icon-top-ranking-page .prev, .icon-top-ranking-page .next:hover {
    background-color: #58464C;
    text-decoration: none;
  }
  
  /* General ~end */
  
  .icon-top-ranking-page .prev, .icon-top-ranking-page .next:hover {
    color: white !important;
  }
  
  div[style="text-align: left; padding: 7px;"] {
    background: rgba(255, 255, 255, 0.9);
  }
  
  .user-status-block .btn-user-status-add-list {
    border-color: #251C21;
  }
  
  .ownlist_anime_update {
    background: white !important;
  }
  
  #dialog {
    border: 10px solid #F9EDF7;
  }
  
  #dialog td {
    border: 1px solid #46373C;
  }
  
  .head-logo {
    box-sizing: border-box;
    background: url("http://i.imgur.com/OIqg5FP.png");
    padding-left: 433px;
  }
  
  .page-common .btn-form-submit.small {
    background: #F1D5EE;
    color: #46373C;
    text-shadow: none;
  }
  
  .page-common .btn-form-submit.small:hover {
    background: #F9EDF7;
    color: #46373C;
    text-shadow: none;
  }
  
  .top-ranking-table .table-header td {
    background-color: #46373C;
    border-color: #251C21;
  }
  
  .top-ranking-table .table-header td:first-of-type {
    border-color: #251C21;
  }
  
  .page-common .bgColor1, .majorPad .friendHolder {
    background-color: rgba(249, 237, 247, 0.5);
  }
  
  .majorPad .bgLightBlue {
    background-color: rgba(241, 213, 238, 0.7);
  }
  
  .widget.featured .news-list.widget .news-unit:hover {
    background: rgba(255, 238, 243, 0.8);
    border-bottom: 1px solid #F7BBCF;
  }
  
  .page-common .goodresult, .page-common .successQuery, .ownlist_style #new-style .info {
    background-color: #F1D5EE;
    border-color: #46373C;
    color: #46373C;
  }
  
  .page-common .goodresult a:link, .page-common .goodresult a:active, .page-common .goodresult a:visited, .ownlist_style #new-style .info a:link {
    color: #9A3456;
  }
  
  .quickadd-content .quickadd-form-search .inputButton {
    color: #46373C;
  }
  
  .quickadd-content .quickadd-form-search .inputButton:hover {
    background-color: #DDD;
  }
  
  .btn-sns-setting {
    background: #F1D5EE;
    border-color: #46373C;  
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
  }
  
  .btn-sns-setting:hover {
    background: #46373C;
    border-color: #251C21;  
    color: #F1D5EE;
    text-shadow: 0px -1px #46373C;
  }
  
  /* People & Characters */
  
  .people-favorites-ranking-table .table-header td, .characters-favorites-ranking-table .table-header td {
    background-color: #46373C;
    border: #251C21 0px solid;
    border-left: #251C21 1px solid;
  }
  
  .people-favorites-ranking-table .table-header td:first-of-type, .characters-favorites-ranking-table .table-header td:first-of-type {
    border-left: #251C21 1px solid;
  }
  
  .btn-choose-your-own {
    border-color: #796C98;
  }
  
  .btn-choose-your-own:hover {
    background: #AC9CD1;
    text-shadow: 0px -1px #796C98;
  }
  
  /* People & Characters ~end */
  
  /* News */
  
  body.news .news-side-block .header, body.news .featured-side-block .header {
    color: #46373C !important;
    text-transform: uppercase;
  }
  
  body.news .news-side-block .news-list .news:hover, body.news .featured-side-block .featured-list .featured:hover {
    background: rgba(255, 238, 243, 0.8) !important;
    border-bottom: 1px solid #F7BBCF !important;
  }
  
  body.news .news-side-block .news-list .news:hover .title, body.news .featured-side-block .featured-list .featured:hover .title {
    color: #46373C;
  }
  
  body.news .news-side-block .news-list .news .title, body.news .featured-side-block .featured-list .featured .title {
    color: #9A3456;
  }
  
  body.news .news-side-block .news-list .news .information .comment {
    color: #796C98;
  }
  
  .page-common .pagination .link.current, .page-common .pagination .link:hover {
    background-color: #46373C;
  }
  
  .news.featured .featured-search-side .featured-search-input-submit {
    color: #46373C;
  }
  
  .news-list .news-unit .image-link .image, .news-list .news-unit .image-link {
    border-radius: 20px;
  }
  
  .news-list .news-unit .image-link {
    border-color: #D57C9A;
  }
  
  /* News ~end */
  
  /* Advertising Page */
  
  .page-advertising #ad_menu li a:hover, .page-advertising #ad_menu li a.on, .page-advertising .ad_form_content label.required {
    color: #9A3456;
  }
  
  .page-advertising .ad-contents .text-blue1 {
    color: transparent;
  }
  
  .page-advertising .ad-contents .text-blue1::before {
    content: "*Purple character fields are required";
    color: #9A3456;
  }
  
  .page-advertising #ad_menu.fixed {
    left: 68%;
  }
  
  .page-advertising .ad-spec-sample-list {
    border-color: #F1D5EE;
  }
  
  .page-advertising .ad-spec-sample-button-block .ad-spec-sample-button-cell {
    background: #F9EDF7;
    border-color: #F1D5EE;
    color: #46373C;
  }
  
  .page-advertising .ad-spec-sample-button-block .ad-spec-sample-button-cell:hover {
    background: #F1D5EE;
    color: #46373C;
  }
  
  .page-advertising .ad-spec-sample-button-block .ad-spec-sample-button-cell.on, .page-advertising .ad-spec-sample-button-block .ad-spec-sample-button-cell.on:hover {
    background: #46373C;
    border-color: #F1D5EE;
    color: white;
  }
  
  /* Advertising Page ~end */
  
  /* Video list */
  
  .watch-video .video-list.episode, .watch-video .video-list {
    border: 1px solid #D57C9A;
    border-radius: 20px;
  }
  
  img[src="http://cdn.myanimelist.net/images/episodes/videos/img-watch-officail-anime.png"] {
    box-sizing: border-box !important;
    background: url("http://i.imgur.com/XwWb1GI.png") no-repeat !important;
    padding-left: 9999px;
  }
  
  .watch .video-menu-category .btn-category {
    background: #F9EDF7;
    border-color: #F1D5EE;
    color: #46373C;
  }
  
  .watch .video-menu-category .btn-category:hover {
    background: #F1D5EE;
    border-color: #F1D5EE;
    color: #46373C;
  }
  
  .watch .video-menu-category .btn-category.on {
    background: #46373C;
    border-color: #F1D5EE;
    color: white;
  }
  
  .user-status-block .btn-forum-episode {
    background-color: #46373C;
    border-color: #251C21;
    color: white;
    text-shadow: 0px -1px #251C21;
  }
  
  .user-status-block .btn-forum-episode:hover {
    background: #F1D5EE;
    border-color: #46373C;
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
  }
  
  /* Video list ~end */
  
  /* Clubs */
  
  .club-search-form-search .inputButton {
    background-color: #F9EDF7 !important;
    border: 1px solid #C3C3C3 !important;
    border-radius: 0px 8px 8px 0px !important;
    color: #46373C !important;
  }
  
  .club-search-form-search .inputButton.notActive {
    background-color: #F9EDF7 !important;
    color: #C3C3C3 !important;
  }
  
  .page-common .pagination .link-blue-box {
    background-color: #46373C;
    text-shadow: 0px -1px rgba(0, 0, 0, 0.5);
  }
  
  .page-common .pagination .link-blue-box:hover {
    background-color: #58464C;
  }
  
  .club-list .table-header td {
    background: #F1D5EE;
    border-color: #D5B9D2;
    color: #46373C;
    text-shadow: 0px 0px 2px #F9EDF7;
  }
  
  .club-list .table-data td {
    border-color: #D5B9D2;
  }
  
  .club-list .table-data:nth-of-type(2n+1) td {
    background-color: rgba(249, 237, 247, 0.5);
  }
  
  .club-list .btn-club-join {
    border-color: #46373C;
    color: #46373C;
  }
  
  .club-list .btn-club-join:hover {
    background-color: rgba(156, 30, 144, 0.1);
    color: #46373C;
  }
  
  /* Clubs ~end */

  /* Panel */
  
  .widget-header, .page-common .normal_header {
    color: #46373C !important;
    text-transform: uppercase;
  }
  
  #widget-episode-video .widget-slide-outer, #widget-episode-video {
    width: 100%;
  }
  
  .index-watch-description {
    width: 0px;
    display: none;
  }
  
  .item .fw-b {
    color: #9A3456 !important;
  }
  
  .page-common .lightLink, .page-common .lightLink:link, .page-common .lightLink:visited, .page-common .lightLink:active {
    color: #D57C9A;
  }
  
  .item .picSurround img, .page-common div.picSurround img {
    border: 1px solid #D57C9A !important;
    border-radius: 20px;
  }
  
  .item:hover {
    background: rgba(255, 238, 243, 0.8) !important;
    border-bottom: 1px solid #F7BBCF !important;
    box-shadow: none;
  }
  
  .item:hover .fw-b {
    color: #46373C !important;
  }
  
  .page-common .button_add, .page-common .button_form, .seasonal-anime-list .seasonal-anime .prodsrc a.button_add, .anime-filter .btn-anime-watch-status.button_add {
    padding: 1px 4px 1px 4px;
    background-color: #B0E1A6;
    border-bottom: 1px solid #95BF8D;
    color: #274B20;
    font-size: 9px;
  }
  
  .page-common .button_add:hover, .page-common .button_form:hover, .seasonal-anime-list .seasonal-anime .prodsrc a.button_add:hover, .anime-filter .btn-anime-watch-status.button_add:hover {
    background-color: #396B2F;
    border-bottom: 1px solid #274B20;
    color: #B0E1A6;
  }
  
  .widget-header a {
    color: #D57C9A !important;
  }
  
  .widget-header a:hover {
    color: #BF6D88 !important;
  }
  
  .widget-content a, .widget-content a:visited {
    color: #796C98;
  }
  
  .widget-content a:hover {
    color: #7993AC;
  }
  
  .video-menu-category {
    border-color: #F1D5EE;
  }
  
  /* Panel ~end */
  
  /* Tops */
  
  .ranking-digest {
    background-color: rgba(241, 213, 238, 0.5);
  }
  
  .ranking-digest .ranking-header {
    background-color: #46373C;
    border-bottom: #251C21 1px solid;
    color: #E9E4E8 !important;
    text-shadow: 0px 0px 2px #251C21;
    font-size: 14px;
    padding: 6px 8px 4px;
    text-align: left;
    font-family: Avenir, "lucida grande", tahoma, verdana, arial, sans-serif;
  }
  
  .ranking-digest .ranking-header .btn-view_more {
    color: #F1D5EE;
  }
  
  .ranking-digest.left li.ranking-unit .title {
    color: #796C98;
  }
  
  .ranking-digest.left li.ranking-unit .title:hover {
    color: #7993AC;
  }
  
  .ranking-digest .ranking-unit .rank {
    color: #D57C9A;
  }
  
  .ranking-digest .ranking-unit .image {
    border-radius: 20px;
    box-shadow: 0px 0px 0px 1px #fff, 0px 0px 0px 2px #D57C9A;
  }
  
  /* Tops ~end */
  
  /* Settings Nav */
  
  .page-common #horiznav_nav {
    border-color: #251C21;
    padding: 0 0 2px;
  }
  
  .page-common #horiznav_nav ul li .horiznav_active, .page-common #horiznav_nav ul li .horiznav_active:hover {
    background-color: #46373C;
    padding: 3px 8px;
    border: 1px solid #251C21;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    color: #E9E4E8;
    text-shadow: 0px -1px #251C21;
  }
  
  .page-common #horiznav_nav ul li a {
    color: #46373C;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
