css:
@-moz-document domain("anime-rpg-city.de") {
body {
    background-color: #1e2128;
    background-image: url('http://i.imgur.com/SumIaVe.jpg');
}

#main > div > div > .content {
    background-color: #292d36;
}

.wbbBoardList .wbbBoardNode1 {
    background-color: #292d36;
}

.wbbBoardList .wbbBoardNode1:hover {
    background-color: #292d36;
}

.wbbBoardList .wbbBoardNode2 {
    background-color: #1e2128;
}

.wbbBoardList .wbbBoardNode2:hover {
    background-color: #1e2128;
}

.tabularBox, .dialogTitlebar {
    background-color: #227766;
    background-image: none;
}

a {
    color: #999999;
}

a:hover {
    color: #61c7b2;
}

.containerHeadline > h3, .containerHeadline > h3 > a {
    text-shadow: none;
    color: #227766;
}

.wbbBoardList .wbbLastPost > div {
    background-color: transparent;
    box-shadow: none;
}

.tabularBox {
    border:none;
    border-radius: 0;
}

.wbbBoardList:not(.wbbBoardListReduced) li.wbbLastBoxElement > div {
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
-webkit-box-shadow: 0px 5px 13px -1px rgba(0,0,0,0.5);
-moz-box-shadow: 0px 5px 13px -1px rgba(0,0,0,0.5);
box-shadow: 0px 5px 13px -1px rgba(0,0,0,0.5);
    border-bottom: 1px solid #2a2d34;
}

.navigationHeader {
    background-color: #227766;
    border-bottom: none;
    border-radius: 0;
}

.breadcrumbs {
    background-color: #1e2128;
    border:none;
    border-radius:0;
}

#shoutbox {
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
    background-color: #1e2128;
}

#shoutbox .shoutboxContent.desc:after {
    background: linear-gradient(to bottom,#1e2128 0%,rgba(232,232,232,0) 100%);
}

.wbbBoardContainer > .wbbBoard.new > .icon::before {
    background-image: url('http://i.imgur.com/aDjmG2B.jpg');
}

.wbbBoardContainer > .wbbBoard > .icon::before {
        background-image: url('http://i.imgur.com/r8wHNeH.jpg');
}

.mainMenu {
    height: 60px;
   background: rgba(66,138,126,1);
background: -moz-linear-gradient(top, rgba(66,138,126,1) 0%, rgba(47,99,87,1) 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(66,138,126,1)), color-stop(100%, rgba(47,99,87,1)));
background: -webkit-linear-gradient(top, rgba(66,138,126,1) 0%, rgba(47,99,87,1) 100%);
background: -o-linear-gradient(top, rgba(66,138,126,1) 0%, rgba(47,99,87,1) 100%);
background: -ms-linear-gradient(top, rgba(66,138,126,1) 0%, rgba(47,99,87,1) 100%);
background: linear-gradient(to bottom, rgba(66,138,126,1) 0%, rgba(47,99,87,1) 100%);
}

.wbbBoardDescription {
    color: #777;
}

.badge.badgeUpdate {
    background-color: transparent;
    border: 1px solid #63c8b8;
}

.entryContent {
    color: #777;
}

#shoutbox .shoutboxEntry:hover {
    background-color: #292d36;
}

.boxHeadline > h1, .boxSubHeadline > h2 {
    text-shadow: none;
    color: #227766;
}

.breadcrumbs > ul > li > a {
    text-shadow: none;
    color: #777;
}

.breadcrumbs > ul > li > .pointer > span {
    border-color: transparent transparent transparent #292d36;
}

.breadcrumbs > ul > li > .pointer {
    border-color: transparent transparent transparent #1e2128;
}

.navigation > ul.navigationItems .icon, .navigation > ul.navigationMenuItems .icon, .navigation > ul.navigationIcons .icon {
    color: #BBB;
}

.icon, .fa {
    text-shadow: none;
}

.mainMenu > ul > li > a {
    color: #fff;
}

#shoutbox #shoutboxMessage {
    background-color: #292d36;
    border: #777;
}

::-webkit-scrollbar {
  width: 2px;
  height: 2px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #e1e1e1;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-thumb:hover {
  background: #ffffff;
}
::-webkit-scrollbar-thumb:active {
  background: #000000;
}
::-webkit-scrollbar-track {
  background: #666666;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-track:hover {
  background: #666666;
}
::-webkit-scrollbar-track:active {
  background: #333333;
}
::-webkit-scrollbar-corner {
  background: transparent;
}

.container, .message, .dropdownMenu, .attachmentThumbnail, .attachmentThumbnail > img, .attachmentThumbnail > a > img, .box48 .attachmentTinyThumbnail {
    border-radius: 0;
}

#main.sidebarOrientationRight {
    background-color:#292d36;
}

#main {
    background-color:#292d36;    
}

.navigationFooter {
    border-radius: 0;
    background-color: #227766;
}

.navigation > ul.navigationItems > li > a, .navigation > ul.navigationMenuItems > li > a {
    color: #fff;
}

.navigation > ul.navigationItems > li > p {
    color: #fff;
}

.containerList > li:nth-child(even) {
    background-color: #292d36;
}

.containerList > li:nth-child(odd) {
    background-color: #1e2128;
}

.containerList > li:first-child {
    border-top-left-radius:0;
    border-top-right-radius:0;
}

.containerList > li:last-child {
    border-bottom-left-radius:0;
    border-bottom-right-radius:0;
}

.container {
    border: 0;
}
}

@-moz-document domain("anime-rpg-city.de") {
.boxHeadline > h1 a, .boxSubHeadline > h2 a {
    color: #227766;
}

.button.active, input[type='button'].active, button.active, .button.active:hover, input[type='button'].active:hover, button.active:hover {
    border-color: #1e2128;
    background-color: #164e43;
    background-image: linear-gradient(#175145,#113d34 3px);
}

.button, input[type='reset']:not([disabled]), input[type='submit']:not([disabled]), input[type='button']:not([disabled]), button:not([disabled]){
    border-color: #1e2128;
    background-image: linear-gradient(180deg,#21695b,#41887c 2px,#306458);
}

.pageNavigation ul li > a {
    color: #fff;
    text-shadow: none;
}

.table td, .cke_wysiwyg_div table td {
    background-color: #1e2128;
}


.table > tbody > tr:nth-child(even) > td, .cke_wysiwyg_div table > tbody > tr:nth-child(even) > td {
    background-color: #292d36;
}

.table > tbody > tr:hover > td, .cke_wysiwyg_div table > tbody > tr:hover > td{
        background-color: #16181d;
}

#main > div > div > .sidebar {
    background-color: #227766;
}

#main > div > div > .sidebar > span > .collapsibleButton {
        border-color: #1e2128;
        background-color: #227766;
}

#main.sidebarOrientationRight .sidebar {
    border-left: 1px solid #1e2128;
}

.wbbBoardList:not(.wbbBoardListReduced) > li.wbbDepth1 > div {
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}

.info {
    background-color: #227766;
    background-image: linear-gradient(#40867a,#31685c);
}
}

@-moz-document domain("anime-rpg-city.de") {
li:nth-child(2n+1) .message .messageContent {
    background-color: #1e2128
}

.message .messageContent {
    background-color: #292d36;
}

.message {
	background-color: #1e2128;
}

li:nth-child(2n+1) .message.messageSidebarOrientationLeft .messageHeader::after {
	border-right-color: #1e2128;
}

.messageBody {
    color: #878a8c;
}

.message .messageContent .messageHeader .messageHeadline > h1 {
    color: #428a7e;
    text-shadow: none;
}
}
