/* i really want this to be global */

@-moz-document url-prefix(http://thepara.de){
div#current {
    margin-bottom: 1px;
    height: 3px;
}
.progress-bar.progress-bar-info {
    background-color: rgba(0, 188, 212, 0.25);
}
div#buffer {
    margin-top: -2px;
}
.pull-right {
    color: rgb(195, 195, 195);
    font-size: 12px;
}
span#tracktime {
    color: #DADADA;
    font-size: 9px;
}
span#tracktime2 {
    color: #DADADA;
    font-size: 9px;
}
div#queue-items-new {
    padding: 0px!important;
    height: 340px!important;
}
.row.clickableDiv {
    padding: 5px;
}
.nav-list-vivid div.active,
.nav-list-vivid div.active a,
.nav-list-vivid div.active a:hover,
.nav-list-vivid div.active a:focus {
    background-color: #6ABDC7!important;
}
button#trackinfo_button {
    background-color: #FFFFFF!important;
    color: #AFAFAF!important;
    width: auto!important;
    margin-top: 5px!important;
}
button#loadexternal_button {
    background-color: #FFFFFF!important;
    color: #A5A4A4!important;
    width: auto!important;
    margin-top: 5px!important;
}
button#btnSearch {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
}
button.btn.btn-info.btn-sm.dropdown-toggle {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;

}
button.btn.btn-info.btn-xs.dropdown-toggle {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;

}
.panel {
    background-color: #FFFFFF!important;
    color: white!important;
}
button#scrobbling_button {
    background-color: rgba(239, 239, 239, 0)!important;
    color: #ABABAB!important;
}
span#scrobbling_state {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
    padding: 3px;
}
.clearfix.clickableDiv {
    color: #9A8C8C;
    font-size: 10px!important;
    padding: 3px;
}
div#song_alternates {
    padding: 0px!important;
}
img {
    margin: 0!important;
}
div#playall_button {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
}
.searchresult_ellipsis {
    padding-bottom: 10px;
}
div#queueall_button {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
    padding-left: 5px!important;
    padding-right: 5px!important;
}
div#results_target > div:nth-child(3){
	height: 485px!important;
}
.slider-track {
    width: 8px;
}
.slider-handle.round {
    width: 15px;
    height: 15px;
    margin-left: -3px!important;
}
.slider-handle.round {
    background-color: #6ABDC7!important;
    color: white!important;
    margin-left: -2px!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 50%;
}
.progress-bar.progress-bar-success {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
}
button.btn.btn-primary.btn-xs.dropdown-toggle {
    background-color: #6ABDC7!important;
    color: white!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
}
span.fui-plus.text-primary.glyphicon-small {
    color: #6ABDC7!important;
}
div#login_status_text {
    color: #B7B7B7!important;
}
table.table.table-hover {
    color: rgba(129, 143, 150, 0.83)!important;
}
a.ignoreParentClick {
    padding-top: 1px;
    font-variant: normal!important;
    font-family: inherit;
    font-size: 12px!important;
    color: rgba(129, 143, 150, 0.83)!important;
}
.slider-track {
    border: 0px!important;
}
div#queue-items-new {
    overflow: hidden;
}
span {
    color: rgba(129, 143, 150, 0.83)!important;
}
div#results_target {
    color: #607D8B!important;
}
div#results_target > div >div{
	top: 30px;
}
body {
    background-color: #495152;
    zoom: 1.0;
    overflow: hidden;
    background-size: cover;
    animation: backgroundScroll 10s linear infinite;
}
.btn-info {
    background-color: #6ABDC7;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
}
.btn-info:hover {
    background-color: #e74c3c;
}
.nav-pills > li > a {
    background-color: #6ABDC7!important;
    border-left: 1px solid #16a085;
}
.nav-pills > li > a:hover {
    background-color: #e74c3c!important;
}
.nav-pills > li > a:active {
    background-color: #9c65c5!important;
}
input.cmn-toggle-round:checked + label:before {
    background-color: #1797a7!important;
}
span#numItems {
    background-color: white;
    color: #8e8e92!important;
    font-size: 9px;
    border-radius: 31px;
    margin-left: -3px;
}
.btn-group {
    margin: 5px;
}
.col-xs-3.colnogutter {
    display: none;
}
.playerBlock {
    width: 800px;
    height: 720px!important;
    margin-top: 30px!important;
}
.row.footerBlock {
    height: 100px!important;
}
#footer > .container{
	padding-top: 0px!important;
}
#footer{
	padding: 5px;
}
button.btn.btn-primary.btn-xs {
    background-color: #6ABDC7!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
}
button#btnMagic {
    background-color: #6ABDC7!important;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.32);
    border-radius: 4px;
}
button#btnMagic:hover {
    background-color: #e74c3c!important;
}
.slider-handle.round {
    margin-left: -4px!important;
}
.col-xs-12.colnogutter {
    background-color: #fbfbfb!important;
}
div#queue_title {
    font-size: 10px!important;
    color: #bfbfbf;
    margin-top: 13px!important;
    font-weight: 200!important;
}
.btn-xxs {
    padding: 4px 12px!important;
}
.btn-xs {
    padding: 4px 8px;
    font-size: 12px;
    line-height: 1.083;
    border-radius: 10px;
}
div#tab_browse_genres {
    height: 420px!important;
}
div#my-tab-content {
    height: 378px!important;
}
div#content {
    height: 520px!important;
}
.colnogutter > .row {
    height: 400px!important;
}
.nav-pills > li {
    float: left;
    width: 24%;
    text-align: center;
}
input[type="Button"]:hover {
    background-color: red!important;
}
@keyframes animate {
    from {
        background-position: 0 0;
    }
    to {
        background-position: 518px 0;
    }
}
@-webkit-keyframes backgroundScroll {
    from {
        background-position: -0 0;
    }
    to {
        background-position: -250px 0;
    }
}
@keyframes backgroundScroll {
    from {
        background-position: -0 0;
    }
    to {
        background-position: -250px 0;
    }
}





div#player {
    background: url(http://daily.oktagon.co.id/wp-content/uploads/2015/11/monasround.jpg) repeat;
    background-size: contain;
    background-repeat: round;
    -webkit-animation-name: MOVE-BG;
    -webkit-animation-duration: 100s;
    -webkit-animation-timing-function: linear;
    -webkit-animation-iteration-count: infinite;
    position: absolute;
    left: -30%;
    right: 0;
    top: 0;
    bottom: 0;
    width: 200%;
    z-index: -1;}

div#video_enclosure > div >div >div {
    display: none;
}

@-webkit-keyframes MOVE-BG {
	from {
		-webkit-transform: translateX(0);
	}
	to { 
		-webkit-transform: translateX(-20%);
	}
}




.slider-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    border-radius: 8px;
}

.slider-selection {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    border-radius: 8px;
}

::-webkit-scrollbar {
    width: 8px;
}
 
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.2); 
    border-radius: 8px;
}
 
::-webkit-scrollbar-thumb {
    border-radius: 8px;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.2); 
}


::-webkit-scrollbar-track:hover {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.6); 
    border-radius: 8px;
}
 
::-webkit-scrollbar-thumb:hover {
    border-radius: 8px;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.6); 
}


div#tab_top_tracks {
    margin-top: 20px;
}

ul#tabs {
    margin-top: 20px;
}}
