@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bungle.cafe") {
  
  @font-face {
  font-family: 'Vollkorn';
  font-style: normal;
  font-weight: 700;
  src: local('Vollkorn Bold'), local('Vollkorn-Bold'), url(https://fonts.gstatic.com/s/vollkorn/v6/wMZpbUtcCo9GUabw9JODeo4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
  
  @font-face {
  font-family: 'Muli';
  font-style: normal;
  font-weight: 400;
  src: local('Muli'), url(https://fonts.gstatic.com/s/muli/v7/z6c3Zzm51I2zB_Gi7146Bg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
  
  /*Header Images and Centering*/
  
  header.d-header.clearfix {

    background-color: #FF533D;
    
    color: #000000;

  }
  
  h2#site-text-logo.text-logo {
    
    font-family: Vollkorn, Open Sans;

    color: #000000;
    
    text-shadow: 1px 1px #DCDCDC;
    
    
  }
  
  
  
  i.fa.fa-search {

    /* Search Icon */
    color: #000000;

  }

  i.fa.fa-bars {

    /* Menu Icon */
    color: #000000;

  }
  
  

  
  /*Body Stuff*/
  
  body {
    
    font-family: Muli, Open Sans;

    background-color: #002D57;
    
    background-attachment: fixed;

    background-repeat: no-repeat;

    background-position: center top;

    /* main text color */
    color: #D9D9D9;

  }
  
  div#main-outlet.wrap {
    
   background-color: #1e1e1e;
            
   background-position: center top !important;
    
  }
  
  /* Categories */
  
  div.category-description {
    
    
  }
  
  /* Quote Stuff */
  
  .quote aside .title, aside.quote .title {

    /* TOP HALF OF QUOTE */

    /* quote bg color */


}

aside.quote blockquote {

    /* BOTTOM HALF OF QUOTE */

    /* quote bg color */

    background: rgba(0,0,0,0.5);

}


  
  /*Avatar Settings*/
  
  img.avatar {
    
    border-radius: 35% !important;
      
  }
  
  /* User Card Settings*/
  
  #user-card {
    
   background-color: initial;
  
   box-shadow:2px 1px 6px rgba(255,255,255,0.75);

  }
  
  #user-card.show-badges .badge-section .user-badge,#user-card.show-badges .badge-section .more-user-badges {
	 
    background:#DCDCDC;
	  
    border:1px solid #1e1e1e;
    	  
    color:#1e1e1e;
}
  
  /* Buttons above table */



	.list-controls .category-breadcrumb li.bullet > .badge-category {

		background-color: transparent !important;

		color: #DCDCDC !important;
    

	}



	.nav-pills > li > a {
    
    font-family: Muli, Open Sans;

		color: #000000 !important;

		background-color: #DCDCDC; /* For mobile view */

	}



	.nav-pills > li > a:hover {
    
    font-family: Muli, Open Sans;

		color: #000000;

		background-color: #DCDCDC;

	}



	.nav-pills > li.active > a, .nav-pills > li > a.active {
    
    font-family: Muli, Open Sans;

		color: #000000;

		background-color: #DCDCDC;

	}
  
  /*Timeline*/
  
  div.timeline-padding {    
    

    
  }
  
  div.timeline-handle {
    background-color: #F1FF56 !important;
}

div.timeline-scrollarea {
    border-color: #F1FF56 !important;
}
  
  

}
