css:
@-moz-document domain('joindiaspora.com'),
domain("sechat.org") {
  .avatar {
    border-radius: 100%;
  }
	
	body {
	font-family: Noto Sans, "Helvetica Neue", Helvetica, Arial, sans-serif;
}
	
  .navbar-inverse {
	background-color: #0b0b0b;
	border-color: #090909;
}
  body {
	font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
	font-size: 13px;
	line-height: 1.42857143;
	color: #4d4d4d;
}
  .navbar-inverse .navbar-brand {
	color: #f2f2f2;
}
  .navbar-inverse .navbar-brand:hover, .navbar-inverse .navbar-brand:focus {
	color: #3DAEE9;
	background-color: transparent;
}
 a.tag {
	color: #2ecc71;
}
  .opengraph-container a {
	color: #4d4d4d;
}
  a {
	color: #1d99f3;
	text-decoration: none;
}
a:hover, a:focus {
	color: #3daee9;
	/* text-decoration: underline; */
}
  .page-contacts, .page-conversations, .page-notifications, .page-people.action-show, .page-people.action-contacts, .page-photos, .page-posts, .page-profiles.action-edit, .page-services.action-index, .page-streams, .page-tags, .page-user_applications, .page-users.action-edit, .page-users.action-privacy_settings {
	background-color: #fcfcfc;
}
  body #main_stream .stream_element {
	border: 1px solid #dcdcdc;
	/* border-bottom: 1px solid #ddd; */
	margin-bottom: 10px;
	border-radius: 3px;
}
  .navbar-inverse .navbar-nav > li > a {
	color: #fcfcfc;
}
  .navbar-inverse .navbar-nav > li > a:hover, .navbar-inverse .navbar-nav > li > a:focus {
	color: #3daee9;
	background-color: transparent;
}
.author-name {
	color: #4d4d4d;
}
  .badge-important {
	background-color: #da4453;
}
  .navbar.navbar-fixed-top {
	border-bottom: none;
	/*box-shadow: 1px 0 2px #4d4d4d;*/
}
	#main_stream .stream_element > .media a.author-name {
	color: #4d4d4d;
}
.tag {
	display: inline-block;
	font-weight:normal;
}
	#hovercard h4 a {
	color: #4d4d4d;
	font-weight: bold !important;
}
	body #main_stream .stream_element {
	border: 1px solid #dcdcdc;
	border-bottom: 1px solid #ddd;
	margin-bottom: 10px;
	border-radius: 3px;
	box-shadow: 0 2px 2px #ccc;
}
	.info-bar {
	border-top: 1px solid #ddd;
	margin-top: 20px;
	padding: 10px 20px 0;
	box-shadow: 0 2px 2px #ccc;
}
.left-navbar {
	padding: 0;
	padding-bottom: 0px;
}
		body .profile-sidebar, body.page-tags .left-navbar-fixed-background, body .left-navbar {
	/*border-right: 1px solid #ddd;*/
	/*box-shadow: 0 2px 2px #ccc;*/
}
	h3, .h3 {
	font-size: 30px;
}
	.stream_container .stream-title {
	margin: 14px 0;
	margin-bottom: 25px;
}
	.container-fluid {
	max-width: 1200px;
}
	.container-fluid {
	margin-right: auto;
	margin-left: auto;
	padding-left: 15px;
	padding-right: 15px;
}
	.sidebar, .framed-content {
	background-color: #fcfcfc;
	border: none;
	border-top: none;
	border-right: none;
	box-shadow: none;
	/*box-shadow: 0 2px 2px #ccc;*/
}
	body .profile-sidebar, body.page-tags .left-navbar-fixed-background, body .left-navbar {
	border-right: none;
}
	.navbar.navbar-fixed-top .nav-badge {
	color: #fcfcfc;
}
	
	/* TIGHTER LEFT NAV BAR */
	.col-md-3 {
	width: 180px;
}
	
	/*COLOR LEFT NAV BAR*/
	.left-navbar .hoverable:hover, .left-navbar .hoverable:hover a, .left-navbar .hoverable:hover [class^="entypo"] {
	/*background-color: #3daee9;
	color: #fff;*/
	margin-left: -3px;
	
	color: #777;
	background: #fcfcfc;
	border-left: solid 3px #3daee9;
}
	
  .left-navbar .all-aspects .hoverable.selected, .left-navbar .followed-tags-sidebar .hoverable.selected, .left-navbar .selected {
	color: #777;
	background: #fcfcfc;
	border-left: solid 3px #3daee9;
	margin-left: -3px;
}
	
	.left-navbar .all-aspects .hoverable.selected, .left-navbar .followed-tags-sidebar .hoverable.selected, .left-navbar .hoverable > .selected {
	color: #fcfcfc;
	background: #3daee9;
	border-left: solid 3px #3daee9;
	
}
	.left-navbar .all-aspects .hoverable.selected, .left-navbar .followed-tags-sidebar .hoverable.selected, .left-navbar .selected > .hoverable {
	color: #777;
	background: #fcfcfc;
}
	
	/* UPPERCASE */
	.navbar.navbar-fixed-top .navbar-nav:not(.nav-badges) > li > a {
	text-transform: uppercase;
}
	
	/* BRAND */
	.navbar-brand {
	padding: 14px 15px;
}
	
	.navbar.navbar-fixed-top .nav-badge {
	margin-top: -3px;
}
	
	.navbar.navbar-fixed-top #user_menu .dropdown-toggle {
	text-transform: none;
	color: #f2f2f2;
}
	
	/* SEARCHBOX */
	
	.form-control:focus {
		border-color: #4daee9;
	}
	.input-sm, .input-group-sm > .form-control, .input-group-sm > .input-group-addon, .input-group-sm > .input-group-btn > .btn {
	border-radius: 1px;
}
	
	/* NEW POST */
	
	.publisher form #publisher_textarea_wrapper {
	border-radius: 2px;
	border: 1px solid #4daee9;
}
	.btn {
	border-radius: 3px;
}
	
	.btn-primary {
	color: #fff;
	background-color: #0088e6;
	border-color: #0079cc;
}
	
	.btn-primary.disabled:hover, .btn-primary.disabled:focus, .btn-primary.disabled.focus, .btn-primary[disabled]:hover, .btn-primary[disabled]:focus, .btn-primary.focus[disabled], fieldset[disabled] .btn-primary:hover, fieldset[disabled] .btn-primary:focus, fieldset[disabled] .btn-primary.focus {
	background-color: #1d99f3;
	border-color: #3daee9;
}
	
	.main-stream-publisher .avatar {
	box-shadow: 0 1px 2px #ccc;
}
	
}
