css:
@-moz-document domain("trello.com") {
/*--------------------------------------------------------------------------------
Trello dark theme with light cards+ by Sami Shtotland (sami.shtotlandATgmail.com)
---------------------------------------------------------------------------------*/

/* font size (cards etc) */

html,
body.body-board-view,
input,
select,
textarea,
button {
    font: 13px 'Tahoma', sans-serif!important;
    line-height: 17px!important;
    font-weight: 300!important;
}
/**************** HEADER *****************/
/* header bar */

#header {
    background: #2B2B2B !important;
}
/* header buttons */

.header-btn.header-notifications,
.header-btn.header-boards,
.header-btn.header-search,
.header-btn.header-login,
.header-btn.header-signup {
    background: #6B6B6B !important;
}
.header-btn:hover {
    background: #868686 !important;
}
/* header search box */

#header-search input[type="text"] {
    background: #6B6B6B !important;
}
#header-search input[type="text"]:focus {
    background: #f2f2f2 !important;
}
/* board title */

#board-header .board-name {
    font-weight: normal !important;
}
.board-header {
    background: #2B2B2B!important;
}
/* "Show menu" link */

.board-header-btn-text {
    text-decoration: none;
}
/**************** BOARD*****************/
/* board background */

#board {
    background: #2B2B2B;
    padding-top: 10px;
}
/**************** LISTS *****************/

.list-header-num-cards {
    padding-top: 12px;
    color: #aaa;
    display: inline !important
}
.list-header-name {
    font-size: 15px!important;
    color: #fff;
}
.list-header-menu-icon {
    top: 3px;
}
.list {
    background-color: #2B2B2B;
}
.list-card {
    border: 1px solid #ddd;
    border-width: 0 1px 1px 0;
    margin-bottom: 2px;
}
.list-card:last-child {}
.list-card.selected,
.list-card.active-card {
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.3);
    background: white;
}
.list-card-operation {
    background: #f2f2f2;
}
.list-wrapper.mod-add.is-idle {
    width: auto;
    background: #f2f2f2!important;
}
.list-wrapper.mod-add.is-idle:hover {
    width: auto;
    background: #888!important;
    /* new card link */
}
.open-card-composer {
    font-size: 10px;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin: 0 8px;
    padding: 8px 8px;
}
/* hover color of 'add new card' */

.open-card-composer:hover {
    text-decoration: none;
    background-color: #f2f2f2;
    border-radius: 3px;
}
/* new list link */

.list-wrapper.mod-add.is-idle {
    background: #f2f2f2!important;
}
.list-wrapper.mod-add .placeholder {
    background: none !important;
    box-shadow: none !important;
    text-transform: uppercase !important;
    font-size: 10px !important;
    letter-spacing: 1px;
    color: #888 !important;
}
.list-wrapper.mod-add:hover .placeholder {
    color: white!important;
    background: #888!important;
}
/**************** CARD CONTENT (back side) *****************/
/* popup window */

.window {
    background-color: #f7f7f7;
}
/**************** SIDE MENU right, settings *****************/

.board-menu {
    background-color: #f7f7f7;
}
/**************** SIDE MENU left, boards list *****************/

.sidebar-boards-list a {
    height: 24px;
}
a .sidebar-section-header-title {
    text-decoration: none;
}
.sidebar-boards-list .thumbnail {
    height: 24px;
}
.sidebar-boards-list .thumbnail {
    height: 24px;
    width: 24px;
}
.sidebar-boards-list .details {
    margin-left: 24px;
    padding: 3px 10px;
}
.board-list .board-list-item-icon-star,
.sidebar-boards-list .board-list-item-icon-star {
    padding: 3px 9px;
}
.boards-sidebar-item-link-thumbnail {
    height: auto;
}
.board-list .unread-indicator,
.sidebar-boards-list .unread-indicator {
    top: 6px;
}
.boards-sidebar-item-link-text-sub-name {
    display: none;
}
.compact-board-tile-link-thumbnail {
    height: auto;
}
/* Links at the bottom */

.boards-drawer .quiet-button {
    text-decoration: none;
}
.boards-drawer .empty,
.boards-drawer .quiet-button {
    margin: 0px 4px 0 0;
    padding: 3px 9pt;
}
/* Star */

.compact-board-tile-link-options-remove,
.compact-board-tile-link-options-star {
    margin: 2px 0
}
/**************** BADGES *****************/
/* Makes label text visible on cards */
  .list-card-labels .card-label {
    display: inline-block;
    height: auto;
    width: auto;
    padding: 0 4px;
    font-size: 11px;
    line-height: 18px;
    font-weight: normal;
    text-shadow: none;
  }

  /* Fixes lineheight issues for badges */
  .badge {
    border-radius: 3px;
    font-size: 11px;
    line-height: 18px;
}


/**************** PROJECT *****************/
.project {
    float: right;
    font-size: 11px;
    line-height: 16px;
    text-align: right;
    letter-spacing: 1px;
    background-color: #4d4d4d!important;
    color: white !important;
    background-position: 3px 3px !important;
    padding: 0px 4px !important;
}
}
