css:
@-moz-document domain("blog.cloudflare.com") {
#header{
    background-color: #bdbdbd !important;
    color: black !important;
}
body{
    background-color: #222222 !important;
    color: gray !important;
}
body h1{
    color: #d0d0d0 !important;
}
body h2{
    color: #d0d0d0 !important;
}
body input{
    background-color: #d0d0d0 !important;
    color: black !important;
}
body > div.wrapper.reverse-sidebar > section > article > div.post-content > p code{
    background-color: #424242 !important;
    border-color: white !important;
    color: gray !important;
}
body > div.wrapper.reverse-sidebar > section.primary-content > article > header.post-header > h2 > a{
    color: #bdbdbd !important;
}
ul.menu-sidebar > li{
    background-color: #bdbdbd !important;
    color: black !important;
}
body > div.wrapper.reverse-sidebar > section > section.clearfix > a{
    color: white !important;
}
body > div.wrapper.reverse-sidebar > section > article > aside.section.learn-more{
    background-color: #424242 !important;
    color: #d0d0d0 !important;
}
body > div.wrapper.reverse-sidebar > aside > div.modunit{
    background-color: #bdbdbd !important;
    color: black !important;
}
#footer{
    background-color: #bdbdbd !important;
    color: gray !important;
}
}
