css:
@-moz-document domain("cinesift.com") {
#sb-site {
    background-color: #636363;
}
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #ffffff;
    background-color: #fff;
}
.film {
    /* border-radius: 25px; */
    
    background: #2f2f2f;
    margin: 20px auto 20px auto;
    height: auto;
    overflow: auto;
    padding: 15px;
    border-radius: 5px;
    /* padding: 20px; */
    /* min-height: 300px; */
    
    -webkit-overflow-scrolling: touch;
}
#filter,
.film {
    border: 1px solid #2f2f2f;
}
.btn-default {
    color: #000;
    background-color: #44cabc;
    border-color: #44cabc;
}
element.style {
    color: #cc00cc;
}
:hov .cls element.style {
    color: #3f8edc;
}
a {
    color: #ffd27d;
    text-decoration: none;
}
element.style {
    color: #08a208;
}
#filter {
    color: #ffffff;
}
#filter {
    padding-top: 20px;
    padding-bottom: 20px;
    position: relative;
    margin-bottom: 10px;
    z-index: 99;
    background-color: #2f2f2f;
}
rzslider span.rz-bubble {
    color: white;
}
.loggin {
    position: fixed;
    top: 10px;
    right: 20px;
    cursor: pointer;
    color: #32f1e9;
}
#resultOptions select {
    background-color: #339aff;
    color: #131313;
}
element.style {
    color: #d000d0;
}
}
