css:
@-moz-document domain("urbandictionary.com") {

    * {font-family: calibri !important; padding-top: 0px!important; padding-left: 0px!important;}
    a {color: rgb(255, 222, 0) !important; text-decoration: none !important}
    a:hover, .pagination>a:hover {text-decoration: underline !important; background: rgb(25, 25, 25) !important}
    .container {margin-left: 15px;}
    .row {margin-left: 20px;}
    .small-10 {margin-left: -250px; padding-bottom: 10px; color: rgb(70, 70, 70);}
    .autocomplete {box-shadow: none; border: 1px solid rgb(70, 70, 70); transition: none;}
    .meaning, .example {color: rgb(240, 240, 240) !important;}
    .form-control:focus, .def-panel {box-shadow: none; border-color: rgb(80, 80, 80);}
    .pagination > .current > a{background:none !important;}
    .word-list-panel {display: none;}
    .def-header, .meaning, .example {margin-top: 10em ;margin-left: 0em; margin-right: auto; width: 24em}
    
    div#term
    {
        display: inline !important;
    }
    
    #dark-top, #left-column, #nearby_titles, .col-md-2, .col-md-4, .app-banner a, .modal, div.columns.small-6.large-2.large-push-7.text-right.main-actions,
    .btn, .look_up, .tt-dropdown-menu, .full-width-nav, #logo, .large, .thumb.up, .thumb.down, .right-link, .buy,
    #navbar1,
    #nav2,
    .mission,
    #copyright,
    .share, .author, .contributor, .bar, .footer, .box .ribbon, .box .right-links, .counts,
    .tags, .random_button, .dfp, .twitter-timeline, .text-center, .icon-ud_speaker, .sticky, .three-columns > div:nth-child(2) > div:nth-child(2), .tags-panel, .ad-panel, .def-panel .ribbon,
    
    #urban-top-bar, .large-4, .def-footer, #dark_top
    {
        display: none !important;
    }
    
    .def-store {
        display: none !important;
    }
    
    body, .home_controller, .three-columns, .box, .autocomplete, .def-panel
    {
        background: rgb(45, 45, 45) !important;
        color: black;
    }
    
    .small-caps {
        visibility: hidden;
    }
    
    .carousel-image {
        visibility: hidden;
    }
    
    .slick-track {
        visibility: hidden;
    }
    .slick-list {
        visibility: hidden;
    }
    .slick-initialized {
        visibility: hidden;
    }
    
    .pagination-centered {
        margin: auto !important;
        position: relative !important;
        left: -130px;
        top: -30px;
    }
    
    .row {
        margin: auto;
        margin-top: 70pt;
    }
    
    .columns {
        margin-left: 200pt;
    }
    
    .play-sound {
        position: relative;
        top: 3pt;
    }
    
    .def-header {
        margin-top: 0pt;
    }
    
    div.def-panel:nth-child(5) {
        height: auto !important;
    }
    
    .def-panel + .small-caps {
        margin: 0;
        padding: 0;
    }
    
    div.def-panel:nth-child(1) > div:nth-child(2){
        box-sizing: content-box;
    }
    
    div.def-panel:nth-child(n) > div:nth-child(2) > a:nth-child(1)
    {
        font-size: 27pt !important;
    }
    
    div.def-panel:nth-child(1) > div:nth-child(3) {
        font-size: 15pt !important;
        max-width: 384px;
        border-top: 5pt solid #555555;
        padding-top: 10pt !important;
    }
    
    div.def-panel:nth-child(1) > div:nth-child(4) {
        font-size: 15pt !important;
        max-width: 384px;
        border-bottom: 5pt solid #555555;
        padding-bottom: 10pt;
    }
    
    .word {
        color: #FFDE00 !important;
        font-size: 60pt !important;
        margin-left: 10pt;
    }
    
    p {
        color: rgb(240, 240, 240) !important;
    }
    
    #image-set-carousel {
        height: 0 !important;
    }
    
    div.def-panel:nth-child(5) > div:nth-child(1)
    {
        height: 0 !important;
    }
}
