css:
@-moz-document domain("arstechnica.com"),domain("arstechnica.co.uk") {

  body.dark .listing-latest article header h2 a {color: #d5d5d5;}
 
  html {max-width:960px; margin:auto;}
  body {font-size:1em;}

  header.site-header.scrolled-up {position:relative !important;}
 
  .article-single figure.intro-image {margin-left:0px; width: 100%  !important;}
  .article-single figure.full  {margin-left:0px; width:100% !important;}
  .article-single figure.full-width {margin-left:0px; width:100% !important;}
  .article-single figure.video {margin-left:0px; width:100% !important;}  
  .article-single figure.left  {margin-left:0px;}
  .article-single figure.right {margin-left:0px;}
 
  .article-single .article-header {margin-left:0px; width:100% !important;}
  .article-single .gallery  {margin-left:0px; width:100% !important;}  
  .article-single .column-wrapper .xrail {width:0px;}
  .article-single .column-wrapper .left-column {width:100%; margin-left:0px;}
 

  .quotecontent {background-color:#080808; font-size:16px;}

  #promoted-comments {width:100%; max-width: 100%;}
 
  .comments-row {margin-left:0px; width:100% !important;}  
  .comments-row #comments-area #comments > li > header .author > a {color: #d5d5d5;}
  .comments-row #comments-area #comments > li > .body .quotecontent {border-left:2px solid #ccc;}
  .comments-row #comments-area #comments > li > .body .quotetitle {border-left:2px solid #ccc;}
  .comments-row #comments-area #comments > li > .body {font-size:16px;}

  body.dark .prev-next-links {font-size:1.5em; margin:1em 0px;}
 
  .site-footer .nav-footer {font-size:16px;}
 
}
 
@-moz-document url-prefix('http://arstechnica.com/civis/'),url-prefix('http://arstechnica.co.uk/civis/') {

  .quotecontent {background-color:#f5f5f5; font-size:12px;}
 
}
