css:
@-moz-document url-prefix(https://www.facebook.com), url-prefix(https://apps.facebook.com) {
    
/*Banh rộng */
#contentCol {min-width: 900px !important; margin-left: 20px !important} 
#contentArea {min-width: 640px !important}
._2w3 {min-width : 100% !important}

#leftCol {z-index: 2 !important}
._3t54 * , .UFILikeLink {z-index: 1 !important}    
    
body:not(.pagesTimelineLayout) #pagelet_timeline_main_column {min-width: 932px !Important} 
.fbTimelineUnit.fbTimelineTwoColumn.clearfix, ._502f{min-width: 400px !important}

/* Font chữ */    
.userContent {font-family: Verdana; font-size: 14px !important} /* Text thường */
.mtm *, .uiList  {font-size: 13px !important; font-family: Verdana} /* Text chú thích */
.UFIList {font-size: 14.2px !Important} /* Text Comment */

/* BO tròn Avatar Chat */
._55lq:after, ._4tdw:after {display:none !important}
._55lt img, ._4tdw img {border-radius: 50% !important} 
  
/* Hình nền */
body  {background: url(http://i.imgur.com/vwOY8j9.jpg) no-repeat fixed !important}
.uiSideNav {margin-left: 40px !important}
 
/* Thu gọn cột trái */    
 body:not(.SettingsPage) #leftCol {
position: fixed !important; left: 0; top: 0;
height: 100%; overflow-y: scroll; resize: horizontal;
width: 200px !important;
z-index: 600; 
background: rgba(255,255,255,0.9) !important;
border-right: 0 solid #385998;
transition: left .2s linear
}
 body:not(.SettingsPage) #leftCol:not(:hover) {
background: transparent;
left: -200px !important;
border-right-width: 2mm
}

/* Autohide Footer setting */
#pageFooter:not(:hover) {opacity: 0 !important}

/* Bỏ side ad Timeline */
#pagelet_side_ads {display:none !important}

}
