css:
@-moz-document domain('furvilla.com') {
body {
    background: #424242 url("http://i.imgur.com/5phWJDT.png") no-repeat 50% 0;
}
.content {
    background-image: url(http://i.imgur.com/sKnCsrE.png);
    background: #505050;
    background-color: rgba(0, 0, 0, 0.6);
    background-repeat: repeat;
    background-position: top center;
    color: #672525!important;
}
.content h1 {
    color: #37317e;
}
.user-panel {
    background: #5f5f5f!important;
    color: #fff;
}
header nav {
    background-image: linear-gradient(#424242, #000);
}
header nav a {
    color: #ababab !important;
}
.user-panel a {
    color: #000!important;
}
.fa {
    color: #000!important;
}
.content h1 small {
    color: #8e8e8e;
}
.content h1 {
    color: #ababab;
}
.user-info h4 {
    color: #000000;
}
.widget-content {
    opacity: .7;
}
.widget-header h3 {
    background-image: linear-gradient(#a0a0a0, #4e4e4e);
    color: #000;
}
.widget-header {
    border-bottom: 3px solid #000000;
}
.toolbar-title h2,
.toolbar-title h3 {
    color: #ababab;
}
.btn {
    background-image: linear-gradient(#a0a0a0, #4e4e4e);
}
a {
    color: #2b266d!important;
}
#logo a {
    background: url("http://i.imgur.com/B1rHwcu.png") no-repeat;
}
.btn-link {
    color: #c1c1c1 !important;
}
.search {
    background: #3e3e3e;
}
.help-block {
    color: #000000;
}
.sod_select:hover,
.sod_select.open,
.sod_select.focus {
    border-color: #000000!important;
}
strong.blue {
    color: #1b1b1b;
}
.inventory-item-info,
.item-block {
    border: 1px solid #000;
    background: #5f5f5f;
}
.inventory-item-info .counter,
.item-block .counter {
    border-color: #000000;
    color: #191919;
}
.pagination > li > a,
.pagination > li > span {
    color: #000000;
    background-color: #b3b3b3;
    border: 1px solid #848484;
}
.pagination > .active > a,
.pagination > .active > a:hover,
.pagination > .active > a:focus,
.pagination > .active > span,
.pagination > .active > span:hover,
.pagination > .active > span:focus {
    color: #fff;
    background-color: #2f2f2f;
    border-color: #2f2f2f;
}
.fur-residents-item-info {
    border: 1px solid #6f6f6f;
    background: #a7a7a7;
}
.thread-user-info {
    background: #424242;
}
.textarea {
    background-color: #c0c0c0;
}
.content .left-column {
    float: left;
    width: 716px;
    border-radius: 10px;
}
.table thead,
.table tr:hover .table thead {
    background: #353535 !important;
}
.nav-pills > li.active > a,
.nav-pills > li.active > a:hover,
.nav-pills > li.active > a:focus {
    color: #fff;
    background-color: #444444;
}
.modal-content {
    background-color: #b5b5b5;
}
.animal-item-info {
    background: #c2c2c2;
}
.villager-name {
      text-shadow:
   -1px -1px 0 #fff,  
    1px -1px 0 #fff,
    -1px 1px 0 #fff,
     1px 1px 0 #fff;
}
.explore-item {
background: #c7c7c7;
background-image: url(http://i.imgur.com/FtBwbB3.png);
border: #c7c7c7;
}
.warrior-view-sword .color-thief-bg, .warrior-view-shield .color-thief-bg {
border: #8c8c8c;
background: #8c8c8c;
}
.warrior-view-sword:before, .warrior-view-shield:before {
background: #8c8c8c;
}
.villager-data-wrapper {
background: none;
}
.float-left {
color: #000!important;
}
}
