css:
@-moz-document domain('imgur.com'), url-prefix('http://imgur.com') {
/* Make vote buttons permanently visible and resize them */
.comment-votes {
    opacity: 1;
    left: -39px;
    top: 0px;
    padding: 0;
}

.comment-vote {
    font-size: 12px;
}

.comment-votes-wrapper {
    background: #353535;
}

/* Reduce corner radius */

#topbar li.account .account-user-name {
    border-radius: 2px;
}

/* Reply Button */

.comment-create-reply {
    width: 20px;
    height: 20px;
}

#div-gpt-ad-spotlight {
    display: none;
}

.caption .comment-create-reply {
    display: block !important;
    -webkit-justify-content: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
}


.comment-create-reply-wrapper > div {
    text-indent: 150%;
    white-space: nowrap;
    overflow: hidden;
    font-size: 0.0001em;
}

.comment-create-reply-wrapper > div.icon-reply {
    text-indent: 0;
    white-space: nowrap;
    font-size: 10pt;
    margin: 7px 5px 0 0;
    color: #636363;
    text-shadow: none !important;
}

.comment-create-reply-wrapper > div.icon-reply:hover {
	color: #f2f2f2;
}

.comment-create-reply-wrapper > div.icon-reply:before {
    font-style: normal!important;
    font-weight: 400!important;
    font-variant: normal!important;
    text-transform: none!important;
    speak: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

a.comment-username {
    vertical-align: inherit;
}

/* Reduce corner radius, tighten wasted space, and get rid of gradient thing + speech bubble */

#captions .usertext {
    background: #232425;
}

#captions .usertext:before {
	display:none;
}

#captions .usertext {
    border-radius: 3px 3px;
}

.comment[data-level="0"]>.caption .caption-gradient {
    left: 16px;
}

.caption-gradient-wrapper {
	display: none;
}

.comment-create-reply {
    background: none;
}


#captions .usertext {
    line-height: 11px;
}

#topbar a.topbar-icon:hover, #topbar span.topbar-icon:hover {
    background:none;
}

form.caption-create {
    background: #232425;
    border: 2px solid #36383c
}

#captions .comment[data-level="0"]>.caption .usertext {
    border-radius: 3px 3px;
}

/* Darken light gray theme across the board */

#content, .panel {
    background-color: #303131;
}

.post-action {
    background-color: #303131;
}

.post-image-meta {
    background: #303131 !important;
}

#topbar {
    background-color: #303131;
}

.post-header {
	background-color: #303131;
}

.posts {
	background-color: #303131;
}

#outside-tagging {
	background-color: #303131;
}

#outside-tagging-showhide {
	background-color: #303131;
}
    
#side-gallery .new-panel-header {
	background-color: #303131;
}

.layout-options {
	background-color: #303131;
}

.js-post-truncated {
	background-color: #303131;
}

.layout-options {
    background-color: #141518;
}

.account-upload-button {
    background: #39c442;
}

.account-upload-button:hover {
    background: #39c442;
}
a.post-action-tags-tag {
    background: #3f3f3f !important;
    border-radius: 5px;
    padding: 0 5px;
	height: 30px;
	line-height: 25px;
    margin-top: 10px;
    font-size: 13px;
    height: 17px;
	padding: 0px 2px 4px 2px !important;
    line-height: 17px;
    margin-top: 4px;
    padding: 2px 4px;
	border: solid 1px #575757;
    text-shadow: none;
    font-weight: 500;
    font-size: 15px;
}

.post-tag-link {
    line-height: 0;
    font-weight: 500;
    font-size: 13px;
    text-shadow: none;
}

a.post-action-tags {
    margin-top: 3px;
}

a.post-action-tags-tag .post-tag-link {
    font-size: 13px;
    font-weight: 500;
    line-height: 1;
    text-shadow: none;
}

div.post-action-tags {
    float: right;
	margin-top: -40px;
    width: 462px;
}

/*Fix comment character and comment cancel button overlap*/
form.caption-create .counter {
    right: 34px;
    top: -9px;
}


/* Visible Topbar */
#topbar {
	top: 0 !important;
  	bottom: 5 !important;
  	position: fixed !important;
  	padding:0px 0px 0px 0px !important;
  	margin-bottom: 0px !important;
}

#cta-container-placeholder {
    padding-top: 52px !important;
}

#content {
    margin-top: 60px !important;
    padding: 10px;
    border-radius: 3px;
}

#inside {
    margin: 60px auto !important;
}

#edit-sentence, .sentence-sorting {
    font-size: 14px;
    line-height: 18px;
    padding: 10px 0 20px;
}

.sentence-sorting .combobox .options {
    margin-top: 32px;
}

.sentence-sorting .combobox-current {
    font-size: 16px;
    margin-top: -1px;
}

.sentence-sorting #section .options .item, .sentence-sorting #sort .item, .sentence-sorting #window .item {
    line-height: 12px;
}

.sentence-sorting .item.bold a {
    font-size: 14px;
}

.sentence-sorting .selection {
    line-height: 15px;
    margin: 0;
    font-size: 14px;
    position: relative;
    padding: 0 7px 0 0;
    overflow: visible;
    border-bottom: 2px solid #39C442;
    border-radius: 0;
    background-color: transparent;
    border-top: 0;
    color: #fff;
}

.gifpicker-header {
    display: none !important;
}

#user-gallery-message {
    display: none;
}

#section.combobox .pulse-dot {
    display: none;
}

.sentence-sorting .sort-options {
    top: 0px;
}

/*Hide Native Advertising*/
a[href='/t/mikes happy place']{display:none;}
div.comment-gif-container {display:none;}

@media only screen and (max-width: 1200px){
#secondary-nav {
    margin-right: -15px;
    padding-left: 146px;
    width: 33%;
}
}

@media only screen and (max-width: 830px){
#secondary-nav {
    float:left;
    margin-right: -15px;
    padding-left: 147px;
    width: 33%;
}
}
}
