css:
@-moz-document domain("t.people.com.cn"){

/* 
Author: Wayzlin
Version: 1.0.0
Update Date: 2016/07/20
*/


.siteMsg,
h2.skin_color_04,
#fixBlkUser,
#fixBlk,
#fixBlkGroup,
.wbp_footer,
.recom_single,
.comment_fast,
.wbp_tips_v2,
#recFocusBox,
.pbl_top,
#weiboOthersTag,
.lineH_01,
.wbPart_arr,
div.blk_01,
#fixBlk,
.tpb_menu,
.line_01 {
    display:none !important;
}
.wbp_main_c2_a .m_cont,
.blk_pscard01,
.wbp_main_c2_b {
    border-color: rgba(0, 0, 0, 0.3) !important;
    background: rgba(0, 0, 0, 0.4) !important;
}
.WBM_list .list_forward,
div.list_open_media,
.WBM_list .list_del,
.WBM_comment {
    border-color: rgba(0, 0, 0, 0.2) !important;
    border: rgba(0, 0, 0, 0.2) !important;
    background-color: rgba(0, 0, 0, 0.2) !important;
    background: rgba(0, 0, 0, 0.2) !important;
}
.WBM_list .list_item:hover {
    background:#000 !important;
}
div.list_text,
div.list_open_media a,
div.m_right a:not([href="javascript:void(0)"]),
div.m_right p,
div.m_right i,
a.list_name,
#weiboTypeBox a,
div.pbl_bot.clearfix a,
div.blk_pscard01.pb_boder01.clearfix p,
div.blk_pscard01.pb_boder01.clearfix h2,
div.blk_pscard01.pb_boder01.clearfix span,
div.blk_pscard01.pb_boder01.clearfix a,
div.lb_tags,
.nav_his a,
div.nav_label.clearfix h6,
div.pbl_bot.clearfix,
div.comment_list div,
#feedListPage span {
    color: #FFF !important;
}
.wbp_listnav .ln_type a.selected,
.wbp_listnav .ln_type a:hover {
    background: #eb7350 !important;
}
div.list_time,
div.list_time a,
div.list_func,
div.list_func a {
    color: #fffdd8 !important;
}
div.list_text.skin_color_01 a{
    color: #eb7350 !important;
}
#weiboTypeBox{
    color: #c8f0ff !important;
}
.WBM_topbar .tpd_fixed {
    background: url(http://i0.peopleurl.cn/microblog-v3/images/skin/skin_02/topbar_bg01.png) repeat-x 0 0 !important;
}

}
@-moz-document url-prefix("http://t.people.com.cn/search"){

body {
    background: #000 !important;
}
.blk_result {
    background: rgba(0, 0, 0, 0) !important;
}

}
