css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
   url-prefix("http://www.cnrtl.fr/definition")
  ,url-prefix("http://www.cnrtl.fr/synonymie")
  ,url-prefix("http://www.cnrtl.fr/etymologie")
  ,url-prefix("http://www.cnrtl.fr/morphologie")
  ,url-prefix("http://www.cnrtl.fr/portailindex/MORF")
  ,url-prefix("http://www.cnrtl.fr/portailindex/DEFI")
  ,url-prefix("http://www.cnrtl.fr/portailindex/SYNO")
  ,url-prefix("http://www.cnrtl.fr/portailindex/ETYM")
{
  
  /* General */
  
  #nav, #header, #menubox, #footer, .font_change, tbody td h2
  {
    display:none;
  }
  
  body, div, a, .box h2
  {
    color : #111111 !important;
    background-color : #FFFFFF !important;
    font-family: helvetica !important;
  }
  
  .box, .tab_box, #contentbox
  {
    border : 0px;
  }
  
  .center img
  {
    display : initial !important;
  }
  
  body
  {
    background-color : #111111 !important;
  }
  
  #wrap
  {
    width : 100% !important;
  }
  
  /* Navigation Menu */
  
  .tab_box 
  {
    display : flex !important;
    flex-wrap : wrap;
    justify-content: center;
  }
  .tab_box li
  {
    margin : 5px;
  }
  .tab_box a:hover
  {
    border-bottom: 2px solid #D9D7D3;
    background: #D9D7D3;
  }
  .tab_box .active
  {
    font-weight: initial;
    border-bottom: 2px solid #D9D7D3;
    background: #D7E5FF;
  }
  
  /* Search Area */
  
  #reqform table
  {
    min-width : 50px !important;
    max-width : 200px !important;
    /*width:50% !important;*/
    margin : 0 auto !important;
  }

  #reqform tr
  {
    display: flex;
    flex-direction: column;
  }
  
  #reqform td
  {
    display : flex;
    justify-content: center;
  }
  
  input
  {
    color : #D9D7D3  !important;
    background-color : #111111 !important;
    
    font-size: 15px !important;
    width : 100% !important;
    display : block !important;
    border-radius: 10px !important;
  }
  
  #query
  {
    height : 25px !important;
    padding: 6px !important;
  }
  
  
  /* Result */
  
  #vtoolbar ul
  {
    display: flex;
    padding-bottom : 15px !important;
  }
  
  #vtoolbar li
  {
    border : 0px !important;
    width : 100% !important;
  }
  
  #vtoolbar span
  {
    font-size: 13pt;
  }
  
  #vtoolbar #vitemselected a
  {
    font-size: 11pt !important;
    padding-bottom : 2px;
  }
  
  #vitemselected
  {
    width : 100% !important;    
    list-style-type: none !important;
    border : 0px !important;
  }
  
  #vtoolbar #vitemselected a 
  {
    background: inherit !important;
    font-size: 20px;
  }
}
