css:
/*Authors: EGOTISTICALGIRAFFE, NoAsBigAsMediumJockButBiggerThanWeeJockJock, Xsiah*/
@-moz-document domain("imgur.com") {

}

@-moz-document regexp("https?://((?!community)[a-z]*).?imgur.com((?!/adm).*).*") {
body, html {
    font-family: Arial,sans-serif;
    color: #ddddd1;
    height: 100%;
    background-color: #121211;
    margin: 0;
    font-size: 14px;
}

#div-gpt-ad-spotlight {
    display: none;
}
.post-action-tags{
    text-align: right;
    margin-left: 50%;
}
a.post-action-tags-tag{
    background: #494a4f!important;
    color: #fff;
    height: auto;
    line-height: 0;
    padding: 0 14px;
    margin: 4px;
}
a.post-action-tags-tag .post-tag-link{
    text-shadow: none;
    font-weight: 500;
}
#comments-container.comments-initialized #comments #captions:after{
    background-color: transparent!important;
}
.left{
    box-sizing: border-box;
}
.rules{
    height: inherit;
    line-height: 36px;
}
a.post-loadall {
    background: transparent!important;
    color: #5675c8;
}

.sg-item.grid, 
.sg-item-title{
background-color: #2B2B2B!important;
}
.caption-create:after{
    content: "";
  display: table;
  clear: both;
}
.comment-op, .right .panel.menu .button.selected, .right .panel.menu .button.selected h2, .green{
	color:#89c031!important;
}
.error, .error strong, .new, .red{
	color:#e6413f!important;
}

/*better blue links*/
.comment-reply-icon,
.post-image-description a,
.linkified a,
.via a{
color:#4E76C9;
}
.comment-reply-icon{
border-color:#4E76C9;
}
/*better grey boxes*/
.js-post-truncated,
#topbar,
.panel,
.post-header,
.post-image-description,
.post-description,
.post-image-meta,
.post-action,
#post-jump-comments, 
#post-jump-explore, 
#post-jump-top,
#comments-container,
.post-container,
.navPrev{
    background: #2B2B2B;
}


#topbar .user-dropdown-container a{
	color: #484848!important;
}

#topbar #createDropdown a{
	color: #484848!important;
}
#topbar .link-menu a{
	color: #484848!important;
}

#topbar a.topbar-icon:hover {
    background: transparent;
}
#topbar span.topbar-icon:hover {
    background: transparent;
}
.post-container {
    width: 728px;
    border-radius: 5px;
}
#comments-container {
    width: 728px;
    padding: 20px;
    
    box-sizing: border-box;
    border-radius: 5px;
}
.comment-votes-wrapper {
    background: transparent;
    border-radius: 2px;
}
.comment-votes {
    opacity: 1;
    left: -30px;
    padding: 3px 0px 0px 0px;
}
.comment-vote {
    background: #484848;
    padding-top: 2px!important;
    padding-bottom: 2px!important;
    padding-left: 2px!important;
    padding-right: 2px!important;
    width: 23px;
    height: 23px;
    font-size: 14px;
    line-height: 23px;
    text-align: center;
    border-radius: 2px;
    box-shadow: 1px 1px 1px rgba(0, 0, 0, 0.36);
}
.children {
    padding-left: 0px;
}
.children form.caption-create::before {
    display: none;
}
form.caption-create {
    border: 0px;
    background-color: #1c1c1c !important;
        padding: 0;
}
.comment .caption-create .icon-x{
    position: absolute;
    bottom: 15px;
    left: 15px;
    background: #535353;
    border-radius: 4px;
    width: inherit;
    margin-left: 10px;
    border-width: 2px;
    color: #ddddd1;
    height: inherit;
    outline: 0;
    -webkit-user-select: none;
    user-select: none;
    cursor: pointer;
    display: inline-block;
    padding: 10px 25px;
    border: none;
    text-decoration: none;
    box-sizing: border-box;
    margin: 0em 0em 0em 0em;
    right: auto;
    top: auto;
}
/*.comment .caption-create .icon-x:before{content: "X";
    font: 13.3333px Arial!important;
    font-weight: 700!important;
    font-size: 14px!important;
}*/
form.caption-create textarea{
    font-family: inherit;
    border: 1px solid transparent;
    border-top: 1px solid #171717;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.1);
    border-bottom: 0;
    background-color: #1c1c1c;
    outline: 0 none;
    box-sizing: border-box;
    border-radius: 5px;
    padding: 5px 10px;
    font-size: .9em;
    line-height: 1.5em;
    resize: none;
    color: #f2f2f2;
    margin-bottom: 10px;
    letter-spacing: .02em;
    height: 33px;
    display: block;
    width: 100%;
 
    margin: 7px 0 5px;
}
/*form.caption-create textarea:focus{
height: 80px!important;
}*/
 
/*.comment form.caption-create textarea{
    height: 80px!important;
}*/
/*form.caption-create .summary .counter {
    position: absolute;
    top: 30px;
    right: 8px;
    font-size: 14px;
    color: #888;
    font-family: 'Helvetica Neue',Helvetica,Arial,sans-serif;
 
}*/
 
.summary{
    float:right;
    margin-bottom: 17px;
    margin-right: 10px;
}
#captions .usertext>span {
    line-height: 16px;
}
#captions .usertext:before {
    display: none;
}
#captions .usertext {
    background: #181817;
    border-radius: 2px;
    border: 0px;
    padding: 9px 12px 11px 15px;
}
  #captions .comment-reply-count{
	position: absolute !important;
    bottom: 0 !important;
    right: 0 !important;
    top: 0;
    left: 0;
    margin: 0 !important;
    pointer-events: none;
    font-weight: normal;
  }
  .comment-reply-count .comment-reply-icon{
    border: 0px;
    font-size: 16px;
    display: block;
    position: absolute;
    left: -55px !important;
    top: 16px;
    pointer-events: auto;
    height: 10px;
    width: 10px;
    text-align: center;
    padding: 5px;
  }

  .comment-reply-count > .comment-reply-count-text{
    font-size: x-small;
    position: absolute;
    right: 32px !important;
    top: 2px !important;
    margin: 8px;
  }
.comment.expanded>.caption>.comment-reply-count>.comment-reply-count-text {
    display: none!important;
}
.comment-reply-icon:before {
    content: "-";
}
.comment-reply-icon--vertical {
    transform: none!important;
    left: 0;
    top: 0px;
    width: auto;
}
.comment-reply-icon--vertical:before {
    content: "+"!important;
}
.comment-reply-icon:first-of-type {
    display: none!important;
}
.icon-reply {
    display: none;
}
.caption:hover .comment-create-reply {
    display: block;
}
.comment-create-reply:hover{
	text-decoration:underline;
}
.comment-create-reply-wrapper {
    margin: 8px;
}
.post-account{
    color: #5675c8!important;
}
.comment-username {
    color: #5675c8!important;
    font-weight: normal;
}
.comment-meta-spacer {
    margin-left: 10px;
}
.comment-meta-spacer:not(:last-of-type):after {
    display: inline-block;
    content: ':';
    margin-left: 4px;
}
.comment-reply-icon {
    display: none;
}
.caption-gradient-wrapper .caption-gradient {
    display: none;
}
.caption {
    margin-bottom: 4px;
}
  #captions.root-caption-container > .children,
  #captions > .comment
  {
    padding-left:10px !important;
  }

.comment {
    margin-left: 30px !important;
}

.post-action {
    background: transparent;
    box-shadow: none;
}
#side-gallery .new-panel-header {
    background: #181817;
}
.new-panel {
    background: transparent;
}

.post-image-meta {
    margin-top: 0px;
    padding-bottom: 16px !important;
    border-radius: 0px 0px 4px 4px !important;
}

.layout-options .layout-button.selected,
.layout-options .layout-button:hover {
    background-color: #181817;
}

.post-images {
    margin-top: 0;
}
.post-image-container {
    margin-bottom: 8px;
}
.post-image-container--spacer {
    background: transparent;
}
.post-action-icon {
    height: 40px;
    width: 40px;
    padding: 5px;
    margin-right: 10px;
}
.post-action-icon:before {
    font-size: 20px;
    line-height: 20px;
}
.navPrev {
    border: 0;
    height: 36px;
    box-sizing: border-box;
}
.js-post-truncated {
    padding: 8px;
    background: transparent;
}
#captions .usertext .caption-awards {
    top: 8px;
    bottom: auto;
    right: 52px;
    float: none;
}

#captions .comment .caption.contexted .usertext {
    background-color: #353535!important;
}
.comment-create-reply,
#captions .author {
font: x-small verdana,arial,helvetica,sans-serif;
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    font-stretch: normal;
    font-size: x-small;
    line-height: normal;
    font-family: verdana, arial, helvetica, sans-serif;
    margin-bottom: 2px;
    color: #aaa;
    letter-spacing: 0;
}
.comment-create-reply {
    font: x-small verdana,arial,helvetica,sans-serif;
    margin-bottom: 2px;
    letter-spacing: 0;
    display: block;
    background: transparent;
    border-radius: 0;
    color: #5675c8;
	width:auto;
    height: auto;
    text-transform:lowercase;
}

.bad-captions{
	margin-left: 35px;
    margin-top: 6px;
    float: none;
    height: auto;
}
.post-options-publish:hover{
	color: #fff!important;
}
.post-options-publish{
	color: #fff!important;
}

/* Global stuff */
 
  /* TODO: investigate if this breaks something.
  input[type="text"], input[type="password"], input[type="email"], textarea{
    background-color: #1c1c1c !important;
  }
  */

  /* Posts */
  .post-header{
    background-color: rgba(38,38,38,.9);
    width: 728px;
  }
  .post-title{
    font-size: 16px;
    font-family: open_sansbold,sans-serif;
    font-weight: bold; /* TODO: 400 in original. Why not bold ? */
    color: #f2f2f2;
  }
  .post-container, .new-panel, .new-panel-header{
    background-color: #2B2B2B !important;
  }

  .post-image{
    background-color: #181817;
  }

  .js-post-truncated{
    margin-top: 0;
  }

  /* Comments */

  #comments-container::before{
    background-color: inherit;
  }
  
  .caption{
   margin-bottom: 5px; 
  }
  .children form.caption-create{
    margin: 5px 0 0 30px;
  }
  #captions .usertext{
    background-color: #181817;
    border-radius: 6px !important;
  }
  /* Remove the strange gradient thing*/
  .caption-gradient-wrapper{
    display: none;
  }
  /* We need this because we removed the gradient */
  .bad-captions{
    margin-top: 0;
  }
  .bad-captions > .link{
    margin: 3px 0 14px 15px;    
  }
  /* Remove that bubble blib thingy */
  #captions .comment[data-level]>.caption .usertext:before,
  #captions .comment>.caption .usertext:before,
  form.caption-create:before{
    display: none;
  }
  /*
  Move the open replies button to the left.
  We are missing the reply count now though. TODO: find a solution
  */

  /* For now we just don't show it at all on the replies page */
  .caption.unvotable .comment-reply-count{
    display: none !important;
  }
  

  /* Minimize "write comment" area when not focused */
  #captions > .caption-create{
    height: auto;
    padding: 0px;
  }

  /*i'm tired of changing the colour of all the things. Everything I don't like is going to be transparent.*/

.layout-options, 
.post-images{
background-color:transparent!important;
}

/*
	this makes posting comments a pain because it hides the submit button as you go to submit

  #captions > .caption-create textarea:focus + .summary{ display: block; }
  #captions > .caption-create .summary{ display: none; }

*/
  #captions > .caption-create .summary:hover{ display: block; }  
  /* Why is this even there ? In case you forget your username ? */
  #captions > .caption-create .caption-create-username{ display: none; }
}
