css:
@-moz-document domain("ashwinder.sycophanthex.com") {

/*Change this URL to header image of choice. Some options:
#1 (Lion and snake): http://i.imgur.com/0UtXD3o.png
#2 (Text only, large green): http://i.imgur.com/WKVCX8s.png
#3 (Text only, smaller gray): http://i.imgur.com/CmmTKrg.png*/
img[src*="ashwinder"] {
    /*[[header]]*/
    overflow: hidden;
    height: 0px;
    width: 0px;
}

/*Story text area font size and border*/
body {
    font-size: .9em;
    font-family: "Tahoma";
}

table[bordercolor="#030412"] {
    border-color: #fff;
    border-width: 0px;
}

/*Navigation bar*/
body tbody:nth-child(1) tr:nth-child(1) td:nth-child(1) table tbody tr:nth-child(1) td:nth-child(1) {
    background-color: #fff !important;
}

td[bgcolor*="90B290"] {
    color: #fff;
    background-color: #fff;
}

a.menu, a[href="http://ashwinder.sycophanthex.com/help.php#rules"], a[href="http://ashwinder.sycophanthex.com/complete.php?action=complete"], a[href="http://ashwinder.sycophanthex.com/random.php"], a[href="http://ashwinder.sycophanthex.com/top25byrating.php"] {
	background: #57845b !important;
    padding: 2px 4px;
    text-transform: uppercase;
    color: #fff !important;
    font-size: .65em !important;
    letter-spacing: .1em;
}

a.menu:hover, a[href="http://ashwinder.sycophanthex.com/help.php#rules"]:hover, a[href="http://ashwinder.sycophanthex.com/complete.php?action=complete"]:hover, a[href="http://ashwinder.sycophanthex.com/random.php"]:hover, a[href="http://ashwinder.sycophanthex.com/top25byrating.php"]:hover {
    text-decoration: none;
    color: #B2D4BC !important;
    transition: all .21s ease-in;
    -webkit-transition: all .21s ease-in;
    -moz-transition: all .21s ease-in;
    -o-transition: all .21s ease-in;
}

/*Dropdown menus, chapter nav, font change buttons*/
select[name="sid"], select[id="family"] {
    padding: 3px;
    font-family: "Tahoma";
    background-color: #f5f5f5;
    border: none;
}

input[type="button"] {
    font-family: "Tahoma";
    background-color: #f5f5f5;
    border: none;
    padding: 2px;
    font-size: .8em;
}

input[type="button"]:hover {
    background-color: #cce4cc;
    transition: all .21s ease-in;
    -webkit-transition: all .21s ease-in;
    -moz-transition: all .21s ease-in;
    -o-transition: all .21s ease-in;
}

input[id="sizeDown"]:hover {
    background-color: #e6b5b5;
    transition: all .21s ease-in;
    -webkit-transition: all .21s ease-in;
    -moz-transition: all .21s ease-in;
    -o-transition: all .21s ease-in;
}

form[enctype="multipart/form-data"] a {
    background: #f5f5f5;
    padding: 6px 5px 4px 5px;
    font-size: .7em;
    font-weight: bold;
}

form[enctype="multipart/form-data"] a:hover {
    text-decoration: none;
    color: #de2d2d;
    transition: all .21s ease-in;
    -webkit-transition: all .21s ease-in;
    -moz-transition: all .21s ease-in;
    -o-transition: all .21s ease-in;
}

/*Story title, author name, reviews, add to favorites*/
a[href*="reviews.php?action=addreview&sid"] {
    color: #ad2828;
    font-weight: bold;
    font-style: italic;
    font-size: .8em;
}

a[href*="viewstory.php?sid="] {
	color: #ad2828;
    font-style: italic;
}

a[href*="viewuser.php?uid="] {
	color: #ad2828;
}

a[href*="reviews.php?sid="], a[href*="viewuser.php?action=favstor&uid="] {
    font-size: .77em;
}

}
