css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://bato.to/read") {
  html,
  body,
  #comic_wrap,
  #content,
  #mainContent,
  #ipbwrapper {
    width: auto;
    background-color: #333333 !important;
    max-width: none;
    min-width: none;
    min-height: none;
    max-height: none;
    padding: 0;
    margin: 0;
    overflow: hidden;
  }

  #branding,
  #user_bar,
  #footer_utilities {
    display: none;
  }

  #ipboard_body {
    background: none;
  }

  #reader > .moderation_bar {
    position: absolute;
    z-index: 1;
    padding: 0 !important;
    margin: 0 !important;
    height: 230px !important;
  }

  #read_settings {
    background-color: #333333 !important;
    color: #cccccc !important;
  }

  .moderation_bar > ul {
    margin: 10px 0 0 10px !important;
  }

  .moderation_bar > ul > li {
    display: block !important;
    margin: 0 0 5px 0 !important;
    text-align:left;
  }

  .moderation_bar > ul > li:nth-child(n+5):nth-child(-n+8) {
    display: none !important;
  }

  .moderation_bar > ul > li > a:visited,
  .moderation_bar > ul > li > a {
    color: #cccccc !important;
  }

  /* nth-of-type(2) did not work for some reason... */
  #reader > div.moderation_bar:nth-of-type(even) {
    display: none !important;
  }

  #comic_wrap > div {
    top: auto !important;
    right: auto !important;
  }

  #comic_page {
    height: 100vh;
  }
}
