css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.isratrance.com") {

body {
  background: #444 !important;
  }
h1 {
  color:#FFFFFF !important;
  font-family:MetaPro-Normal !important;
  font-size:20px !important;
  font-weight:bold !important;
  }
.border {
  border:8px solid #BEDDF7 !important;
  }
table#font11 td {
  background-color:#333 !important;
  }
.forum_categoriess {
  background-color:#fff !important;
  }
td.forum_categories {
  background-color:#444 !important;
  }
.forum_categories, #font11 table tr {
  background:#444 !important;
  color: #eee !important;
  margin-left:2px !important;
  margin-right:2px !important;
  padding: 8px 2px 4px !important;
  }
.bordertable {
  background-color:#555 !important;
  border:1px solid #BEDDF7 !important;
  }
.ButtonOff {
  background-color:#eee !important;
  border:1px outset #BEDDF7 !important;
  color:#222 !important;
  padding: 4px !important;
  margin: 4px 0 !important;
  font-family:Verdana,Helvetica,Tahoma,Arial !important;
  font-size:12px !important;
  font-weight:bold !important;
  }
table#font11 tr.forum_categories td[style="color: red;"] {
  color: white !important;
  font-size: 120%;
  }
a:link {
  color:#ddd !important;
  }
a:visited {
  color:#999 !important;
  }
table#font11.border tr:hover {
  background: #222 !important;
  }
table tbody tr td.forum_sub_categories a {
  font-family:MetaPro-Normal !important;
  font-size: 18px !important;
  font-weight: normal !important;
  }
table#font11.border tbody tr td[valign="top"] {
  font-size: 17px !important;
  font-family: MetaPro-Normal !important;
  color: #ccc !important;
  background: #222 !important;
  padding: 20px 5px 10px !important;
  }
table#font11.border tbody tr td[valign="bottom"] {
  padding: 20px !important;
  }
.membercolor, .rankcolor10 {
  color:#FFF !important;
  }
#font12n {
  font-size: 14px;
  }

img[src="/images/folder.gif"] {
  background: url(http://www.konspiredesign.com/styling/isra/folder.gif) !important;
  padding: 26px 0 0 25px !important;
