css:
@-moz-document regexp("http:\\/\\/www.xianxiaworld.net\\/A-Thought-Through-Eternity.+") {
/*@import 'https://fonts.googleapis.com/css?family=Oswald';*/

:root{
	--alpha:0.75;
}

@font-face {
	font-family: 'Oswald';
	font-style: normal;
	font-weight: 400;
	src: local('Oswald Regular'), local('Oswald-Regular'), url(https://fonts.gstatic.com/s/oswald/v11/pEobIV_lL25TKBpqVI_a2w.woff2) format('woff2');
}
a {
	font-family: Oswald, sans-serif;
}
body {
	background: url("https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-128001.jpg");
	background-attachment: fixed;
	background-repeat: no-repeat;
	background-size: cover;
	background-position: center;
	color: white;
	font-family: Oswald, sans-serif;
}
#list dt {
	background: none;
}
#list dd a {
	color: #A8A8A8;
}
#list dd a:link {
	color: white;
}

#sidebar #donate{
	background:none;
}

#footer {
	width: 80%;
	background: rgba(0, 0, 0, var(--alpha));
	padding: 20px 25px;
	margin: 5px auto;
	border-radius: 3px;
}
.ywtop {
	padding: 10px 0;
	width: 80%;
	margin-bottom: 5px;
	border: none;
	background: rgba(0, 0, 0, var(--alpha));
	padding-left: 25px;
	padding-right: 25px;
	border-radius: 0 0 3px 3px;
}
#donate1 #donate-content {
	background: none;
}
#donate1 #bar-paypal {
	padding: 10px 0;
}
#donate1 #bar-total {
	margin-left: 10px;
	line-height: 29px;
}
.bottem2 {
	margin: 0 auto;
	border: none;
}
.bottem2 a {
	color: white;
}
#disqus_thread {
	color: white !important;
}
#disqus_thread #disq-app #document html body {
	color: white;
}
input {
	margin-left: 5px;
	margin-right: 10px;
	vertical-align: middle;
}
.con_top {
	background: none;
	border: none;
	font-family: Oswald, sans-serif;
}
.con_top #page_set {
	margin-right: 20px;
}
select {
	margin: 0 10px;
}
.header {
	display: none;
}
.nav {
	width: 80%;
	border: none;
	background: rgba(0, 0, 0, var(--alpha));
	padding-left: 25px;
	padding-right: 25px;
	margin: 5px auto;
	border-radius: 3px;
}
.nav ul {
	height: 100%;
}
.nav li {
	width: 8%;
}
.nav ul li {
	float: left;
	line-height: normal;
	text-align: center;
	height: 100%;
}
.nav ul li a {
	vertical-align: middle;
	height: 100%;
	padding: 0;
	font-size: 1.1em;
	font-weight: normal;
	font-family: Oswald, sans-serif;
	line-height: 40px;
}
.content_read {
	width: 80%;
	padding-left: 25px;
	padding-right: 25px;
	margin: 0 auto;
	background: rgba(0, 0, 0, var(--alpha));
	border-radius: 3px;
}
.con_top {
	width: 100%;
	vertical-align: middle;
}
.box_con #content {
	color: #D8D8D8 !important;
}
.content_read .box_con {
	background: none;
	width: 100%;
	border: none;
}

.box_con{
	width: 80%;
	padding-left: 25px;
	padding-right: 25px;
	margin: 5px auto;
	background: rgba(0, 0, 0, var(--alpha));
	border-radius: 3px;
	border:none;
}

.content_read .box_con font {
	font-family: Oswald, sans-serif;
	color: #d35400;
}
.bookname {
	border: none;
	width: 80%;
	color: #D8D8D8;
	margin-bottom: 0;
}
.bookname h1 {
	color: White;
	font-family: Oswald, sans-serif;
}
.bottem1 a {
	color: white;
}
.bottem1 {
	margin: 0 auto;
}
}
