css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("discordapp.com") {

body{}

  .guilds-wrapper .guilds .friends-icon {
	width: 50px;
	height: 50px;
	background-image: url(https://c776950.ssl.cf2.rackcdn.com/assets/icon/icon_front_color-9ee34392d4724e5ef23f913f622d544e.png);
	background-size: 50px 50px;
	background-position: 50%;
	background-repeat: no-repeat;}
  
  element {
	background-color: rgb(46, 99, 190);
	border-radius: 25px;
	/* border-radius: 25.0139px; */}
  
  .chat .new-messages-bar {
	position: absolute;
	left: 20px;
	top: 0;
	-px: 0;
	right: 30px;
	border-radius: 0 0 3px 3px;
	background-color: rgba(90,90,90,.9);
	text-transform: capitalize;
	font-weight: 500;
	cursor: pointer;
	color: #fff;
	z-index: 1;
	-webkit-transition: background-color .2s;
	transition: background-color .2s;
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
}

  .guilds-wrapper .guilds .guild + .guild {
	margin-top: 0px;
}
  .guilds-wrapper {
	overflow: hidden;
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
	width: 80px;
	-webkit-box-orient: vertical;
	-o-box-orient: vertical;
	-ms-flex-direction: column;
	flex-direction: column;
	background: #63a0d0;
	flex-shrink: 0;
}
  .guilds-wrapper .guilds .friends-online {
	font-size: 10px;
	text-transform: uppercase;
	font-weight: 600;
	color: hsla(0,0%,100%,1);
	margin: 10px 0;
	word-wrap: normal;
	white-space: normal;
	width: 50px;
	line-height: 130%;
	text-align: center;
}
  .account {
	height: 70px;
	background: rgb(41, 86, 115);
	color: #fff;
	font-size: 14px;
	font-weight: 500;
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
	-webkit-box-align: center;
	-o-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	padding: 0 10px;
	border-top: 1px solid #3F84B1;
	flex-shrink: 0;
}
  .guild-channels {
	background-color: #3F84B1;
	padding-bottom: 20px;
	box-sizing: border-box;
	position: relative;
	overflow: hidden;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	-webkit-box-flex: 1;
	-o-box-flex: 1;
	box-flex: 1;
	-ms-flex: 1;
	flex: 1;
}
  .guild-channels header {
	font-size: 12px;
	color: #fff;
	opacity: 1;
	-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=30)";
	filter: alpha(opacity=30);
	text-transform: uppercase;
	letter-spacing: .025em;
	font-weight: 600;
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
	-webkit-transition: opacity .15s ease;
	transition: opacity .15s ease;
	cursor: pointer;
	margin: 0;
	height: 30px;
	line-height: 30px;
	margin-top: 20px;
}
.guild-header header {
	background-color: rgb(41, 86, 115);
	position: relative;
	font-family: Whitney,Helvetica Neue,Helvetica,Arial,Lucida Grande,sans-serif;
	color: #fff;
	font-weight: 600;
	padding: 28px 24px;
	height: 56px;
	box-shadow: 0 1px 0 #3F84B1,inset 0 -1px 0 rgba(0,0,0,.1);
	box-sizing: border-box;
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
	-webkit-box-align: center;
	-o-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	z-index: 2;
	cursor: pointer;
	-webkit-transition: background-color .15s ease;
	transition: background-color .15s ease;
}
  .guild-header header:hover {
	background-color: #3F84B1;
}
  .guild-header ul {
	position: absolute;
	top: 56px;
	left: 0;
	background: rgb(41, 86, 115);
	box-shadow: 0 2px 0 #3F84B1,inset 0 -1px 0 rgba(0,0,0,.06);
	width: 100%;
	z-index: 1;
	-webkit-transition: -webkit-transform .25s ease-out;
	transition: transform .25s ease-out;
	-webkit-transform: translate3d(0,-110%,0);
	transform: translate3d(0,-110%,0);
	box-sizing: border-box;
	-moz-osx-font-smoothing: grayscale;
}
  .account .btn-deafen, .account .btn-mute {
	border-right: 1px solid rgb(41, 86, 115);
}
  .account .btn {
	width: 32px;
	height: 32px;
	background: #295673;
	position: relative;
}
  .account .btn-group {
	border: 1px solid #3F84B1;
	border-radius: 4px;
	overflow: hidden;
	flex-shrink: 0;
}
  .account .btn-deafen, .account .btn-settings {
	box-shadow: inset 1px 0 0 #3F84B1;
}
  .guild-channels .channel-text.selected {
	/* background: -webkit-linear-gradient(left,#282b30 85%,#2e3136); */
	background: linear-gradient(90deg,rgb(41, 86, 115) 85%,#3F84B1);
}
  .scroller-wrap.dark .scrollbar, .scroller-wrap.dark .scrollbar::after {
	background-color: rgb(41, 86, 115);
}
  .scroller-wrap.dark .scrollbar .thumb {
	background-color: #3F84B1;
}
  .account .btn-settings::after {
	background-image: url(https://www.dropbox.com/s/wwinqnd8e0yqrpu/60f6b0788a5d9394b44261ef5d8ecffc.svg?dl=1);
	opacity: .5;
	-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=20)";
	filter: alpha(opacity=20);
}
	.account .btn-mute.disabled::after {
	background-image: url(https://www.dropbox.com/s/usiyrd79svbtxx8/a65804e88b7c4d2dd2149bb3db64c449.svg?dl=1);
}
	.guild-channels .channel-text:hover {
	background: linear-gradient(90deg,rgb(41, 86, 115) 85%,#3F84B1);
}
	.tooltip.tooltip-right::after {
	border-right-color: #3F84B1;
}
	.tooltip {
	background-color: #3F84B1;
}
	.guilds-wrapper .guild-separator::after {
	position: absolute;
	top: 0;
	left: 20%;
	right: 20%;
	content: ' ';
	height: 2px;
	background: #ffffff;
}
	.settings .settings-actions {
	background-color: #3F84B1;
	border-top: 1px solid #3F84B1;
	box-sizing: border-box;
	width: 100%;
	padding: 18px 40px;
	border-radius: 0 0 5px 0;
	text-align: right;
	flex-shrink: 0;
}
	.settings .settings-inner {
	background-color: rgb(41, 86, 115);
	background-position: 0 100%;
	background-size: 100%,auto;
	background-repeat: no-repeat;
	min-height: 0;
	overflow: hidden;
}
	.guilds-wrapper .guilds-add {
	width: 100%;
	height: 100%;
	border-radius: 50%;
	background: #63a0d0;
	color: #FFFFFF;
	border: 1px dashed #FFFFFF;
	padding: 0;
	font-size: 40px;
	font-weight: 200;
	text-align: center;
	line-height: 100%;
	-webkit-transition: border-color .25s ease,color .25s ease;
	transition: border-color .25s ease,color .25s ease;
}
	.guilds-wrapper .btn-download-apps::after {
	content: '';
	display: block;
	width: 30px;
	height: 2px;
	background-color: #FFFFFF;
	position: absolute;
	top: -1px;
	left: 50%;
	margin-left: -15px;
}
	.guild-channels .channel a {
	-webkit-box-orient: horizontal;
	-o-box-orient: horizontal;
	-ms-flex-direction: row;
	flex-direction: row;
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
	color: #fff;
	text-decoration: none;
	font-weight: 500;
	opacity: 0.6;
	-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=30)";
	filter: alpha(opacity=30);
	text-shadow: 0 1px 1px rgba(0,0,0,1);
}
	.guild-channels .channel-text .channel-name::before {
	content: '#';
	color: #FFFFFF !important;
	margin-right: 3px;
	font-weight: 600;
}
	.guilds-wrapper .btn-download-apps {
	position: relative;
	background-color: rgba(171, 67, 67, 0);
	display: block;
	margin: 0;
	padding: 0;
	color: hsla(0, 0%, 100%, 1);
	text-transform: uppercase;
	text-align: center;
	font-size: 11px;
	white-space: wrap;
	width: 80px;
	margin: 10px 0 0 -15px;
	padding: 10px 8px;
	-webkit-transition: color .2s ease;
	transition: color .2s ease;
}
	.guilds-wrapper .guilds .guild.unread::before {
	height: 12px;
	border-radius: 6px;
	background: #ffffff;
	margin-top: -6px;
}
	.private-channels .channel.selected {
	background: -webkit-linear-gradient(left,#282b30 85%,#2e3136);
	background: linear-gradient(90deg,rgb(41, 86, 115) 85%,#3F84B1);
	color: #fff;
}
	.search-bar {
	background-color: rgb(41, 86, 115);
	box-shadow: 0 1px 0 0 rgb(41, 86, 115),inset 0 -1px 0 0 rgb(41, 86, 115);
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
	-webkit-box-align: center;
	-o-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	flex-shrink: 0;
	border-radius: 4px;
}
	.private-channels {
	background-color: #3F84B1;
	box-sizing: border-box;
	position: relative;
	overflow: hidden;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	-webkit-box-flex: 1;
	-o-box-flex: 1;
	box-flex: 1;
	-ms-flex: 1;
	flex: 1;
	display: -webkit-box;
	display: -ms-flexbox;
	display: box;
	display: flex;
	-webkit-box-orient: vertical;
	-o-box-orient: vertical;
	-ms-flex-direction: column;
	flex-direction: column;
	-webkit-transform: translateZ(0);
	transform: translateZ(0);
}
	.private-channels .channel:hover {
	background: -webkit-linear-gradient(left,#282b30 85%,#2e3136);
	background: linear-gradient(90deg,rgb(41, 86, 115) 85%,#3F84B1);
	color: #fff;
}
	.private-channels .channel .channel-name {
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
	padding-right: 5px;
	opacity: 1;
	-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=30)";
	filter: alpha(opacity=100%);
	-webkit-box-flex: 1;
	-o-box-flex: 1;
	-ms-box-flex: 1;
	box-flex: 1;
	flex-grow: 1;
}
	.search-bar-clear::after {
	width: 3px;
	height: 9px;
	background: rgb(41, 86, 115);
	border-radius: 4px;
	margin: 19px 0 0 21px;
	-webkit-transform: rotate(-45deg);
	transform: rotate(-45deg);
	-webkit-transition: all .2s ease;
	transition: all .2s ease;
}
	.search-bar-clear::before {
	width: 19px;
	height: 19px;
	background: rgb(41, 86, 115);
	border-radius: 10px;
	margin: 3px;
	-webkit-transform: rotate(45deg);
	transform: rotate(45deg);
	-webkit-transition: all .1s ease;
	transition: all .1s ease;
}
	.private-channels .search-result.selected, .private-channels .search-result:hover {
	background: -webkit-linear-gradient(left,#282b30 85%,#2e3136);
	background: linear-gradient(90deg,rgb(41, 86, 115) 85%,#3F84B1);
}
	.private-channels .channel:hover .icon-friends {
	background-color: #63a0d0;
}
	.private-channels .channel .icon-friends {
	background: url(https://www.dropbox.com/s/wwinqnd8e0yqrpu/60f6b0788a5d9394b44261ef5d8ecffc.svg?dl=1) 1% no-repeat;
	width: 30px;
	height: 30px;
	border-radius: 15px;
	background-color: rgb(41, 86, 115);
	margin-right: 12px;
}
	.private-channels .channel.selected .icon-friends {
	background-color: #63a0d0;
}
}
