css:
/* AGENT_SHEET */
/*
Rhodophobia Firefox Skin for Tab Center addon, made by miscoined.

Inspired by neiio's Void Windows 10 skin.

#fc615b accent
#323232 bg
#232323 bg accent
#8a8a8a text
#a7a7a7 text accent
rgb(255,144,138)

Importants everywhere this is pain
*/
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* CLEAR EXISTING STYLES */
#verticaltabs-box .tabbrowser-tab[pinned] .tab-text.address-label,
#verticaltabs-box scrollbar[orient="vertical"] {
  display: none !important;
}

#verticaltabs-box .tab-meta-image,
#verticaltabs-box #pin-button,
#verticaltabs-box .tab-close-button,
#verticaltabs-box .tabbrowser-tab[selected] {
  background: none !important;
}

#verticaltabs-box,
#verticaltabs-box #TabsToolbar,
#verticaltabs-box #find-input,
#verticaltabs-box[expanded] #tabbrowser-tabs {
  border: none !important;
}

#verticaltabs-box .tabbrowser-tab,
#verticaltabs-box .tabbrowser-tab[pinned],
#verticaltabs-box .tabbrowser-tab[pinned] .tab-meta-image > * {
  padding: 0 !important;
}

#verticaltabs-box:not([expanded]) #new-tab-button,
#verticaltabs-box #find-input,
#verticaltabs-box .tabbrowser-tab[pinned] .tab-text {
  margin: 0 !important;
}

#verticaltabs-box .tab-close-button {
  margin-bottom: 0 !important;
}

#verticaltabs-box .tab-text { font-weight: normal !important; }

#verticaltabs-box .tab-content,
#verticaltabs-box .tabbrowser-tab,
#verticaltabs-box .tabbrowser-tab:hover,
#verticaltabs-box .tab-meta-image {
  min-height: auto !important;
  max-height: unset !important;
}

#verticaltabs-box .tab-meta-image {
  min-width: auto !important;
  max-width: unset !important;
}

#verticaltabs-box #pin-button:before { content: "p"; }
#verticaltabs-box .tab-close-button:before { content: "x"; }

#appcontent, #verticaltabs-box {
 --collapsed-width: 30px;
}

#verticaltabs-box,
#verticaltabs-box #TabsToolbar {
  background: #323232 !important;
}

#verticaltabs-box .tabbrowser-tab:hover {
  background: rgba(0,0,0,0.25) !important;
}

#verticaltabs-box #TabsToolbar > *:not(#find-input):hover,
#verticaltabs-box .tab-close-button:hover,
#verticaltabs-box #new-tab-button:hover,
#verticaltabs-box .tabbrowser-tab[selected] {
  color: #fc615b !important;
}

#verticaltabs-box #TabsToolbar > *,
#verticaltabs-box .tabbrowser-tab,
#verticaltabs-box .tab-text.address-label{
  color: #8a8a8a !important; 
}

#verticaltabs-box .tab-text {
  color: #a7a7a7 !important;
}

#verticaltabs-box .tab-text.address-label,
#verticaltabs-box .tab-close-button {
  font-size: 12px;
}

#verticaltabs-box[expanded] #TabsToolbar,
#verticaltabs-box .tabbrowser-tab {
  border-right: 2px solid #fc615b !important;
  transition: border-right 0.01s ease-in 0.4s;
}

#verticaltabs-box #tabbrowser-tabs {
  border-top: 3px double #fc615b !important;
}

#verticaltabs-box .tabbrowser-tab[selected] {
  border-left-color: currentColor !important;
}

#verticaltabs-box .tabbrowser-tab[pinned] {
  border-left-color: #a7a7a7 !important;
}

#verticaltabs-box #TabsToolbar > * {
  margin: 0 4px !important;
  font: 12pt "rainyhearts", sans-serif !important;
  background: #232323 !important;
  border-radius: 5px !important;
  box-shadow: 0 1px 0 rgba(0,0,0,0.5);
  padding-top: 1px !important;
}

#verticaltabs-box .tabbrowser-tab {
  font: 16px "rainyhearts", sans-serif !important;
  border-left-width: 2px !important;
}

#verticaltabs-box #TabsToolbar {
  padding: 1px 0 8px !important;
  max-height: 36px;
}

#verticaltabs-box[expanded] .tabbrowser-tab:first-child {
  padding-top: 3px !important;
}

#verticaltabs-box:not([expanded]) #TabsToolbar {
  padding-left: 4px !important;
}

#verticaltabs-box #find-input {
  padding: 6px !important;
}

#verticaltabs-box #pin-button,
#verticaltabs-box #new-tab-button {
  min-width: 23px !important;
  max-width: 23px !important;
}

#verticaltabs-box #find-input > .textbox-input-box {
  min-width: calc(100% - 20px);
}

#verticaltabs-box .tabbrowser-tab[pinned] .tab-meta-image > * {
  margin: 3px !important;
  max-height: 16px !important;
  min-height: 16px !important;
  min-width: 16px !important;
  max-width: 16px !important;
}
