css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://moodle"), url-prefix("https://moodle") {
  
  body {
    background-color: #404040;
    color: #c0c0c0;
  }
  
  /** Login Page **/
  .breadcrumb {
    background-color: #333333;
  }
  
  .breadcrumb > li {
    text-shadow: none;
  }
  
  input, input[type="text"], input[type="password"] {
    background-color: #333333;
    color: #c0c0c0;
  }
  
  input[type="submit"] {
    background-color: #333333;
    color: #c0c0c0;
    background-image: none;
    text-shadow: none;
  }
  
  /** Upper bar **/
  .navbar-inner {
    background-color: #2A2A2A;
    background-image: none;
  }
  
  /* Upper bar - Text heading */
  .navbar .brand {
    text-shadow: none;
  }
    
  .block {
    background-color: #333333;
  }
  
  .block {
    background-color: #333333;
  }
  
  /** Courses **/
  .courses .coursebox, .courses .coursebox.even {
    background-color: #333333;
  }
  
  .courses .coursebox:hover, .courses .coursebox.even:hover {
    background-color: #4c4c4c;
  }
  
  /* text */
  a {
    color: #0070a8;
  }
  
  a:hover {
    color: #0092db;
  }
  
  .text_to_html > p:nth-child(8) > span:nth-child(2) {
    color: #c0c0c0;
  }
  
  /* Course List: XX YYY - XX YYY row */
  .course-content .current {
    background-color: #4c4c4c;
  }
  
  /** Homework/Assignment **/
  /* Grading Summary */
  .generaltable tbody > tr:nth-child(2n+1) > td {
    background-color: #404040;
  }
  
  .generaltable tbody tr:hover > td {
    background-color: #4c4c4c;
  }
  
  /* Submissions Page */
  select {
    background-color: #333333;
    color: #c0c0c0;
  }
  
  /* rows */
  .path-mod-assign .gradingtable tr.unselectedrow td {
    background-color: #404040;
  }
  
  #fitem_id_submitbutton {
    background-color: #404040;
  }
  
  /* row links */
  .path-mod-assign .gradingtable tr.unselectedrow td a {
    color: #0092db;
  }
  
  .path-mod-assign .gradingtable tr.unselectedrow td a:hover {
    color: #29b8ff;
  }
  
  /* 'Edit' dropdown */
  .moodle-actionmenu.show[data-enhanced] .menu {
    background-color: #404040;
  }
  
  .moodle-actionmenu.show[data-enhanced] .menu a {
    color: #c0c0c0;
  }
  
  /* 'Feedback Comments' */
  textarea {
    background-color: #333333;
    color: #c0c0c0;
  }
  
  /** Forum/Discussion Posts **/
  
  /* Post */
  .forumpost {
    background-color: #404040;
  }
  
  /* Message */
  div.editor_atto_toolbar, div.editor_atto_toolbar div.atto_group {
    background-color: #404040;
  }
  
  .editor_atto_content_wrap {
    background-color: #333333;
    color: #c0c0c0;
  }
  
  /* Attachments */
  .filemanager .fp-navbar, .fp-viewbar, .fp-toolbar .fp-btn-add, .filemanager-container {
    background-color: #404040;
  }
  
  .dndupload-arrow {
    filter: invert(75%);
  }
  
  .filemanager-container .dndupload-target {
    background-color: #333333;
  }
  
  .filemanager-container {
    background: #333333;
  }
  
  /* 'Post to Forum', 'Cancel' Pane */
  #fgroup_id_buttonar {
    background-color: #404040;
  }
  
  /** Attachments upload - File Picker - Recent files **/
  
  /* Top Header */
  .moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd, .moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
    background: #333333;
    color: #c0c0c0;
    text-shadow: none;
  }
  
  /* Left Column */
  .moodle-dialogue-base .moodle-dialogue-wrap {
    background-color: #333333;
  }
  
  .file-picker a {
    color: #c0c0c0;
  }
  
  /* Selected row */
  .file-picker .fp-list .fp-repo.active {
    background-color: #404040;
    color: #c0c0c0;
  }
  
  /* Right Top Bar */
  .file-picker .fp-navbar {
    background-color: #333333;
  }
  
  /* Right Bottom Area */
  .file-picker .fp-content, .fp-iconview .fp-filename-field .fp-filename {
    background-color: #404040;
  }
  
  /* Icon/Grid View */
  .fp-iconview .fp-filename-field .fp-filename:hover {
    color: #e6e6e6;
  }
  
  /* List View */
  .yui3-skin-sam .yui3-datatable-header {
    background-color: #404040;
    color: #c0c0c0;
    text-shadow: none;
  }
  
  .moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd {
    color: #e6e6e6;
  }
  
  .yui3-skin-sam .yui3-datatable-even .yui3-datatable-cell {
    background-color: #404040;
  }
  
  .yui3-skin-sam .yui3-datatable-odd .yui3-datatable-cell {
    background-color: #333333;
  }
  
  /** Attachments upload - File Picker - Upload a file **/
  .fp-content-center form + div {
    background-color: #404040;
  }
  
}
