css:
@namespace url(http://www.w3.org/1999/xhtml);

/*       Theme Made by ElBrado  (this is only the beta version, there's still work in progress) */
/* https://katcr.co/community/index.php?action=profile;u=55 */

@-moz-document domain("katcr.co") {

a:link,
a:visited {
    color: #000000;
    text-decoration: none;
    font-weight: bold;
}
a:hover {
    text-decoration: underline;
    cursor: pointer;
}


a.new_win:link,
a.new_win:visited {
    color: #4c4312;
    text-decoration: none;
}
a.new_win:hover {
    text-decoration: underline;
}

table {
    empty-cells: show;
}

body {
    background: #FFFFFF url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) repeat-x;
    font: 78%/130% "Verdana", "Arial", "Helvetica", sans-serif;
    margin: 0 auto;
    padding: 0px;
}


body#help_popup {
    padding: 1em;
}

body,
th,
tr {
    color: #000;
}
div#wrapper {
    margin: 0 auto;
    min-width: 764px;
    max-width: 2300px;
}
form {
    padding: 0;
    margin: 0;
}
input,
button,
select,
textarea {
    font: 95%/115% verdana, Helvetica, sans-serif;
    color: #000;
    background: #fff;
    border: 1px solid #7f9db9;
    padding: 2px;
}

select {
    padding: 0;
}

select option {
    padding: 1px;
}

textarea {
    font: 100%/130% verdana, Helvetica, sans-serif;
}

.button_submit,
.button_reset {
    background: #97a77e  url(https://katcr.co/community/Themes/default/images/submit_bg.png) no-repeat;
    border: 1px solid #aaa;
    cursor: pointer;
    font-weight: normal;
}
input:hover,
textarea:hover,
button:hover,
select:hover {
    border: 1px solid #454545;
}
.button_submit:hover,
.button_reset:hover {
    border: 1px solid #aaa;
    background: url(https://katcr.co/community/Themes/default/images/submit_bg.png) no-repeat 0 -140px #cde7ff;
}
input:focus,
textarea:focus,
button:focus,
select:focus {
    border: 1px solid #454545;
}

input.input_check,
input.input_radio {
    border: none;
    background: none;
}
h3.catbg input.input_check {
    margin: 9px 7px 0 7px;
}

input[disabled].input_text {
    background-color: #eee;
}

hr,
.hrcolor {
    height: 1px;
    border: 0;
    color: #ccc;
    background-color: #ccc;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #1500b9;
    font-size: 1.5em;
    margin: 0;
    padding: 0;
}

fieldset {
    border: 1px solid #c4c4c4;
    padding: 1em;
    margin: 0 0 0.5em 0;
}
fieldset legend {
    font-weight: bold;
    color: #444;
}

a img {
    border: 0;
}

strong {
    font-weight: bold;
}
em {
    font-style: italic;
}


.underline {
    text-decoration: underline;
}

.floatright {
    float: right;
}
.floatleft {
    float: left;
}
.flow_auto {
    overflow: auto;
}
.flow_hidden {
    overflow: hidden;
}
.flow_hidden .windowbg,
.flow_hidden .windowbg2 {
    margin-top: 2px;
}
.clear {
    clear: both;
}
.clear_left {
    clear: left;
}
.clear_right {
    clear: right;
}

.smalltext,
tr.smalltext th {
    font-size: 0.85em;
    font-family: verdana, sans-serif;
}
.middletext {
    font-size: 0.9em;
    line-height: 1em;
    font-family: verdana, sans-serif;
}
.normaltext {
    font-size: 1em;
    line-height: 1.2em;
}
.largetext {
    font-size: 1.4em;
}
.centertext {
    margin: 0 auto;
    text-align: center;
}
.righttext {
    margin-left: auto;
    margin-right: 0;
    text-align: right;
}
.lefttext {
    margin-left: 0;
    margin-right: auto;
    text-align: left;
}
.double_height {
    line-height: 2em;
}

.padding {
    padding: 0.7em;
}
.main_section,
.lower_padding {
    padding-bottom: 0.5em;
}

ul.reset,
ul.reset li {
    padding: 0;
    margin: 0;
    list-style: none;
}

blockquote.bbc_standard_quote,
blockquote.bbc_alternate_quote {
    font-size: x-small;
    color: #000;
    line-height: 1.4em;
    background: url(https://katcr.co/community/Themes/default/images/quote.png) 0.1em 0.1em no-repeat;
    border-top: 2px solid #99A;
    border-bottom: 2px solid #99A;
    padding: 1.1em 1.4em;
    margin: 0.1em 0 0.3em 0;
    overflow: auto;
}

blockquote.bbc_standard_quote {
    background-color: #d7daec;
}
blockquote.bbc_alternate_quote {
    background-color: #e7eafc;
}

code.bbc_code {
    display: block;
    font-family: "dejavu sans mono", "monaco", "lucida console", "courier new", monospace;
    font-size: x-small;
    background: #eef;
    border-top: 2px solid #999;
    border-bottom: 2px solid #999;
    line-height: 1.5em;
    padding: 3px 1em;
    overflow: auto;
    white-space: nowrap;
    
    max-height: 24em;
}

.codeheader,
.quoteheader {
    color: #666;
    font-size: x-small;
    font-weight: bold;
    padding: 0 0.3em;
}

.codeoperation {
    font-weight: normal;
}

.bbc_link:link,
.bbc_link:visited {
    border-bottom: 1px solid #A8B6CF;
}
.bbc_link:hover {
    text-decoration: none;
    border-bottom: 1px solid #346;
}
.bbc_size {
    line-height: 1.4em;
}
.bbc_color a {
    color: inherit;
}
.bbc_img {
    border: 0;
}
.bbc_table {
    font: inherit;
    color: inherit;
}
.bbc_table td {
    font: inherit;
    color: inherit;
    vertical-align: top;
}
.bbc_u {
    text-decoration: underline;
}
.bbc_list {
    text-align: left;
}
.bbc_tt {
    font-family: "dejavu sans mono", "monaco", "lucida console", "courier new", monospace;
}

.help {
    cursor: help;
}

.meaction {
    color: red;
}

.highlight {
    font-weight: bold;
    color: #ff7200 !important;
    font-size: 1.1em;
}

.highlight2 {
    background-color: #D1E1EF;
    color: #000 !important;
}


.titlebg,
.titlebg2,
tr.titlebg th,
tr.titlebg td,
tr.titlebg2 td {
    color: #222;
    font-family: arial, helvetica, sans-serif;
    font-size: 1.1em;
    font-weight: bold;
    background: #eaeaea url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat -10px -380px;
}
.catbg,
.catbg2,
tr.catbg td,
tr.catbg2 td,
tr.catbg th,
tr.catbg2 th {
    color: #fff;
    font-family: arial, helvetica, sans-serif;
    font-size: 1.1em;
    font-weight: bold;
    background: #a7b9cd url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat -10px -280px;
}

tr.titlebg th,
tr.titlebg2 th,
td.titlebg,
td.titlebg2,
tr.catbg th,
tr.catbg2 th,
td.catbg,
td.catbg2 {
    padding: 0 6px;
}
tr.titlebg th a:link,
tr.titlebg th a:visited,
tr.titlebg2 td a:link,
tr.titlebg2 td a:visited {
    color: #222;
}
tr.catbg th a:link,
tr.catbg th a:visited,
tr.catbg2 td a:link,
tr.catbg2 td a:visited {
    color: #fff;
}
.catbg select {
    height: 1.5em;
    font-size: 0.85em;
}

.windowbg,
#preview_body {
    color: #000;
    background-color: #ffffff;
}
.windowbg2 {
    color: #000;
    background-color: #ffffff;
}
.windowbg3 {
    color: #000;
    background-color: #cacdd3;
}

.pagesection {
    font-size: 0.9em;
    padding: 0.2em;
    overflow: hidden;
    margin-bottom: 1px;
}
div.pagesection div.floatright input {
    margin-top: 3px;
}
.pagelinks {
    padding: 0.6em 0 0.4em 0;
}

.approvebg {
    color: #000;
    background-color: #ffeaea;
}
.approvebg2 {
    color: #000;
    background-color: #fff2f2;
}

.approvetbg {
    color: #000;
    background-color: #e4a17c;
}
.approvetbg2 {
    color: #000;
    background-color: #f3bd9f;
}

.stickybg {
    background: #e8d8cf;
}
.stickybg2 {
    background: #f2e3d9;
}

.lockedbg {
    background: #d4dce2;
    font-style: italic;
}
.lockedbg2 {
    background: #d8e1e7;
    font-style: italic;
}

.post,
.personalmessage {
    overflow: auto;
    line-height: 1.4em;
    padding: 0.1em 0;
}

.signature,
.attachments {
    width: 98%;
    overflow: auto;
    clear: right;
    padding: 1em 0 3px 0;
    border-top: 1px solid #aaa;
    line-height: 1.4em;
    font-size: 0.85em;
}
.signature .bbc_img {
    max-width: 650px;
    max-height: 250px;
}
.custom_fields_above_signature {    
    clear: right;
    padding: 1em 0 3px 0;
    border-top: 1px solid #aaa;
    line-height: 1.4em;
    font-size: 0.85em;
    max-width: 650px;
    max-height: 250px;
}

.error {
    color: red;
}

.alert {
    color: red;
}

.birthday {
    color: #920ac4;
}
.event {
    color: #078907;
}
.holiday {
    color: #000080;
}

.warn_mute {
    color: red;
}
.warn_moderate {
    color: #ffa500;
}
.warn_watch,
.success {
    color: green;
}
a.moderation_link,
a.moderation_link:visited {
    color: red;
    font-weight: bold;
}
.openid_login {
    background: white url(https://katcr.co/community/Themes/default/images/openid.gif) no-repeat;
    padding-left: 18px;
}

.description,
.description_board,
.plainbox {
    padding: 0.5em 1em;
    font-size: 0.9em;
    line-height: 1.4em;
    border: 1px solid #eaeaea;
    background: #f5f5f0;
    margin: 0.2em 1px 1em 1px;
}
.description_board {
    margin: 1em 1px 0 1px;
}

.information {
    padding: 0.5em 1em;
    font-size: 0.9em;
    line-height: 1.3em;
    border: 1px solid #eaeaea;
    background: #f0f6f0;
    margin: 0.2em 1px 1em 1px;
}
.information p {
    padding: 1em;
    margin: 0;
}
p.para2 {
    padding: 1em 0 3.5em 0;
    margin: 0;
}


#ajax_in_progress {
    background: url(https://katcr.co/community/Themes/default/images/loadingbar.png) repeat-x;
    color: #f96f00;
    text-align: center;
    font-size: 16pt;
    padding: 8px;
    width: 100%;
    height: 66px;
    line-height: 25px;
    position: fixed;
    top: 0;
    left: 0;
}
#ajax_in_progress a {
    color: orange;
    text-decoration: underline;
    font-size: smaller;
    float: right;
    margin-right: 20px;
}
dl.settings {
    clear: right;
    overflow: auto;
    margin: 0 0 10px 0;
    padding: 0;
}
dl.settings dt {
    width: 40%;
    float: left;
    margin: 0 0 10px 0;
    padding: 0;
    clear: both;
}
dl.settings dt.settings_title {
    width: 100%;
    float: none;
    margin: 0 0 10px 0;
    padding: 5px 0 0 0;
    font-weight: bold;
    clear: both;
}
dl.settings dt.windowbg {
    width: 98%;
    float: left;
    margin: 0 0 3px 0;
    padding: 0 0 5px 0;
    clear: both;
}
dl.settings dd {
    width: 56%;
    float: right;
    overflow: auto;
    margin: 0 0 3px 0;
    padding: 0;
}
dl.settings img {
    margin: 0 10px 0 0;
}

dl.settings dt a img {
    position: relative;
    top: 2px;
}
h3.catbg,
h3.catbg2,
h3.titlebg,
h4.titlebg,
h4.catbg {
    overflow: hidden;
    height: 31px;
    line-height: 31px;
    font-size: 1.2em;
    font-weight: bold;
}
h3.catbg a:link,
h3.catbg a:visited,
h4.catbg a:link,
h4.catbg a:visited,
h3.catbg,
.table_list tbody.header td,
.table_list tbody.header td a {
    color: #fff;
}
h3.catbg2 a,
h3.catbg2 {
    color: #feb;
}
h3.catbg a:hover,
h4.catbg a:hover,
.table_list tbody.header td a:hover {
    color: #fd9;
    text-decoration: none;
}
h3.catbg2 a:hover {
    color: #fff;
    text-decoration: none;
}
h3.titlebg a,
h3.titlebg,
h4.titlebg,
h4.titlebg a {
    color: #222;
}
h3.titlebg a:hover,
h4.titlebg a:hover {
    color: #53616f;
    text-decoration: none;
}
h3.catbg img.icon,
h4.titlebg img.icon {
    vertical-align: middle;
    margin: -2px 5px 0 0;
}
h4.catbg a.toggle img {
    vertical-align: middle;
    margin: -2px 5px 0 5px;
}
h4.catbg,
h4.catbg2,
h3.catbg,
h3.catbg2,
.table_list tbody.header td.catbg {
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 100% -160px;
    padding-right: 9px;
}
h4.titlebg,
h3.titlebg {
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 100% -200px;
    padding-right: 9px;
}
h4.titlebg img.icon {
    float: left;
    margin: 5px 8px 0 0;
}
div.cat_bar {
    background: #bbb url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 0 -160px;
    padding-left: 9px;
    height: 31px;
    overflow: hidden;
    margin-bottom: 1px;
}
div.title_bar {
    background: #bbb url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 0 -200px;
    padding-left: 9px;
    height: 31px;
    overflow: hidden;
    margin-bottom: 1px;
}
div.roundframe div.cat_bar {
    background: #bbb url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 0 -240px;
    margin-bottom: 0;
}
div.roundframe div.cat_bar h3.catbg {
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 100% -240px;
}
div.title_barIC {
    background: #dadfe6 url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 0 -120px;
    padding-left: 9px;
    height: 31px;
    overflow: hidden;
    margin-bottom: 1px;
}
div.title_barIC h4.titlebg {
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 100% -120px;
}
#upshrinkHeaderIC p.pminfo {
    margin: 0;
    padding: 0.5em;
}
img#upshrink_ic,
img#newsupshrink {
    float: right;
    margin: 10px 5px 0 0;
}
table.table_list a.unreadlink,
table.table_list a.collapse {
    float: right;
}
table.table_list a.collapse {
    margin: 10px 5px 0 1em;
    height: 31px;
    line-height: 31px;
}
.table_grid tr.catbg,
.table_grid tr.titlebg {
    font-size: 0.95em;
    border-bottom: 1px solid #fff;
}
.table_grid tr.catbg th,
.table_grid tr.titlebg th {
    height: 28px;
    line-height: 28px;
}
tr.catbg th.first_th {
    background: #a7b9cd url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 0 -280px;
}
tr.catbg th.last_th {
    background: #a7b9cd url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 100% -280px;
}
tr.titlebg th.first_th {
    background: #eaeaea url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 0 -380px;
}
tr.titlebg th.last_th {
    background: #eaeaea url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) no-repeat 100% -380px;
}
.table_grid th.last_th input {
    margin: 0 2px;
}
.table_grid th.lefttext {
    padding: 0 0.7em;
}

table.table_grid {
    border-collapse: collapse;
    margin-top: 0.1em;
}
table.table_grid td {
    padding: 3px;
    border-bottom: 1px solid #fff;
    border-right: 1px solid #fff;
}

.additional_row {
    padding: 0.5em 0 0.5em 0;
}
table.table_grid thead tr.catbg th {
    white-space: nowrap;
}

#permissions table.table_grid td {
    padding: 0.4em 0.8em;
    cursor: default;
}

.windowbg span.topslice {
    display: block;
    padding-left: 20px;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -30px no-repeat;
}
.windowbg span.topslice span {
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% -30px no-repeat;
    height: 11px;
}
.windowbg span.botslice {
    display: block;
    padding-left: 20px;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -40px no-repeat;
    font-size: 5px;
    line-height: 5px;
    margin-bottom: 0.2em;
}
.windowbg span.botslice span {
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% -40px no-repeat;
    height: 11px;
}
.windowbg2 span.topslice {
    display: block;
    padding-left: 20px;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -60px no-repeat;
}
.windowbg2 span.topslice span {
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% -60px no-repeat;
    height: 11px;
}
.windowbg2 span.botslice {
    display: block;
    padding-left: 20px;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -71px no-repeat;
    font-size: 5px;
    line-height: 5px;
    margin-bottom: 0.2em;
}
.windowbg2 span.botslice span {
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% -71px no-repeat;
    height: 11px;
}
.approvebg span.topslice {
    display: block;
    padding-left: 20px;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 0 no-repeat;
}
.approvebg span.topslice span {
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% 0 no-repeat;
    height: 11px;
}
.approvebg span.botslice {
    display: block;
    padding-left: 20px;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -11px no-repeat;
    margin-bottom: 0.2em;
}
.approvebg span.botslice span {
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% -11px no-repeat;
    height: 11px;
}
.postbg {
    border-left: 1px solid #7f7f7f;
    border-right: 1px solid #7f7f7f;
}

.roundframe {
    padding: 0 10px;
    background: #fff;
    border-left: 1px solid #c5c5c5;
    border-right: 1px solid #c5c5c5;
}
.roundframe dl,
.roundframe dt,
.roundframe p {
    margin: 0;
}
.roundframe p {
    padding: 0.5em;
}
span.upperframe {
    padding: 0;
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -90px no-repeat;
    padding-left: 20px;
}
span.upperframe span {
    padding: 0;
    height: 12px;
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% -90px no-repeat;
}
span.lowerframe {
    padding: 0;
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -102px no-repeat;
    padding-left: 20px;
}
span.lowerframe span {
    padding: 0;
    height: 12px;
    display: block;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 100% -102px no-repeat;
}
.content {
    padding: 0.5em 1.2em;
    margin: 0;
    border: none;
}
.content p {
    margin: 0 0 0.5em 0;
}
.auto_suggest_div {
    border: 1px solid #000;
    position: absolute;
    visibility: hidden;
}
.auto_suggest_item {
    background-color: #ddd;
}
.auto_suggest_item_hover {
    background-color: #888;
    cursor: pointer;
    color: #eee;
}
#main_menu {
    padding: 0 0.5em;
    float: left;
    margin: 0;
    width: 98%;
}
.dropmenu,
.dropmenu ul {
    list-style: none;
    line-height: 1em;
    padding: 0;
    margin: 0;
}
.dropmenu {
    padding: 0 0.5em;
}
.dropmenu a {
    display: block;
    color: #000;
    font-weight: bold;
    background: linear-gradient(to bottom, #c3c51a 0%, #999a11 100%) repeat scroll 0% 0% transparent;
    border-bottom: 2px solid #ff0001;
    width: 110px;
    height: 18px;
    background-color: #000;
    border: solid 1px #000;
    border-radius: 7px;
    text-align: center;
}
.dropmenu a span {
    display: block;
    padding: 0 0 0 5px;
    font-size: 0.9em;
}

.dropmenu li a.firstlevel {
    margin-right: 8px;
}
.dropmenu li a.firstlevel span.firstlevel {
    display: block;
    position: relative;
    left: 0px;
    padding-left: 0px;
    height: 22px;
    line-height: 19px;
    white-space: pre;
}
.dropmenu li {
    float: left;
    padding: 0;
    margin: 0;
    position: relative;
}
.dropmenu li ul {
    z-index: 90;
    display: none;
    position: absolute;
    width: 19.2em;
    font-weight: normal;
    border-bottom: 1px solid #999;
    background: url(https://pximg.xyz/images/ff02d39d7bdf37e0fb9c9af2115e5942.jpg) 0 -130px no-repeat;
    padding: 7px 0 0 0;
}
.dropmenu li li {
    width: 19em;
    margin: 0;
    border-left: 1px solid #999;
    border-right: 1px solid #999;
}
.dropmenu li li a span {
    display: block;
    padding: 0px;
}
.dropmenu li ul ul {
    margin: -1.8em 0 0 13em;
}
.dropmenu li a.active {
    background: url(https://katcr.co/community/Themes/default/images/theme/menu_gfx.png) no-repeat 100% 0;
    color: #fff;
    font-weight: bold;
}
.dropmenu li a.active span.firstlevel {
background: url(https://katcr.co/community/Themes/default/images/theme/menu_gfx.pngg) no-repeat 0 0;}
.dropmenu li a.firstlevel:hover,
.dropmenu li:hover a.firstlevel {
    background: url(https://katcr.co/community/Themes/default/images/theme/menu_gfx.png) no-repeat 100% -30px;
    color: #fff;
    cursor: pointer;
    text-decoration: none;
}
.dropmenu li a.firstlevel:hover span.firstlevel,
.dropmenu li:hover a.firstlevel span.firstlevel {
    background: url(https://katcr.co/community/Themes/default/images/theme/menu_gfx.png) no-repeat 0 -30px;
}

.dropmenu li li a:hover,
.dropmenu li li:hover>a {
    background: #decd91;
    color: #000;
    text-decoration: none;
}
.dropmenu li:hover ul ul,
.dropmenu li:hover ul ul ul {
    top: -999em;
}
.dropmenu li li:hover ul {
    top: auto;
}
.dropmenu li:hover ul {
    display: block;
}
.dropmenu li li.additional_items {
    background-color: #fff;
}

#menu_toggle {
    float: right;
    margin-right: 10px;
    padding-top: 3px;
}
#menu_toggle span {
