css:
@-moz-document domain("sqlalchemy.org") {
/*
 * Style:   zDark - Blue Grey
 * Site:    SQLAlchemy Docs
 * Author:  -z-
 * License: MIT
 * File:    _static/docs.css
 * Date:    07/25/16
 */

::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #152128;
}

::-webkit-scrollbar-thumb {
    background: #37474F;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}

::-webkit-scrollbar-corner {
    background: #000;
}

body {
    background-color: #152128;
    margin-left: 0;
    margin-right: 0;
}

a, a:link, a:active {
    color: #CFD8DC;
}

a:visited {
    color: #B0BEC5;
}

a:hover {
    color: #B0BEC5 !important;
}

.code-font, code, .darkcell span.pre {
    color: #607D8B;
    background-color: #192125 !important;
	border: 1px solid #1b313e;
    border-radius: 3px;    
}
a code span.pre {
    color: #CFD8DC;
}

code, tt {
    background: none;
}

code.descname, tt.descname {
    font-size: 1.2em;
    color: #81a1b1;
}

#nav {
    background: #152128;
}

pre, #nav ul li ul li {
    clear: both;
    background: #0f181d;
    color: #B0BEC5;
    margin-top: -1px;
}

#nav ul li:hover {
    background-color: #000;
}

.nav-link-font, #nav li a {
    height: auto;
    color: #ECEFF1;
}

#nav ul li ul li:hover {
    color: #fff;
    background-color: #37474F;
}

#content dl#features dd {
    padding-left: 165px;
}

select, input {
    border-color: #152128;
    background: #0f181d;
    color: #a7b5bc;
}

input[type="text"], div.sphinxsidebar input {
    background-color: #030e13;
    border: 1px solid #6c828d;
    border-radius: 3px;
    line-height: 1.3em;    
    font-size: 1em;
    padding: 2px 5px;
    width: 11em;
}

input[type="submit"] {
    border: 1px solid #6c828d;
    font-size: 1em;
    line-height: 1.3em;
    background-color: #455A64 !important;
    border-radius: 3px;
    padding: 2px 5px;
}

h1, h2, h3, h4, h5 h6, #content h2 {
    border-bottom: 1px solid #637983;
    margin: 0.6em 0;
    padding: 0.1em 0;
    background: none;
    color: #699bad !important;
}

body, #wrap, .body-background, #docs-body {
    font-family: 'Lucida Grande', Arial, sans-serif;
    background-color: #263238;
    color: #78909C;
}
body {
    background: #152128;
}

#docs-container, #wrap {
    max-width: 1260px;
    margin: 0;
}

#wrap {
	border-right: 5px solid #0c171d;
}

#docs-container .version-num {
    color: #90A4AE;
}
#docs-top-navigation-container {
    background: none !important;
    max-width: 1260px;
}
#main-body {
    background: #152128;
}
#main-body.docs {
    padding: 0;
}
#docs-body {
    background: #263238;
    border: 0;
}
#main-body.docs #docs-body {
    margin-top: 0;
    border-bottom: 2px solid #0c171d;
}
#docs-body.withsidebar {
    margin-left: 235px;
    border-left: 5px solid #0c171d;
}

.highlight {
    background: none !important;
}

#fixed-sidebar {
    padding: 0.5em 0.3em;
}

#header {
   -webkit-filter: invert(1) hue-rotate(-360deg) saturate(40%) contrast(130%);
   filter: invert(1) hue-rotate(-360deg) saturate(40%) contrast(130%);
}

#docs-header h1, #docs-body h1, #docs-body h2, #docs-body h3, #docs-body h4 {
    font-family: 'Lucida Grande', Arial, sans-serif;
    border: 0;
    border-bottom: 1px solid #637983;
	margin: 1em 0 0;
    padding: 0.3em 0;
    background: none;
    color: #699bad;
    font-weight: normal;
}

#docs-body h1:first-of-type {
    margin-top: 0.3em !important;
}

#docs-header {
    background: #455A64;
}

#docs-header h1 {
    margin-top: 0 !important;
    padding: 0.5em 0.3em 0.2em !important;
	color: #ECEFF1 !important;
}

#docs-sidebar-popout {
    background: #0c171d;
    color: #ccc;
    height: 7em;
    padding: .5em;
    border-radius: 5px;
    border: 1px solid #223742;
}

#docs-sidebar, .box, #content blockquote, #sidebar .box, #sidebar #content blockquote, #content #sidebar blockquote{
    background-color: #152128;
	border: 1px solid #546E7A;
    padding: .5em;
    border-radius: 3px;
}

.topic-box-body-font, #sidebar {
    color: #90A4AE;
}

#sidebar .box {
    background: #263238;
}

#docs-sidebar {
	top: 8em;
    width: 211px;
	border: 0;
}

#docs-sidebar h1, #docs-sidebar h2, #docs-sidebar h3, #docs-sidebar h4, #docs-sidebar h5, #docs-sidebar h6,
#docs-sidebar h1 a, #docs-sidebar h2 a, #docs-sidebar h3 a, #docs-sidebar h4 a, #docs-sidebar h5 a, #docs-sidebar h6 a {
    background: #152128;
    color: #B0BEC5;
    text-shadow: none;
}

#docs-sidebar a {
    color: #607D8B;
}

#docs-sidebar .current > span > a {
    color: #CFD8DC !important;
}

#docs-sidebar-popout h3 {
    font-size: 1em;
}

#docs-sidebar li.current > span.link-container {
    color: #CFD8DC !important;
    background-color: #455A64;
}

p.sidebar-title, div.admonition .admonition-title, div.topic .topic-title {
    font-size: 1.3em;
    margin: 0;
}

#docs-container .sql_link {
    color: #B0BEC5;
    margin: 0;
    margin-top: -2px;
    font-family: monospace;
    font-weight: bold;
    background: #000;
    width: 30px;
}

#docs-container a.sql_link:hover {
    color: #ECEFF1 !important;
    border-color: #ECEFF1;
    background-color: #000;
}

#docs-container pre {
	background-color: #0f181d;
    border-color: #455A64;
    box-shadow: none;
    border-radius: 3px;
}

table.indextable tr.cap {
    margin-top: 10px;
    background-color: #1d2b31;
}

table.docutils thead tr {
    background-color: #37474F;
}

table.docutils tbody tr.row-even {
    background-color: #0b191f;
}

table.docutils tbody tr.row-odd {
    background-color: #010a0e;
}

table.docutils td, table.docutils th {
    background-color: inherit;
    border: 1px solid #455A64 !important;
    border-radius: 3px;
}

table.docutils th {
    color: #B0BEC5;
    padding: .5em;
}

dt:target, .highlight, .highlighted {
    background-color: rgba(141, 160, 35, 0.22);
    color: #EEE;
}

dt, dl.function > dt, dl.attribute > dt, dl.classmethod > dt, dl.method > dt, dl.class > dt, dl.exception > dt {
    color: #78909C;
    background: #172227;
    padding: 4px;
    margin: .5em;
    border-bottom: 2px solid #455A64;    
}

dl {
    border-bottom: 1px solid #1d2b31;
}

dt em {
    color: #538ead;
}
dt code {
    border: 0;
    background: transparent !important;
}

img {
    -webkit-filter: invert(1);
    filter: invert(1);
}

#description img {
    -webkit-filter: invert(0);
    filter: invert(0);
}

div.admonition, div.topic, .deprecated, .versionadded, .versionchanged, div.note, div.warning, div.seealsom, #index-nav, div.inherited-member {
    box-shadow: none;
    border-radius: 5px;
    margin: 0.5em 0;
    border: none;
    padding: 0.5em;
}

#docs-container a.sql_link::after {
    content: '> ';
}

div.admonition {
	background-color: #37474F;
}

div.warning .admonition-title {
    color: #f39227;
}

div.inherited-member {
    border: 1px solid #607D8B;
    box-shadow: none;
}

div.topic {
    background-color: #152128;
    border: 1px solid #90A4AE;
}

#docs-bottom-navigation, #index-nav {
    min-height: 45px;
    background: #37474F;
    border-color: #546E7A;
}

#index-nav p {
    float: left;
}

div.note, #docs-bottom-navigation, .release-status .inline-alert, .download .inline-alert, div.involved #content ul.nav, div.library #content ul.nav {
    background-color: #2d3f48;
    color: #aec6d2;
    border: 1px solid #607D8B;
}

div.warning {
    border: 1px solid #803737;
    color: #ab6530;
    background-color: #322821;
}

div.seealso {
    color: #b9b278;
    background-color: rgba(72, 66, 18, 0.31);
    border: 1px solid #a19631 !important;
}

div.seealso dd {
    margin-left: 10px;
}

div.seealso dt {
    color: #b5b49f;
    background: #1d211b;
    border-bottom: 2px solid #6b654d;
}

div.deprecated, div.deprecated-removed {
    background-color: rgba(43, 9, 5, 0.48);
    color: #923d3d;
    padding: .1em .5em;
    border-radius: 5px;
    border: 1px solid #752e2e;
    margin-top: .5em;
}

div.seealso ul {
    margin-right: 1em;
}

div.versionadded {
	background: #1f292d;
    border: 1px solid #334146;
    padding: 0 0.5em;
}

.versionadded .versionmodified, .versionchanged .versionmodified, .deprecated .versionmodified, .versionadded > p:first-child > span:first-child, .versionchanged > p:first-child > span:first-child, .deprecated > p:first-child > span:first-child {
    background-color: #630807;
    color: #ffc5c5;
    margin-right: .5em;
}

div.sidebar {
    border: 1px solid #78909C;
    background-color: #37474F;
    border-radius: 5px;
}

.popup_sql, .show_sql {
    background: #000;
}

.release-status table.table th, .download table.table th {
    background: #37474F;
    border: 1px solid #546E7A;
}

.release-status table.table td, .download table.table td {
    border: 1px solid #546E7A;
}

.selling-point-font, #content dl#features dt {
    color: #ECEFF1;
}

#content dl#features dt {
    padding: .5em;
}

#content dl#features dd {
	border-top: 1px solid #455A64;
    padding-left: 11em;
}
#content dl#inproduction dd {
    border-bottom: 1px solid #455A64;
}
#content dl#inproduction {
    border: none;
}

.release-status .inline-alert.deprecation-maintenance, .download .inline-alert.deprecation-maintenance {
    border: 1px solid #aa6708;
    border-left-width: 10px;
}

div#dialect-documentation {
    clear: both;
}

#docs-bottom-navigation {
    float: left;
    margin: 0;
    color: #798e98;
    border: 0;
    border-left: 5px solid #0c171d;
    margin-left: 235px;
    width: calc(100% - 260px);
}

#docs-bottom-navigation a {
    color: #c7eafb;
}

#docs-copyright a {
    color: #607D8B;
}

.features dt {
    background: #04151f;
    margin: 0;
    text-align: center;
}

.features img {
    margin: 0 auto;
}

#footer {
    border-top: 2px solid #607D8B;
    background: #000;
    line-height: 150%;
    margin-top: 0;
    text-align: right;
    color: #607D8B;
    padding: 1em 0.5em;
    font-size: 0.7em;
}

#footer img {
    filter: invert(0.9) hue-rotate(-360deg) saturate(60%) contrast(148%);
    -webkit-filter: invert(0.9) hue-rotate(-360deg) saturate(60%) contrast(148%);
}

#footer .copyright {
    float: right;
}
}

@-moz-document domain("sqlalchemy.org") {
